/**
 * AgentActionCard Component Styles
 *
 * Action cards with clear approval workflow and visual weight on execute button.
 * Uses Ink Design System tokens for consistent theming.
 */

@import '../../../design-system/1-tokens/tokens.css';

/* =============================================================================
   Container
   ============================================================================= */

.container {
  background: var(--ink-white-100);
  border-radius: var(--ink-radius-size-m);
  border: 1px solid var(--ink-neutral-30);
  overflow: hidden;
  box-shadow: var(--ink-shadow-sm);
  transition: all 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

/* Proposed state - subtle amber highlight */
.statusProposed {
  border-color: var(--ink-orange-30);
  box-shadow:
    var(--ink-shadow-sm),
    0 0 0 1px var(--ink-orange-20);
}

.statusProposed:hover {
  box-shadow:
    var(--ink-shadow-md),
    0 0 0 1px var(--ink-orange-30);
  transform: translateY(-1px);
}

/* Executing state - pulsing border */
.statusExecuting {
  border-color: var(--ink-cobalt-80);
  animation: executingPulse 2s ease-in-out infinite;
}

@keyframes executingPulse {
  0%,
  100% {
    box-shadow:
      var(--ink-shadow-sm),
      0 0 0 2px var(--ink-cobalt-20);
  }
  50% {
    box-shadow:
      var(--ink-shadow-sm),
      0 0 0 4px var(--ink-cobalt-30);
  }
}

/* Complete state */
.statusComplete {
  border-color: var(--ink-green-70);
  background: linear-gradient(135deg, var(--ink-white-100) 0%, var(--ink-green-10) 100%);
}

/* Error state */
.statusError {
  border-color: var(--ink-red-70);
  background: linear-gradient(135deg, var(--ink-white-100) 0%, var(--ink-red-10) 100%);
}

/* Cancelled state */
.statusCancelled {
  opacity: 0.6;
  border-color: var(--ink-neutral-30);
}

/* =============================================================================
   Header
   ============================================================================= */

.header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--ink-spacing-150) var(--ink-spacing-200);
  background: var(--ink-neutral-10);
  border-bottom: 1px solid var(--ink-neutral-15);
}

.headerLeft {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-100);
}

.actionIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 24px;
  height: 24px;
  border-radius: var(--ink-radius-size-xs);
  background: var(--ink-orange-20);
  color: var(--ink-orange-80);
}

.statusComplete .actionIcon {
  background: var(--ink-green-20);
  color: var(--ink-green-80);
}

.statusError .actionIcon {
  background: var(--ink-red-20);
  color: var(--ink-red-80);
}

.statusExecuting .actionIcon {
  background: var(--ink-cobalt-20);
  color: var(--ink-cobalt-100);
}

.actionType {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-semibold);
  text-transform: uppercase;
  letter-spacing: 0.05em;
  color: var(--ink-neutral-70);
}

.statusBadge {
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-50);
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-medium);
  padding: var(--ink-spacing-50) var(--ink-spacing-125);
  border-radius: var(--ink-radius-size-full);
  background: var(--ink-neutral-15);
  color: var(--ink-neutral-80);
}

.statusBadge[data-status='proposed'] {
  background: var(--ink-orange-20);
  color: var(--ink-orange-100);
}

.statusBadge[data-status='executing'] {
  background: var(--ink-cobalt-20);
  color: var(--ink-cobalt-100);
}

.statusBadge[data-status='complete'] {
  background: var(--ink-green-20);
  color: var(--ink-green-90);
}

.statusBadge[data-status='error'] {
  background: var(--ink-red-20);
  color: var(--ink-red-90);
}

.statusBadge[data-status='cancelled'] {
  background: var(--ink-neutral-15);
  color: var(--ink-neutral-60);
}

.statusDot {
  width: 6px;
  height: 6px;
  border-radius: var(--ink-radius-size-full);
  background: currentColor;
  animation: statusPulse 1.5s ease-in-out infinite;
}

@keyframes statusPulse {
  0%,
  100% {
    opacity: 1;
  }
  50% {
    opacity: 0.4;
  }
}

/* =============================================================================
   Content
   ============================================================================= */

.content {
  padding: var(--ink-spacing-200);
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-150);
}

.title {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-m);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-neutral-130);
  margin: 0;
  line-height: 1.3;
}

.description {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-s);
  color: var(--ink-neutral-80);
  margin: 0;
  line-height: 1.5;
}

/* =============================================================================
   Parameters
   ============================================================================= */

.parameters {
  background: var(--ink-neutral-10);
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-size-s);
  padding: var(--ink-spacing-125) var(--ink-spacing-150);
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50);
}

.paramRow {
  display: flex;
  align-items: baseline;
  gap: var(--ink-spacing-100);
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
}

.paramKey {
  color: var(--ink-neutral-70);
  min-width: 80px;
}

.paramKey::after {
  content: ':';
}

.paramValue {
  color: var(--ink-neutral-100);
  font-weight: var(--ink-font-weight-medium);
}

/* =============================================================================
   Progress Section
   ============================================================================= */

.progressSection {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-150);
}

.progressSection > div:first-child {
  flex: 1;
}

.progressLabel {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-cobalt-100);
  min-width: 36px;
  text-align: right;
}

/* =============================================================================
   Output Stream
   ============================================================================= */

.outputStream {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  background: var(--ink-neutral-110);
  color: var(--ink-neutral-30);
  padding: var(--ink-spacing-150) var(--ink-spacing-150);
  border-radius: var(--ink-radius-size-s);
  line-height: 1.6;
  white-space: pre-wrap;
  max-height: 120px;
  overflow-y: auto;
}

.outputStream::after {
  content: '';
  animation: cursorBlink 1s steps(1) infinite;
  color: var(--ink-orange-50);
}

@keyframes cursorBlink {
  0%,
  50% {
    opacity: 1;
  }
  51%,
  100% {
    opacity: 0;
  }
}

/* =============================================================================
   Result Section
   ============================================================================= */

.resultSection {
  background: var(--ink-green-10);
  border: 1px solid var(--ink-green-30);
  border-radius: var(--ink-radius-size-s);
  padding: var(--ink-spacing-150);
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-100);
}

.resultHeader {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-green-90);
}

.resultContent {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-s);
  color: var(--ink-neutral-100);
  line-height: 1.5;
}

.artifacts {
  display: flex;
  flex-wrap: wrap;
  gap: var(--ink-spacing-50);
  margin-top: var(--ink-spacing-50);
}

.artifactTag {
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-50);
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-green-90);
  background: var(--ink-white-100);
  padding: var(--ink-spacing-50) var(--ink-spacing-100);
  border-radius: var(--ink-radius-size-xs);
  border: 1px solid var(--ink-green-40);
}

/* =============================================================================
   Error Section
   ============================================================================= */

.errorSection {
  background: var(--ink-red-10);
  border: 1px solid var(--ink-red-30);
  border-radius: var(--ink-radius-size-s);
  padding: var(--ink-spacing-150);
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-100);
}

.errorHeader {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-red-90);
}

.errorContent {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-s);
  color: var(--ink-neutral-100);
  line-height: 1.5;
}

/* =============================================================================
   Footer
   ============================================================================= */

.footer {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: var(--ink-spacing-100);
  padding: var(--ink-spacing-150) var(--ink-spacing-200);
  background: var(--ink-neutral-10);
  border-top: 1px solid var(--ink-neutral-15);
}

/* Make execute button more prominent with orange theme */
.footer button[class*='primary'] {
  background: linear-gradient(135deg, var(--ink-orange-60) 0%, var(--ink-orange-70) 100%);
  border-color: var(--ink-orange-70);
  box-shadow: var(--ink-shadow-sm);
}

.footer button[class*='primary']:hover {
  background: linear-gradient(135deg, var(--ink-orange-70) 0%, var(--ink-orange-80) 100%);
  box-shadow: var(--ink-shadow-md);
  transform: translateY(-1px);
}

.cancelledNote {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  color: var(--ink-neutral-60);
  font-style: italic;
}

/**
 * AgentActionCard Component
 *
 * Displays proposed agent actions awaiting user approval.
 * Shows action type, parameters, and provides execute/cancel controls.
 *
 * @example
 * ```tsx
 * <AgentActionCard
 *   action={{
 *     id: '1',
 *     type: 'analyze',
 *     label: 'Analyze Contract Terms',
 *     description: 'Review and extract key terms from the agreement',
 *     status: 'proposed',
 *     parameters: { document: 'NDA-2024.pdf' },
 *   }}
 *   onExecute={(id) => console.log('Execute:', id)}
 *   onCancel={(id) => console.log('Cancel:', id)}
 * />
 * ```
 */

// AgentActionCard Component
import { Icon, Button, ProgressBar } from '@/design-system';
import type { AgentAction, AgentActionType, AgentActionStatus } from './types';
import styles from './AgentActionCard.module.css';

// =============================================================================
// Types
// =============================================================================

export interface AgentActionCardProps {
  /** The action to display */
  action: AgentAction;
  /** Called when user clicks Execute */
  onExecute?: (actionId: string) => void;
  /** Called when user clicks Cancel/Dismiss */
  onCancel?: (actionId: string) => void;
  /** Called when user clicks Retry (error state) */
  onRetry?: (actionId: string) => void;
  /** Additional CSS class */
  className?: string;
  /** Progress value 0-100 when executing */
  progress?: number;
  /** Streaming output text */
  outputText?: string;
}

// =============================================================================
// Helper Functions
// =============================================================================

function getActionIcon(type: AgentActionType): string {
  const iconMap: Record<AgentActionType, string> = {
    analyze: 'search',
    draft: 'document-edit',
    route: 'send',
    schedule: 'calendar',
    generate: 'document-text',
    update: 'refresh',
    notify: 'bell',
  };
  return iconMap[type] || 'sparkle';
}

function getActionTypeLabel(type: AgentActionType): string {
  const labelMap: Record<AgentActionType, string> = {
    analyze: 'Analysis',
    draft: 'Draft',
    route: 'Route',
    schedule: 'Schedule',
    generate: 'Generate',
    update: 'Update',
    notify: 'Notify',
  };
  return labelMap[type] || type;
}

function getStatusLabel(status: AgentActionStatus): string {
  const labelMap: Record<AgentActionStatus, string> = {
    proposed: 'Proposed',
    executing: 'Executing',
    complete: 'Complete',
    cancelled: 'Cancelled',
    error: 'Failed',
  };
  return labelMap[status] || status;
}

function capitalize(str: string): string {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

// =============================================================================
// Main Component
// =============================================================================

export function AgentActionCard({
  action,
  onExecute,
  onCancel,
  onRetry,
  className,
  progress,
  outputText,
}: AgentActionCardProps) {
  const { id, type, label, description, icon, status, parameters, result } = action;
  const displayIcon = icon || getActionIcon(type);

  const isProposed = status === 'proposed';
  const isExecuting = status === 'executing';
  const isComplete = status === 'complete';
  const isError = status === 'error';
  const isCancelled = status === 'cancelled';

  const handleExecute = () => {
    onExecute?.(id);
  };

  const handleCancel = () => {
    onCancel?.(id);
  };

  const handleRetry = () => {
    onRetry?.(id);
  };

  return (
    <div
      className={`${styles.container} ${styles[`status${capitalize(status)}`]} ${className || ''}`}
    >
      {/* Header with action type badge */}
      <div className={styles.header}>
        <div className={styles.headerLeft}>
          <span className={styles.actionIcon}>
            <Icon name={displayIcon as any} size="small" />
          </span>
          <span className={styles.actionType}>{getActionTypeLabel(type)}</span>
        </div>
        <span className={styles.statusBadge} data-status={status}>
          {isExecuting && <span className={styles.statusDot} />}
          {getStatusLabel(status)}
        </span>
      </div>

      {/* Main content */}
      <div className={styles.content}>
        <h4 className={styles.title}>{label}</h4>
        <p className={styles.description}>{description}</p>

        {/* Parameters */}
        {parameters && Object.keys(parameters).length > 0 && (
          <div className={styles.parameters}>
            {Object.entries(parameters).map(([key, value]) => (
              <div key={key} className={styles.paramRow}>
                <span className={styles.paramKey}>{key}</span>
                <span className={styles.paramValue}>{value}</span>
              </div>
            ))}
          </div>
        )}

        {/* Execution progress */}
        {isExecuting && progress !== undefined && (
          <div className={styles.progressSection}>
            <ProgressBar
              variant="determinate"
              value={progress}
              kind="info"
              showLabel={false}
              showContent={false}
            />
            <span className={styles.progressLabel}>{progress}%</span>
          </div>
        )}

        {/* Streaming output */}
        {isExecuting && outputText && <div className={styles.outputStream}>{outputText}</div>}

        {/* Success result */}
        {isComplete && result && (
          <div className={styles.resultSection}>
            <div className={styles.resultHeader}>
              <Icon name="status-check" size="small" />
              <span>Result</span>
            </div>
            <div className={styles.resultContent}>{result.output}</div>
            {result.artifacts && result.artifacts.length > 0 && (
              <div className={styles.artifacts}>
                {result.artifacts.map((artifact, i) => (
                  <span key={i} className={styles.artifactTag}>
                    <Icon name="document" size="small" />
                    {artifact}
                  </span>
                ))}
              </div>
            )}
          </div>
        )}

        {/* Error result */}
        {isError && result && (
          <div className={styles.errorSection}>
            <div className={styles.errorHeader}>
              <Icon name="status-warn" size="small" />
              <span>Error</span>
            </div>
            <div className={styles.errorContent}>
              {result.error || 'An unexpected error occurred'}
            </div>
          </div>
        )}
      </div>

      {/* Footer with action buttons */}
      <div className={styles.footer}>
        {isProposed && (
          <>
            <Button kind="secondary" size="small" onClick={handleCancel}>
              Dismiss
            </Button>
            <Button kind="primary" size="small" onClick={handleExecute}>
              <Icon name="flash" size="small" />
              Execute
            </Button>
          </>
        )}

        {isExecuting && (
          <Button kind="secondary" size="small" onClick={handleCancel} disabled>
            <Icon name="close" size="small" />
            Cancel
          </Button>
        )}

        {isError && (
          <>
            <Button kind="secondary" size="small" onClick={handleCancel}>
              Dismiss
            </Button>
            <Button kind="primary" size="small" onClick={handleRetry}>
              <Icon name="refresh" size="small" />
              Retry
            </Button>
          </>
        )}

        {isComplete && (
          <Button kind="secondary" size="small" onClick={handleCancel}>
            Done
          </Button>
        )}

        {isCancelled && <span className={styles.cancelledNote}>Action dismissed</span>}
      </div>
    </div>
  );
}

export default AgentActionCard;

/**
 * AgentCanvas Component Styles
 *
 * Multi-mode canvas for document viewing, forms, and visualizations.
 * Uses Ink Design System tokens for consistent theming.
 */

@import '../../../design-system/1-tokens/tokens.css';

/* =============================================================================
   Container
   ============================================================================= */

.container {
  display: flex;
