  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
  padding: var(--ink-spacing-150) var(--ink-spacing-200);
  background: transparent;
  border: none;
  cursor: pointer;
  transition: all 200ms cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
}

.header:hover {
  background: var(--ink-orange-10);
}

.header:active {
  background: var(--ink-orange-20);
}

.headerLeft {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-125);
}

.headerIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 28px;
  height: 28px;
  border-radius: var(--ink-radius-size-s);
  background: linear-gradient(135deg, var(--ink-orange-50) 0%, var(--ink-orange-60) 100%);
  color: var(--ink-white-100);
  box-shadow: var(--ink-shadow-sm);
  position: relative;
}

/* Subtle glow animation for active thinking */
.headerIcon::after {
  content: '';
  position: absolute;
  inset: -3px;
  border-radius: 11px;
  background: var(--ink-orange-50);
  opacity: 0;
  animation: thinkingGlow 2s ease-in-out infinite;
  z-index: -1;
}

@keyframes thinkingGlow {
  0%,
  100% {
    opacity: 0;
    transform: scale(0.95);
  }
  50% {
    opacity: 0.15;
    transform: scale(1);
  }
}

.headerIconComplete {
  background: linear-gradient(135deg, var(--ink-green-70) 0%, var(--ink-green-80) 100%);
  box-shadow: var(--ink-shadow-sm);
}

.headerIconComplete::after {
  display: none;
}

.headerIconError {
  background: linear-gradient(135deg, var(--ink-red-70) 0%, var(--ink-red-80) 100%);
  box-shadow: var(--ink-shadow-sm);
}

.headerIconError::after {
  display: none;
}

.headerTitle {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-s);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-neutral-110);
  letter-spacing: -0.01em;
}

.headerProgress {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-70);
  padding: 3px var(--ink-spacing-100);
  background: var(--ink-neutral-15);
  border-radius: var(--ink-radius-size-xs);
  border: 1px solid var(--ink-neutral-30);
  letter-spacing: 0.02em;
}

.headerChevron {
  color: var(--ink-neutral-60);
  transition: transform 250ms cubic-bezier(0.4, 0, 0.2, 1);
}

.header[aria-expanded='true'] .headerChevron {
  transform: rotate(180deg);
}

/* =============================================================================
   Steps List
   ============================================================================= */

.stepsList {
  display: flex;
  flex-direction: column;
  padding: var(--ink-spacing-50) var(--ink-spacing-200) var(--ink-spacing-200);
  gap: 2px;
}

/* =============================================================================
   Individual Step
   ============================================================================= */

.step {
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-150);
  padding: var(--ink-spacing-125) 0;
  position: relative;
  opacity: 1;
  transform: translateX(0);
  animation: stepSlideIn 300ms cubic-bezier(0.4, 0, 0.2, 1) backwards;
}

@keyframes stepSlideIn {
  from {
    opacity: 0;
    transform: translateX(-8px);
  }
}

/* Stagger animation for each step */
.step:nth-child(1) {
  animation-delay: 0ms;
}
.step:nth-child(2) {
  animation-delay: 50ms;
}
.step:nth-child(3) {
  animation-delay: 100ms;
}
.step:nth-child(4) {
  animation-delay: 150ms;
}
.step:nth-child(5) {
  animation-delay: 200ms;
}
.step:nth-child(6) {
  animation-delay: 250ms;
}
.step:nth-child(7) {
  animation-delay: 300ms;
}
.step:nth-child(8) {
  animation-delay: 350ms;
}

/* Connector line between steps */
.step:not(:last-child)::after {
  content: '';
  position: absolute;
  left: 11px;
  top: 32px;
  bottom: -4px;
  width: 2px;
  background: var(--ink-neutral-30);
  border-radius: 1px;
  transition: background 300ms ease;
}

.stepComplete:not(:last-child)::after {
  background: var(--ink-green-70);
}

.stepActive:not(:last-child)::after {
  background: linear-gradient(to bottom, var(--ink-orange-50) 0%, var(--ink-neutral-30) 100%);
}

/* =============================================================================
   Step Icon
   ============================================================================= */

.stepIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 24px;
  height: 24px;
  border-radius: var(--ink-radius-size-full);
  flex-shrink: 0;
  position: relative;
  z-index: 1;
  transition: all 300ms cubic-bezier(0.4, 0, 0.2, 1);
}

.stepIconComplete {
  background: var(--ink-green-70);
  color: var(--ink-white-100);
  box-shadow: var(--ink-shadow-sm);
}

.stepIconActive {
  background: linear-gradient(135deg, var(--ink-orange-50) 0%, var(--ink-orange-60) 100%);
  box-shadow:
    0 0 0 4px var(--ink-orange-20),
    var(--ink-shadow-sm);
}

.stepIconError {
  background: var(--ink-red-70);
  color: var(--ink-white-100);
  box-shadow: var(--ink-shadow-sm);
}

.stepIconPending {
  background: var(--ink-neutral-15);
  border: 2px solid var(--ink-neutral-30);
}

.stepIconDot {
  width: 6px;
  height: 6px;
  border-radius: var(--ink-radius-size-full);
  background: var(--ink-neutral-60);
}

.stepIconPulse {
  width: 10px;
  height: 10px;
  border-radius: var(--ink-radius-size-full);
  background: var(--ink-white-100);
  animation: activePulse 1.5s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

@keyframes activePulse {
  0%,
  100% {
    opacity: 1;
    transform: scale(1);
  }
  50% {
    opacity: 0.7;
    transform: scale(0.75);
  }
}

/* =============================================================================
   Step Content
   ============================================================================= */

.stepContent {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50);
  padding-top: 2px;
}

.stepHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--ink-spacing-150);
}

.stepTitle {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-s);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  line-height: 1.4;
  transition: color 200ms ease;
}

.stepPending .stepTitle {
  color: var(--ink-neutral-60);
}

.stepActive .stepTitle {
  color: var(--ink-neutral-130);
  font-weight: var(--ink-font-weight-semibold);
}

.stepComplete .stepTitle {
  color: var(--ink-neutral-80);
}

.stepDuration {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-60);
  padding: 2px var(--ink-spacing-50);
  background: var(--ink-neutral-15);
  border-radius: var(--ink-radius-size-xs);
  letter-spacing: 0.03em;
  white-space: nowrap;
}

.stepProgress {
  max-width: 180px;
  margin-top: 2px;
}

.stepDescription {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  color: var(--ink-neutral-70);
  line-height: 1.5;
}

.stepError {
  font-family: var(--ink-font-family), system-ui, sans-serif;
  font-size: var(--ink-font-size-body-xs);
  color: var(--ink-red-90);
  background: var(--ink-red-10);
  padding: var(--ink-spacing-50) var(--ink-spacing-125);
  border-radius: var(--ink-radius-size-xs);
  line-height: 1.4;
}

/* =============================================================================
   Streaming Text Effect
   ============================================================================= */

.streamingText {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  color: var(--ink-neutral-80);
  background: var(--ink-neutral-10);
  padding: var(--ink-spacing-100) var(--ink-spacing-150);
  border-radius: var(--ink-radius-size-xs);
  border: 1px solid var(--ink-neutral-30);
  line-height: 1.6;
  white-space: pre-wrap;
  position: relative;
  overflow: hidden;
}

.streamingText::after {
  content: '';
  animation: cursorBlink 1s steps(1) infinite;
  color: var(--ink-orange-60);
}

@keyframes cursorBlink {
  0%,
  50% {
    opacity: 1;
  }
  51%,
  100% {
    opacity: 0;
  }
}

/* =============================================================================
   Tool Call Display
   ============================================================================= */

.toolCall {
  font-family: 'JetBrains Mono', 'SF Mono', 'Consolas', monospace;
  font-size: var(--ink-font-size-body-xs);
  background: var(--ink-neutral-110);
  color: var(--ink-neutral-15);
  padding: var(--ink-spacing-125) var(--ink-spacing-150);
  border-radius: var(--ink-radius-size-s);
  margin-top: var(--ink-spacing-50);
  position: relative;
  overflow: hidden;
}

.toolCall::before {
  content: '>';
  color: var(--ink-orange-50);
  margin-right: var(--ink-spacing-100);
  font-weight: var(--ink-font-weight-semibold);
}

.toolCallName {
  color: var(--ink-orange-40);
  font-weight: var(--ink-font-weight-semibold);
}

.toolCallParams {
  color: var(--ink-neutral-60);
  margin-left: var(--ink-spacing-50);
}

/**
 * ThinkingSteps Component
 *
 * Progressive reasoning visualization that shows the agent's thinking process.
 * Features step-by-step reveal, streaming text, and tool call visualization.
 *
 * @example
 * ```tsx
 * <ThinkingSteps
 *   steps={[
 *     { id: '1', title: 'Analyzing document', status: 'complete', duration: 1200 },
 *     { id: '2', title: 'Extracting key terms', status: 'active', progress: 45 },
 *     { id: '3', title: 'Comparing with policy', status: 'pending' },
 *   ]}
 *   streamingText="Found 3 key clauses..."
 * />
 * ```
 */

import { useState, useEffect, useRef } from 'react';
import { Icon, ProgressBar } from '@/design-system';
import type { ThinkingStep, ThinkingStepStatus } from './types';
import styles from './ThinkingSteps.module.css';

// =============================================================================
// Types
// =============================================================================

export interface ThinkingStepsProps {
  /** Array of thinking steps to display */
  steps: ThinkingStep[];
  /** Whether the component is collapsed */
  isCollapsed?: boolean;
  /** Callback when collapse state changes */
  onToggleCollapse?: () => void;
  /** Additional CSS class */
  className?: string;
  /** Streaming text for active step */
  streamingText?: string;
  /** Tool call being executed */
  toolCall?: {
    name: string;
    params?: Record<string, string>;
  };
}

// =============================================================================
// Helper Components
// =============================================================================

interface StepIconProps {
  status: ThinkingStepStatus;
}

function StepIcon({ status }: StepIconProps) {
  switch (status) {
    case 'complete':
      return (
        <span className={`${styles.stepIcon} ${styles.stepIconComplete}`}>
          <Icon name="check" size="small" />
        </span>
      );
    case 'active':
      return (
        <span className={`${styles.stepIcon} ${styles.stepIconActive}`}>
          <span className={styles.stepIconPulse} />
        </span>
      );
    case 'error':
      return (
        <span className={`${styles.stepIcon} ${styles.stepIconError}`}>
          <Icon name="close" size="small" />
        </span>
      );
    case 'pending':
    default:
      return (
        <span className={`${styles.stepIcon} ${styles.stepIconPending}`}>
          <span className={styles.stepIconDot} />
        </span>
      );
  }
}

function formatDuration(ms: number): string {
  if (ms < 1000) {
    return `${ms}ms`;
  }
  return `${(ms / 1000).toFixed(1)}s`;
}

function StreamingText({ text }: { text: string }) {
  return <div className={styles.streamingText}>{text}</div>;
}

function ToolCallDisplay({ name, params }: { name: string; params?: Record<string, string> }) {
  const paramsStr = params
    ? Object.entries(params)
        .map(([k, v]) => `${k}="${v}"`)
        .join(' ')
    : '';

  return (
    <div className={styles.toolCall}>
      <span className={styles.toolCallName}>{name}</span>
      {paramsStr && <span className={styles.toolCallParams}>{paramsStr}</span>}
    </div>
  );
}

// =============================================================================
// Main Component
// =============================================================================

export function ThinkingSteps({
  steps,
  isCollapsed: isCollapsedProp,
  onToggleCollapse,
  className,
  streamingText,
  toolCall,
}: ThinkingStepsProps) {
  const [isCollapsedInternal, setIsCollapsedInternal] = useState(false);
  const stepsListRef = useRef<HTMLDivElement>(null);

  // Support both controlled and uncontrolled modes
  const isCollapsed = isCollapsedProp !== undefined ? isCollapsedProp : isCollapsedInternal;

  const handleToggle = () => {
    if (onToggleCollapse) {
      onToggleCollapse();
    } else {
      setIsCollapsedInternal(!isCollapsedInternal);
    }
  };

  // Auto-scroll to active step when streaming
  useEffect(() => {
    if (streamingText && stepsListRef.current) {
      const activeStep = stepsListRef.current.querySelector(`.${styles.stepActive}`);
      if (activeStep) {
        activeStep.scrollIntoView({ behavior: 'smooth', block: 'nearest' });
      }
    }
  }, [streamingText]);

  // Calculate progress stats
  const completedCount = steps.filter((s) => s.status === 'complete').length;
  const hasError = steps.some((s) => s.status === 'error');
  const isComplete = completedCount === steps.length && !hasError && steps.length > 0;

  // Determine header icon class based on state
  const headerIconClass = isComplete
    ? styles.headerIconComplete
    : hasError
      ? styles.headerIconError
      : '';

  return (
    <div className={`${styles.container} ${className || ''}`}>
      {/* Header */}
      <button
        type="button"
        className={styles.header}
        onClick={handleToggle}
        aria-expanded={!isCollapsed}
      >
        <div className={styles.headerLeft}>
          <span className={`${styles.headerIcon} ${headerIconClass}`}>
            {isComplete ? (
              <Icon name="status-check" size="small" />
            ) : hasError ? (
              <Icon name="status-warn" size="small" />
            ) : (
              <Icon name="ai-spark" size="small" />
            )}
          </span>
          <span className={styles.headerTitle}>
            {isComplete ? 'Analysis Complete' : hasError ? 'Analysis Error' : 'Agent Reasoning'}
          </span>
          <span className={styles.headerProgress}>
            {completedCount}/{steps.length}
          </span>
        </div>
        <Icon name="chevron-down" size="small" className={styles.headerChevron} />
      </button>

      {/* Steps List */}
      {!isCollapsed && (
        <div className={styles.stepsList} ref={stepsListRef}>
          {steps.map((step) => {
            const isActiveStep = step.status === 'active';

            return (
              <div
                key={step.id}
                className={`${styles.step} ${styles[`step${capitalize(step.status)}`]}`}
              >
                <StepIcon status={step.status} />
                <div className={styles.stepContent}>
                  <div className={styles.stepHeader}>
                    <span className={styles.stepTitle}>{step.title}</span>
                    {step.status === 'complete' && step.duration !== undefined && (
                      <span className={styles.stepDuration}>{formatDuration(step.duration)}</span>
                    )}
                  </div>

                  {/* Progress bar for active step */}
                  {isActiveStep && step.progress !== undefined && (
                    <div className={styles.stepProgress}>
                      <ProgressBar
                        variant="determinate"
                        value={step.progress}
                        kind="info"
                        showLabel={false}
                        showContent={false}
                      />
                    </div>
                  )}

                  {/* Streaming text for active step */}
                  {isActiveStep && streamingText && <StreamingText text={streamingText} />}

                  {/* Tool call visualization */}
                  {isActiveStep && toolCall && (
                    <ToolCallDisplay name={toolCall.name} params={toolCall.params} />
                  )}

                  {/* Description */}
                  {step.description && (
                    <span className={styles.stepDescription}>{step.description}</span>
                  )}

                  {/* Error state */}
                  {step.status === 'error' && step.result && (
                    <span className={styles.stepError}>{step.result}</span>
                  )}
                </div>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
}

function capitalize(str: string): string {
  return str.charAt(0).toUpperCase() + str.slice(1);
}

export default ThinkingSteps;

/**
 * AgentPanel Pattern
 *
 * Resizable AI/Agent panel with support for chat + canvas split layout.
 * Includes components for displaying agent reasoning and proposed actions.
 *
 * @example
 * ```tsx
 * import {
 *   AgentPanel,
 *   AgentCanvas,
 *   ThinkingSteps,
 *   AgentActionCard,
 * } from '@/design-system/5-patterns/AgentPanel';
 *
 * // Basic panel with AIChat
 * <AgentPanel isOpen={isOpen} onClose={handleClose} title="AI Assistant">
 *   <AIChat messages={messages} />
 * </AgentPanel>
 *
 * // Panel with split layout
 * <AgentPanel
 *   isOpen={isOpen}
 *   onClose={handleClose}
 *   showCanvas
 *   canvasContent={<AgentCanvas mode="document" documentUrl={url} />}
 * >
 *   <AIChat messages={messages} />
 * </AgentPanel>
 * ```
 */

// Main components
export { AgentPanel } from './AgentPanel';
export type { AgentPanelProps } from './AgentPanel';

export { AgentCanvas } from './AgentCanvas';
export type { AgentCanvasProps } from './AgentCanvas';

export { ThinkingSteps } from './ThinkingSteps';
export type { ThinkingStepsProps } from './ThinkingSteps';

export { AgentActionCard } from './AgentActionCard';
export type { AgentActionCardProps } from './AgentActionCard';

// Types
export type {
  ThinkingStep,
  ThinkingStepStatus,
  AgentAction,
  AgentActionType,
  AgentActionStatus,
  AgentActionResult,
  AgentCanvasMode,
  AgentPanelResizeState,
} from './types';

/**
 * AgentPanel Types
 *
 * Shared types for the AgentPanel pattern components.
 */

// =============================================================================
// ThinkingSteps Types
// =============================================================================

export type ThinkingStepStatus = 'pending' | 'active' | 'complete' | 'error';

export interface ThinkingStep {
  id: string;
  /** The title/action of this step */
  title: string;
  /** Current status of the step */
  status: ThinkingStepStatus;
  /** Optional description or result text */
  description?: string;
  /** Error message when status is 'error' */
  result?: string;
  /** Duration in milliseconds (shown when complete) */
  duration?: number;
  /** Progress 0-100 when status is 'active' */
  progress?: number;
}

// =============================================================================
// AgentActionCard Types
// =============================================================================

export type AgentActionType =
  | 'analyze'
  | 'draft'
  | 'route'
  | 'schedule'
  | 'generate'
  | 'update'
  | 'notify';

export type AgentActionStatus = 'proposed' | 'executing' | 'complete' | 'cancelled' | 'error';

export interface AgentActionResult {
  output?: string;
  error?: string;
  artifacts?: string[];
}

export interface AgentAction {
  id: string;
  type: AgentActionType;
  label: string;
  description: string;
  icon?: string;
  status: AgentActionStatus;
  parameters?: Record<string, string>;
  result?: AgentActionResult;
}

// =============================================================================
// AgentCanvas Types
// =============================================================================

export type AgentCanvasMode = 'document' | 'form' | 'visualization' | 'custom';

// =============================================================================
// AgentPanel Types
// =============================================================================

export interface AgentPanelResizeState {
  width: number;
  isResizing: boolean;
}

