export { DataTableCell } from './DataTableCell';
export { DataTableFooter } from './DataTableFooter';
export { DataTableActionBar } from './DataTableActionBar';
export { DataTableColumnControl } from './DataTableColumnControl';

// Types
export type {
  // Core types
  DataTableProps,
  DataTableColumn,
  DataTableRowHeight,
  DataTableAlignment,
  DataTableSortDirection,
  ColumnVisibility,
  // Action types
  DataTableAction,
  DataTableActionBarProps,
  // Pagination types
  DataTablePaginationConfig,
  DataTableFooterProps,
  PageSizeOption,
  // Column control types
  DataTableColumnControlProps,
  // Sub-component props
  DataTableHeaderProps,
  DataTableBodyProps,
  DataTableRowProps,
  DataTableCellProps,
} from './types';

/**
 * DataTable Types - Production Match
 *
 * Type definitions for the DataTable pattern that exactly matches
 * the DocuSign Navigator production table.
 */

import type { ReactNode } from 'react';

// =============================================================================
// Core Types
// =============================================================================

export type DataTableRowHeight = 'compact' | 'default' | 'tall';
export type DataTableAlignment = 'left' | 'center' | 'right';
export type DataTableSortDirection = 'ascending' | 'descending' | null;
export type ColumnVisibility = boolean | 'locked';

// =============================================================================
// Column Definition
// =============================================================================

export interface DataTableColumn<T = any> {
  /** Unique column identifier */
  key: string;

  /** Column header text or element */
  header: string | ReactNode;

  /** Custom cell renderer - receives the entire row */
  cell?: (row: T) => ReactNode;

  /** Alternative render function - receives value, row, and index */
  render?: (value: any, row: T, rowIndex: number) => ReactNode;

  /** Column width (CSS value) */
  width?: string;

  /** Column max width (CSS value) */
  maxWidth?: string;

  /** Text alignment */
  alignment?: DataTableAlignment;

  /** Whether column is sortable */
  sortable?: boolean;

  /** Custom sort value extractor */
  sortValue?: (row: T) => string | number;

  /** Start with descending sort instead of ascending */
  startWithDescending?: boolean;

  /** Column visibility - true, false, or 'locked' (always visible) */
  isVisible?: ColumnVisibility;

  /** Column order for display */
  order?: number;

  /** Fixed position in column control (can't be reordered) */
  fixedPosition?: 'start' | 'end';

  /** Additional CSS class for this column's cells */
  className?: string;

  /** Tooltip for header */
  headerTooltip?: string;

  /** Make this column sticky (fixed position on horizontal scroll) */
  sticky?: boolean;
}

// =============================================================================
// Action Types
// =============================================================================

export interface DataTableAction {
  /** Unique action identifier */
  id: string;

  /** Action label */
  label: string;

  /** Icon name */
  icon?: string;

  /** Click handler - receives selected row keys */
  onClick: (selectedRows: Set<string | number>) => void;

  /** Whether action is disabled */
  disabled?: boolean;

  /** Action variant */
  variant?: 'primary' | 'secondary' | 'tertiary' | 'danger';
}

// =============================================================================
// Pagination Config
// =============================================================================

export type PageSizeOption = 5 | 10 | 25 | 50;

export interface DataTablePaginationConfig {
  /** Current page (1-indexed) */
  page: number;

  /** Items per page */
  pageSize: PageSizeOption;

  /** Total number of items */
  totalItems: number;

  /** Page change handler */
  onPageChange: (page: number) => void;

  /** Page size change handler */
  onPageSizeChange: (size: PageSizeOption) => void;

  /** Pagination variant */
  variant?: 'default' | 'simple';

  /** Show page info (e.g., "1-25 of 100") */
  showInfo?: boolean;

  /** Disable pagination controls */
  disabled?: boolean;
}

// =============================================================================
// Main DataTable Props
// =============================================================================

export interface DataTableProps<T = any> {
  // Data
  /** Column definitions */
  columns: DataTableColumn<T>[];

  /** Data rows */
  data: T[];

  /** Function to get unique key for each row */
  getRowKey: (row: T, index: number) => string | number;

  // Appearance
  /** Row height variant */
  rowHeight?: DataTableRowHeight;

  /** Additional CSS class */
  className?: string;

  /** Test automation ID */
  'data-qa'?: string;

  // Selection
  /** Enable row selection */
  selectable?: boolean;

  /** Controlled selected rows */
  selectedRows?: Set<string | number>;

  /** Selection change handler */
  onSelectionChange?: (selection: Set<string | number>) => void;

  // Sorting
  /** Currently sorted column key */
  sortColumn?: string;

  /** Current sort direction */
  sortDirection?: DataTableSortDirection;

  /** Sort change handler */
  onSortChange?: (column: string, direction: DataTableSortDirection) => void;

  // Column Control
  /** Show column control button */
  showColumnControl?: boolean;

  /** Initial column configuration (for reset) */
  initialColumns?: DataTableColumn<T>[];

  /** Column control save handler */
  onColumnControlSave?: (columns: DataTableColumn<T>[]) => void;

  // Action Bar
  /** Actions to show when rows are selected */
  selectionActions?: DataTableAction[];

  /** Custom action bar content */
  actionBarContent?: ReactNode;

  // Pagination
  /** Pagination configuration */
  pagination?: DataTablePaginationConfig;

  // States
  /** Loading state */
  loading?: boolean;

  /** Empty state message */
  emptyMessage?: ReactNode;

  // Row Events
  /** Row click handler */
  onRowClick?: (row: T, index: number) => void;

  /** Render function for row actions (appears in column control cell, aligned with header icon) */
  renderRowActions?: (row: T, rowIndex: number) => ReactNode;

  // Sticky Behavior
  /** Sticky header */
  stickyHeader?: boolean;

  /** Sticky footer */
  stickyFooter?: boolean;
}

// =============================================================================
// Sub-Component Props
// =============================================================================

export interface DataTableHeaderProps<T = any> {
  columns: DataTableColumn<T>[];
  selectable?: boolean;
  isAllSelected?: boolean;
  isIndeterminate?: boolean;
  onSelectAll?: () => void;
  sortColumn?: string;
  sortDirection?: DataTableSortDirection;
  onSort?: (columnKey: string) => void;
  showColumnControl?: boolean;
  onColumnControlClick?: () => void;
  stickyHeader?: boolean;
}

export interface DataTableBodyProps<T = any> {
  columns: DataTableColumn<T>[];
  data: T[];
  getRowKey: (row: T, index: number) => string | number;
  rowHeight?: DataTableRowHeight;
  selectable?: boolean;
  selectedRows?: Set<string | number>;
  onRowSelect?: (rowKey: string | number) => void;
  onRowClick?: (row: T, index: number) => void;
  showColumnControl?: boolean;
  /** Render function for row actions (appears in column control cell) */
  renderRowActions?: (row: T, rowIndex: number) => ReactNode;
}

export interface DataTableRowProps<T = any> {
  row: T;
  rowIndex: number;
  rowKey: string | number;
  columns: DataTableColumn<T>[];
  rowHeight?: DataTableRowHeight;
  selectable?: boolean;
  isSelected?: boolean;
  onSelect?: () => void;
  onClick?: () => void;
  showColumnControl?: boolean;
  /** Content to render in the column control cell */
  rowActions?: ReactNode;
}

export interface DataTableCellProps<T = any> {
  column: DataTableColumn<T>;
  row: T;
  rowIndex: number;
  value: any;
  /** Left offset for sticky columns (calculated from previous sticky columns) */
  stickyLeft?: number;
}

export interface DataTableFooterProps {
  pagination: DataTablePaginationConfig;
  disabled?: boolean;
  stickyFooter?: boolean;
}

export interface DataTableActionBarProps {
  selectedCount: number;
  actions: DataTableAction[];
  customContent?: ReactNode;
  visible: boolean;
  selectedRows: Set<string | number>;
}

export interface DataTableColumnControlProps<T = any> {
  open: boolean;
  columns: DataTableColumn<T>[];
  initialColumns?: DataTableColumn<T>[];
  onSave: (columns: DataTableColumn<T>[]) => void;
  onCancel: () => void;
}

FilterBar:5.content:
/* FilterBar Pattern - Exact DocuSign Navigator Specs */

@import '../../1-tokens/tokens.css';

/* Main Container */
.filterBar {
  display: flex;
  align-items: center;
  width: 100%;
  min-height: 40px; /* Matches button height */
  box-sizing: border-box;
  gap: var(--ink-spacing-50); /* Tight gap (4px) */
}

/* Gap variants using data attribute */
.filterBar[data-gap='medium'] {
  gap: var(--ink-spacing-150); /* 12px */
}

/* View Selector (Documents dropdown) */
.viewSelector {
  flex-shrink: 0;
}

/* Vertical Divider between view selector and search */
.dividerWrapper {
  width: 1px;
  height: 24px;
  flex-shrink: 0;
  background-color: var(--ink-neutral-fade-20); /* Divider color */
  margin: 0 var(--ink-spacing-100); /* 8px space on either side */
}

/* Search Wrapper with indicator */
.searchWrapper {
  position: relative;
  flex: 1;
  min-width: 300px; /* Ensure placeholder text is fully visible */
  max-width: 500px; /* Match production - constrained width */
}

/* AI/Special Search Indicator - Blue dot above search icon */
.searchIndicator {
  position: absolute;
  top: -4px;
  left: 20px; /* Position above search icon, not centered */
  width: 6px;
  height: 6px;
  background-color: var(--ink-cobalt-100);
  border-radius: 50%;
  z-index: 1;
}

/* Search Input */
.searchInput {
  width: 100%;
}

/* Quick Actions Container */
.quickActions {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-100); /* 8px gap between actions */
  flex-shrink: 0;
}

/* Filters Container */
.filters {
  flex-shrink: 0;
}

/**
 * FilterBar Pattern
 *
 * A horizontal action bar for filtering and searching data tables.
 * Typically placed above a DataTable to provide view selection, search,
 * and filter controls.
 *
 * @example
 * ```tsx
 * <FilterBar
 *   viewSelector={
 *     <Dropdown items={viewItems}>
 *       <Button kind="secondary" size="small" endElement={<Icon name="chevron-down" />}>
 *         Documents
 *       </Button>
 *     </Dropdown>
 *   }
 *   search={{
 *     value: searchValue,
 *     onChange: setSearchValue,
 *     placeholder: 'Try keywords, phrases, or a question',
 *   }}
 *   quickActions={[
 *     <IconButton key="bookmark" icon="bookmark" variant="secondary" size="small" />
 *   ]}
 *   filters={
 *     <Button kind="secondary" size="small" startElement={<Icon name="filter" />}>
 *       Filters
 *     </Button>
 *   }
 * />
 * ```
 */

import React from 'react';
import { Divider } from '../../3-primitives/Divider';
import { SearchInput } from '../../4-composites/SearchInput';
import styles from './FilterBar.module.css';

export interface FilterBarSearchConfig {
  /** Current search value */
  value: string;
  /** Callback when search value changes */
  onChange: (value: string) => void;
  /** Callback when search is submitted (Enter key) */
  onSubmit?: () => void;
  /** Placeholder text */
  placeholder?: string;
  /** Search suggestions */
  suggestions?: Array<{ id: string; label: string }>;
  /** Callback when a suggestion is selected */
  onSuggestionSelect?: (suggestion: { id: string; label: string }) => void;
}

export interface FilterBarProps {
  /**
   * View selector element (typically a Dropdown with Button trigger)
   * Appears at the left of the filter bar
   */
  viewSelector?: React.ReactNode;

  /**
   * Search input configuration
   * If provided, renders a SearchInput with the specified props
   */
  search?: FilterBarSearchConfig;

  /**
   * Quick action elements (typically IconButtons)
   * Rendered after the search input
   */
  quickActions?: React.ReactNode[];

  /**
   * Filters element (typically a Button or Dropdown)
   * Appears at the right of the filter bar
   */
  filters?: React.ReactNode;

  /**
   * Show visual indicator on search input (like Navigator's blue dot)
   * Indicates AI-powered or special search capability
   */
  showSearchIndicator?: boolean;

  /**
   * Gap between elements
   * @default "small"
   */
  gap?: 'small' | 'medium';

  /** Additional class name */
  className?: string;

  /** Data QA attribute for testing */
  'data-qa'?: string;
}

export const FilterBar: React.FC<FilterBarProps> = ({
  viewSelector,
  search,
  quickActions,
  filters,
  showSearchIndicator = false,
  gap = 'small',
  className,
  'data-qa': dataQa,
}) => {
  const classes = [styles.filterBar, className].filter(Boolean).join(' ');

  return (
    <div className={classes} data-qa={dataQa} data-gap={gap}>
      {/* View Selector */}
      {viewSelector && <div className={styles.viewSelector}>{viewSelector}</div>}

      {/* Divider between view selector and search */}
      {viewSelector && search && (
        <div className={styles.dividerWrapper}>
          <Divider orientation="vertical" />
        </div>
      )}

      {/* Search Input */}
      {search && (
        <div className={styles.searchWrapper}>
          {showSearchIndicator && <div className={styles.searchIndicator} />}
          <SearchInput
            placeholder={search.placeholder || 'Search...'}
            value={search.value}
            onChange={search.onChange}
            onSearch={search.onSubmit ? () => search.onSubmit?.() : undefined}
            suggestions={search.suggestions}
            onSuggestionSelect={search.onSuggestionSelect}
            size="small"
            className={styles.searchInput}
          />
        </div>
      )}

      {/* Quick Actions */}
      {quickActions && quickActions.length > 0 && (
        <div className={styles.quickActions}>
          {quickActions.map((action, index) => (
            <React.Fragment key={index}>{action}</React.Fragment>
          ))}
        </div>
      )}

      {/* Filters */}
      {filters && <div className={styles.filters}>{filters}</div>}
    </div>
  );
};

FilterBar.displayName = 'FilterBar';

GlobalNav:5.content:
/* Ink Design System - Global Navigation Pattern */

/* Container */
.container {
  width: 100%;
  height: 64px;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 var(--ink-spacing-200); /* 16px */
  background: var(--ink-white-100); /* Pure white for GlobalNav */
  border-bottom: 1px solid var(--ink-border-color-subtle);
  position: sticky;
  top: 0;
  z-index: var(--ink-z-sticky);
}

/* Start Section */
.start {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-400); /* 40px */
  flex-shrink: 0;
}

/* Product Section */
.product {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-150); /* 12px */
}

/* App Switcher */
.appSwitcher {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 64px;
  padding: 0 var(--ink-spacing-150); /* 12px */
  background: transparent;
  border: none;
  color: var(--ink-font-color-default);
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
}

.appSwitcher:hover {
  background-color: var(--ink-item-bg-color-hover);
}

.appSwitcher:active {
  background-color: var(--ink-item-bg-color-active);
}

/* Logo */
.logo {
  height: 24px;
  display: flex;
  align-items: center;
}

.logo img {
  height: 24px;
  width: 119.25px;
  object-fit: contain;
}

/* Navigation */
.navigation {
  display: flex;
  align-items: center;
  gap: 0;
}

/* Navigation Item */
.navItem {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 64px;
  padding: 0 var(--ink-spacing-300); /* 24px */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 16px */
  font-weight: var(--ink-font-weight-semibold); /* 600 */
  line-height: 20px;
  color: var(--ink-font-secondary); /* rgba(25,24,35,0.65) */
  text-decoration: none;
  border-bottom: 3px solid transparent;
  transition: all var(--ink-transition-fast);
  cursor: pointer;
  position: relative;
}

.navItem:hover {
  color: var(--ink-font-color-default);
  background-color: var(--ink-item-bg-color-hover);
}

.navItemActive {
  color: var(--ink-font-color-default); /* rgba(25,24,35,0.9) */
  border-bottom-color: var(--ink-font-color-default);
}

/* End Section */
.end {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: 0;
  flex-shrink: 0;
}

/* Icon Button */
.iconButton {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 64px;
  padding: 0 var(--ink-spacing-150); /* 12px */
  background: transparent;
  border: none;
  color: var(--ink-font-color-default);
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
  position: relative;
}

.iconButton:hover {
  background-color: var(--ink-item-bg-color-hover);
}

.iconButton:active {
  background-color: var(--ink-item-bg-color-active);
}

/* Badge */
.badge {
  position: absolute;
  top: 12px;
  right: 8px;
  min-width: 18px;
  height: 18px;
  padding: 0 4px;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--ink-font-size-xxs); /* 10px - small badge counter */
  font-weight: var(--ink-font-weight-semibold);
  background: var(--ink-red-100);
  color: white;
  border-radius: 9px;
  pointer-events: none;
}

/* Search Pill Button */
.searchPill {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-100); /* 8px */
  height: 40px;
  padding: 0 var(--ink-spacing-200); /* 16px */
  background: var(--ink-white-100);
  border: var(--ink-form-border-width) solid var(--ink-border-color-default);
  border-radius: var(--ink-radius-full);
  color: var(--ink-font-secondary);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 16px */
  font-weight: var(--ink-font-weight-regular);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  white-space: nowrap;
}

.searchPill:hover {
  background-color: var(--ink-item-bg-color-hover);
  border-color: var(--ink-border-hover);
}

.searchPill:active {
  background-color: var(--ink-item-bg-color-active);
}

/* Avatar Button */
.avatarButton {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 64px;
  padding: var(--ink-spacing-100) var(--ink-spacing-150); /* 8px 12px */
  background: transparent;
  border: none;
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
}

.avatarButton:hover {
  background-color: var(--ink-item-bg-color-hover);
}

.avatarButton:active {
  background-color: var(--ink-item-bg-color-active);
}

/* Responsive */
@media (max-width: 768px) {
  .navigation {
    display: none;
  }

  .start {
    gap: var(--ink-spacing-200); /* 24px on mobile */
  }
}

import React from 'react';
import styles from './GlobalNav.module.css';
import { Avatar } from '../../3-primitives/Avatar';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';
import type { IconName } from '../../3-primitives/Icon';

export interface GlobalNavItem {
  id: string;
  label: string;
  href?: string;
  active?: boolean;
  onClick?: () => void;
}

export interface GlobalNavProps {
  /** Logo element (image or component) */
  logo?: React.ReactNode;
  /** Navigation items */
  navItems?: GlobalNavItem[];
  /** Show app switcher icon */
  showAppSwitcher?: boolean;
  /** App switcher click handler */
  onAppSwitcherClick?: () => void;
  /** Show search icon */
  showSearch?: boolean;
  /** Search button variant - 'icon' for icon only, 'pill' for pill-shaped button with text */
  searchVariant?: 'icon' | 'pill';
  /** Search click handler */
  onSearchClick?: () => void;
  /** Show notifications */
  showNotifications?: boolean;
  /** Notification click handler */
  onNotificationClick?: () => void;
  /** Notification count */
  notificationCount?: number;
  /** Show settings */
  showSettings?: boolean;
  /** Settings icon to use */
  settingsIcon?: IconName;
  /** Settings click handler */
  onSettingsClick?: () => void;
  /** User info for avatar */
  user?: {
    name: string;
    avatar?: string;
  };
  /** User menu items */
  onUserMenuClick?: () => void;
  /** Extra actions rendered before help/avatar (e.g., AI assistant button) */
  extraActions?: React.ReactNode;
  /** Additional className */
  className?: string;
}

export const GlobalNav = ({
  logo,
  navItems = [],
  showAppSwitcher = true,
  onAppSwitcherClick,
  showSearch = false,
  searchVariant = 'icon',
  onSearchClick,
  showNotifications = false,
  onNotificationClick,
  notificationCount,
  showSettings = false,
  settingsIcon = 'settings',
  onSettingsClick,
  user,
  onUserMenuClick,
  extraActions,
  className,
}: GlobalNavProps) => {
  const containerClasses = [styles.container, className].filter(Boolean).join(' ');

  return (
    <header className={containerClasses}>
      {/* Left Section */}
      <div className={styles.start}>
        {/* Product Section */}
        <div className={styles.product}>
          {/* App Switcher */}
          {showAppSwitcher && (
            <IconButton
              icon="menu"
              variant="tertiary"
              size="medium"
              onClick={onAppSwitcherClick}
              aria-label="App switcher"
              className={styles.appSwitcher}
            />
          )}

          {/* Logo */}
          {logo && <div className={styles.logo}>{logo}</div>}
        </div>

        {/* Navigation Items */}
        {navItems.length > 0 && (
          <nav className={styles.navigation}>
            {navItems.map((item) => (
              <a
                key={item.id}
                href={item.href || '#'}
                className={`${styles.navItem} ${item.active ? styles.navItemActive : ''}`}
                onClick={(e) => {
                  if (item.onClick) {
                    e.preventDefault();
                    item.onClick();
                  }
                }}
              >
                {item.label}
              </a>
            ))}
          </nav>
        )}
      </div>

      {/* Right Section */}
      <div className={styles.end}>
        {/* Search */}
        {showSearch && searchVariant === 'icon' && (
          <IconButton
            icon="search"
            variant="tertiary"
            size="medium"
            onClick={onSearchClick}
            aria-label="Search"
            className={styles.iconButton}
          />
        )}
        {showSearch && searchVariant === 'pill' && (
          <button className={styles.searchPill} onClick={onSearchClick} aria-label="Search">
            <Icon name="search" size="small" />
            <span>Search</span>
          </button>
        )}

        {/* Notifications */}
        {showNotifications && (
          <button
            className={styles.iconButton}
            onClick={onNotificationClick}
            aria-label="Notifications"
          >
            <Icon name="bell" size="medium" />
            {notificationCount !== undefined && notificationCount > 0 && (
              <span className={styles.badge}>
                {notificationCount > 99 ? '99+' : notificationCount}
              </span>
            )}
          </button>
        )}

        {/* Settings */}
        {showSettings && (
          <IconButton
            icon={settingsIcon}
            variant="tertiary"
            size="medium"
            onClick={onSettingsClick}
            aria-label="Settings"
            className={styles.iconButton}
          />
        )}

        {/* Extra actions (e.g., AI assistant) */}
        {extraActions}

        {/* Help Menu */}
        <IconButton
          icon="help"
          variant="tertiary"
          size="medium"
          aria-label="Help"
          className={styles.iconButton}
        />

        {/* User Avatar */}
        {user && (
          <button className={styles.avatarButton} onClick={onUserMenuClick} aria-label="User menu">
            <Avatar name={user.name} src={user.avatar} size="small" />
          </button>
        )}
      </div>
    </header>
  );
};

GlobalNav.displayName = 'GlobalNav';

LocalNav:5.content:
@import '../../1-tokens/tokens.css';

.localNav {
  display: flex;
  flex-direction: column;
  width: 280px; /* Fixed width for local navigation sidebar */
  height: 100%;
  background-color: var(--ink-bg-color-canvas-page); /* #F7F6F7 - DocuSign canvas page background */
  font-family: var(--ink-font-family);
  transition: width 150ms cubic-bezier(0.33, 0, 0.67, 1); /* Production timing */
  overflow: hidden; /* Prevent content overflow during animation */
  /* Note: No border-right - DocuSign separates via content area background */
}

/* Collapsed state - 72px per Figma spec */
.navCollapsed {
  width: 72px;
}

/* Hovered state when collapsed - expand */
.navCollapsed.navHovered {
  width: 280px;
  position: absolute;
  z-index: 100;
  box-shadow: var(--ink-shadow-lg);
}

/* Header Wrapper - fixed height to prevent jitter during animation */
.headerWrapper {
  position: relative;
  padding: var(--ink-spacing-200); /* 16px all sides */
  padding-bottom: 0;
  min-height: 56px; /* 40px button + 16px padding */
}

/* Header Chevron Rotation */
.headerChevron {
  display: inline-flex;
  align-items: center;
  transition: transform var(--ink-transition-fast);
}

.headerChevronRotated {
  transform: rotate(180deg);
}

/* Header Button Wrappers - for smooth crossfade between states */
.headerButtonExpanded {
  display: none;
}

.headerButtonExpanded.visible {
  display: block;
}

.headerButtonCollapsedWrapper {
  display: none;
  justify-content: center;
}

.headerButtonCollapsedWrapper.visible {
  display: flex;
}

/* Header Button - Collapsed variant (IconButton) */
.headerButtonCollapsed {
  width: 40px;
  height: 40px;
}

/* Collapsed header wrapper - same min-height to prevent jitter */
.navCollapsed .headerWrapper {
  padding: var(--ink-spacing-200);
  padding-bottom: 0;
}

/* Header Dropdown Menu */
.headerMenu {
  position: absolute;
  top: calc(100% + 4px);
  left: 0;
  right: 0;
  background-color: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-20);
  border-radius: var(--ink-radius-sm);
  box-shadow: var(--ink-shadow-md);
  z-index: 100;
  padding: var(--ink-spacing-1) 0;
  overflow: hidden;
}

.headerMenuItem {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  width: 100%;
  padding: 11px 15px 11px 16px;
  min-height: 44px;
  border: none;
  background: transparent;
  cursor: pointer;
  text-align: left;
  transition: background-color var(--ink-transition-fast);
  font-family: inherit;
}

.headerMenuItem:hover {
  background-color: var(--ink-neutral-fade-5); /* Production: rgba(19, 0, 50, 0.05) */
}

.headerMenuItemIcon {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--ink-neutral-fade-70);
}

.headerMenuItemLabel {
  flex: 1;
  font-size: var(--ink-font-body-s-size);
  font-weight: var(--ink-font-weight-regular);
  line-height: var(--ink-font-body-s-line-height);
  color: var(--ink-neutral-fade-90);
}

.headerMenuItemArrow {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--ink-neutral-fade-50);
}

/* Sections */
.sections {
  flex: 1;
  overflow-y: auto;
  padding: 0; /* DocuSign: no padding on scrollable section */
  margin-top: var(--ink-spacing-200); /* 16px space between header and first item */
}

.section {
  margin-bottom: var(--ink-spacing-2);
}

.section:last-child {
  margin-bottom: 0;
}

/* Section with divider line above - matches production exactly */
.sectionWithDivider {
  position: relative;
  margin-top: var(--ink-spacing-200); /* 16px */
  padding-top: var(--ink-spacing-200); /* 16px - space after divider */
}

/* Divider line with horizontal inset to match production */
.sectionWithDivider::before {
  content: '';
  position: absolute;
  top: 0;
  left: 32px;
  right: 16px;
  height: 1px;
  background-color: var(--ink-neutral-fade-10); /* Production exact color */
}

.sectionHeader {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  padding: 11px 15px 11px 23px; /* Matches DocuSign exact padding */
  min-height: 48px;
  cursor: pointer;
  border: none;
  background: transparent;
  width: 100%;
  text-align: left;
  transition: background-color var(--ink-transition-fast);
  font-family: inherit;
  font-size: var(--ink-font-body-s-size); /* 14px - DocuSign section header font size */
}

.sectionHeader:hover {
  background-color: var(--ink-neutral-fade-5); /* Production: rgba(19, 0, 50, 0.05) */
}

.sectionIcon {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--ink-neutral-fade-70);
}

.sectionTitle {
  flex: 1;
  font-size: var(--ink-font-element-label-size); /* 14px */
  font-weight: var(--ink-font-weight-semibold); /* 600 */
  line-height: var(--ink-font-element-label-line-height);
  color: var(--ink-neutral-fade-90);
}

/* Non-collapsible section title (standalone div, not in button) */
.section > .sectionTitle {
  padding: 11px 15px 11px 23px; /* Matches DocuSign exact padding */
  min-height: 48px;
  margin-bottom: 0;
  display: flex;
  align-items: center;
}

/* Section Label Header (uppercase, non-interactive) */
.sectionLabelHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 11px 15px 11px 23px; /* Matches DocuSign exact padding */
  min-height: 48px;
}

.sectionLabel {
  font-size: var(--ink-font-section-headline-size); /* 12px */
  font-weight: var(--ink-font-weight-semibold);
  line-height: var(--ink-font-section-headline-line-height);
  text-transform: uppercase;
  letter-spacing: 0.5px;
  color: var(--ink-neutral-fade-70);
  flex: 1;
}

/* Icon for section label headers (non-collapsible) */
.sectionLabelIcon {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--ink-neutral-fade-70);
  margin-right: var(--ink-spacing-150); /* 12px gap between icon and label */
}

.sectionChevron {
  display: flex;
  align-items: center;
  color: var(--ink-neutral-fade-70);
  transition: transform var(--ink-transition-fast);
}

.sectionHeader.collapsed .sectionChevron {
  transform: rotate(-90deg);
}

.sectionItems {
  overflow: hidden;
  max-height: 1000px; /* Large enough for any reasonable nav section */
  transition: max-height var(--ink-transition-fast);
}

.sectionItems.collapsed {
  max-height: 0;
}

/* Items */
.itemList {
  list-style: none;
  margin: 0;
  padding: 0;
}

.item {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  width: 100%;
  padding: 11px 15px 11px 23px; /* Matches DocuSign exact padding */
  height: 42px; /* DocuSign inactive item height */
  min-height: 42px;
  border: none;
  background: transparent;
  cursor: pointer;
  position: relative;
  text-align: left;
  transition: background-color var(--ink-transition-fast);
  font-family: inherit;
  font-size: var(--ink-font-body-s-size); /* 14px - DocuSign item font size */
}

.item.nested {
  /* Nested items align text with icon items */
  padding-left: var(--ink-spacing-600); /* 48px */
  height: 42px; /* Production: 41.5px - nested items are shorter */
  min-height: 42px;
}

.item.hasIcon {
  /* Items with icons are 48px height per DocuSign */
  height: 48px;
  min-height: 48px;
}

.item.hasMenu {
  padding-right: var(--ink-spacing-100); /* 8px - room for menu button */
}

.item:hover {
  background-color: var(--ink-neutral-fade-5); /* Production: rgba(19, 0, 50, 0.05) */
}

.item.active {
  height: 48px; /* DocuSign active item height */
  min-height: 48px;
  background-color: var(--ink-bg-active-subtle);
  font-weight: var(--ink-font-weight-semibold); /* 600 - DocuSign active weight */
}

/* Nested active items: background but NO left indicator bar */
.item.nested.active {
  height: 42px;
  min-height: 42px;
  background-color: var(--ink-bg-active-subtle);
  font-weight: var(--ink-font-weight-semibold);
}

/* Hide indicator bar for nested items */
.item.nested .activeIndicator {
  display: none;
}

.itemIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  color: var(--ink-neutral-fade-70);
}

.item.active .itemIcon {
  color: var(--ink-font-color-default);
}

.itemLabel {
  flex: 1;
  font-size: var(--ink-font-body-s-size); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-body-s-line-height);
  color: var(--ink-neutral-fade-90);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  opacity: 1;
  transition: opacity 100ms cubic-bezier(0.33, 0, 0.67, 1); /* Production timing */
}

/* Hide labels when nav is collapsed */
.navCollapsed .itemLabel {
  opacity: 0;
}

.item.active .itemLabel {
  color: var(--ink-font-color-default);
}

/* Active Indicator */
.activeIndicator {
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: var(--ink-spacing-50); /* 4px active indicator bar */
  background-color: var(--ink-border-active-indicator); /* Solid visible bar for active state */
  border-radius: 0;
}

/* Collapsed Item (icon only) - full width for active indicator to reach edge */
.itemCollapsed {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 100%;
  height: 48px;
  border: none;
  background: transparent;
  cursor: pointer;
  position: relative;
  color: var(--ink-neutral-fade-70);
  transition: background-color var(--ink-transition-fast);
}

.itemCollapsed:hover {
  background-color: var(--ink-neutral-fade-5); /* Production: rgba(19, 0, 50, 0.05) */
}

.itemCollapsed.active {
  background-color: var(--ink-bg-active-subtle);
  color: var(--ink-font-color-default);
}

.itemCollapsed .activeIndicator {
  left: 0;
  width: var(--ink-spacing-50); /* 4px - matches expanded state */
  border-radius: 0;
}

/* Menu Button - align within item row, constrained by divider bounds */
.menuButton {
  flex-shrink: 0;
  margin-right: var(--ink-spacing-100); /* 8px from right edge to align with divider */
}

/* Scrollbar styling */
.sections::-webkit-scrollbar {
  width: var(--ink-spacing-2); /* 8px */
}

.sections::-webkit-scrollbar-track {
  background: transparent;
}

.sections::-webkit-scrollbar-thumb {
  background-color: var(--ink-neutral-30);
  border-radius: var(--ink-radius-full);
  border: var(--ink-focus-width) solid var(--ink-white-100);
}

.sections::-webkit-scrollbar-thumb:hover {
  background-color: var(--ink-neutral-40);
}

/* Footer */
.footer {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--ink-spacing-200); /* 16px - matches DocuSign footer padding */
  min-height: calc(
    var(--ink-spacing-500) + var(--ink-spacing-400)
  ); /* 40px + 32px = 72px total with padding */
  margin-top: auto;
  gap: var(--ink-spacing-2);
  /* Note: DocuSign has no border-top */
}

.footerToggle {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  flex: 1;
  opacity: 1;
  transition: opacity 100ms cubic-bezier(0.33, 0, 0.67, 1); /* Production timing */
}

/* Hide footer toggle content when collapsed */
.navCollapsed .footerToggle {
  opacity: 0;
  pointer-events: none;
}

.footerLabel {
  font-size: var(--ink-font-element-label-size);
  font-weight: var(--ink-font-weight-regular);
  line-height: var(--ink-font-element-label-line-height);
  color: var(--ink-neutral-fade-90);
}

.footerIcon {
  display: flex;
  align-items: center;
  color: var(--ink-neutral-fade-70);
  flex-shrink: 0;
}

/* Collapsed Footer - center the lock button in 72px width */
.footerCollapsed {
  justify-content: center;
  padding: var(--ink-spacing-200);
}

import React, { useState, useRef, useEffect } from 'react';
import styles from './LocalNav.module.css';
import { Icon } from '../../3-primitives/Icon';
import type { IconName } from '../../3-primitives/Icon';
import { Badge } from '../../3-primitives/Badge';
import { Switch } from '../../3-primitives/Switch';
import { Tooltip } from '../../3-primitives/Tooltip';
import { Button } from '../../3-primitives/Button';
import { IconButton } from '../../3-primitives/IconButton';

export interface LocalNavItem {
  id: string;
  label: string;
  icon?: IconName;
  badge?: string;
  hasMenu?: boolean;
  onMenuClick?: (e: React.MouseEvent) => void;
  onClick?: () => void;
  active?: boolean;
  nested?: boolean;
}

export interface HeaderMenuItem {
  id: string;
  label: string;
  icon?: IconName;
  /** Shows submenu arrow indicator */
  hasSubmenu?: boolean;
  onClick?: () => void;
}

export interface LocalNavSection {
  id: string;
  title?: string;
  icon?: IconName;
  collapsible?: boolean;
  defaultExpanded?: boolean;
  items: LocalNavItem[];
  /** Show as uppercase label header (non-interactive) */
  headerLabel?: boolean;
  /** Action button for header (e.g., "New Folder") */
  headerAction?: {
    icon: IconName;
    label: string;
    onClick: () => void;
  };
  /** Show a divider line above this section */
  hasDivider?: boolean;
}

export interface LocalNavProps {
  /** Header dropdown label */
  headerLabel?: string;
  /** Header icon for collapsed state (defaults to 'plus') */
  headerIcon?: IconName;
  /** Menu items for header dropdown */
  headerMenuItems?: HeaderMenuItem[];
  /** Sections of navigation items */
  sections: LocalNavSection[];
  /** Callback when header dropdown is clicked (only used if no headerMenuItems) */
  onHeaderClick?: () => void;
  /** Active item ID */
  activeItemId?: string;
  /** Custom className */
  className?: string;
  /**
   * Whether the nav is locked open. When locked, nav stays expanded.
   * When unlocked, nav collapses and expands on hover.
   * Defaults to true (locked/expanded).
   */
  isLocked?: boolean;
  /** Callback when lock state changes */
  onLockChange?: (locked: boolean) => void;
  /**
   * Whether collapsibility is enabled. If false, nav is always expanded
   * and lock button is hidden. Defaults to true.
   */
  allowCollapsibility?: boolean;
  /** Footer toggle for feature flags (e.g., "New navigation") */
  footerToggle?: {
    label: string;
    checked: boolean;
    onChange: (checked: boolean) => void;
    icon?: IconName;
  };
}

export const LocalNav: React.FC<LocalNavProps> = ({
  headerLabel = 'Start',
  headerIcon = 'plus',
  headerMenuItems,
  sections,
  onHeaderClick,
  activeItemId,
  className,
  isLocked: controlledIsLocked,
  onLockChange,
  allowCollapsibility = true,
  footerToggle,
}) => {
  const [headerMenuOpen, setHeaderMenuOpen] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const headerRef = useRef<HTMLDivElement>(null);
  const navRef = useRef<HTMLElement>(null);
  const [collapsedSections, setCollapsedSections] = useState<Set<string>>(
    new Set(sections.filter((s) => s.collapsible && !s.defaultExpanded).map((s) => s.id))
  );

  // Support both controlled and uncontrolled lock state
  const isControlled = controlledIsLocked !== undefined;
  const [internalIsLocked, setInternalIsLocked] = useState(true);
  const isLocked = isControlled ? controlledIsLocked : internalIsLocked;

  // Handle lock toggle
  const handleLockToggle = () => {
    const newLocked = !isLocked;
    if (!isControlled) {
      setInternalIsLocked(newLocked);
    }
    onLockChange?.(newLocked);
  };

  // Determine if nav should show expanded
  // If collapsibility is disabled, always expanded
  // If locked, always expanded
  // Otherwise, expand on hover
  const isExpanded = !allowCollapsibility || isLocked || isHovered;

  // Handle mouse enter/leave for hover-to-expand (when unlocked)
  const handleMouseEnter = () => {
    if (allowCollapsibility && !isLocked) {
      setIsHovered(true);
    }
  };

  const handleMouseLeave = () => {
    if (allowCollapsibility && !isLocked) {
      setIsHovered(false);
      setHeaderMenuOpen(false); // Close menu when leaving collapsed nav
    }
  };

  // Close header menu when clicking outside
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (headerRef.current && !headerRef.current.contains(event.target as Node)) {
        setHeaderMenuOpen(false);
      }
    };

    if (headerMenuOpen) {
      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }
  }, [headerMenuOpen]);

  const handleHeaderClick = () => {
    if (headerMenuItems && headerMenuItems.length > 0) {
      setHeaderMenuOpen(!headerMenuOpen);
    } else if (onHeaderClick) {
      onHeaderClick();
    }
  };

  const handleMenuItemClick = (item: HeaderMenuItem) => {
    item.onClick?.();
    setHeaderMenuOpen(false);
  };

  const handleItemClick = (item: LocalNavItem) => {
    if (item.onClick) {
      item.onClick();
    }
  };

  const toggleSection = (sectionId: string) => {
    setCollapsedSections((prev) => {
      const newSet = new Set(prev);
      if (newSet.has(sectionId)) {
        newSet.delete(sectionId);
      } else {
        newSet.add(sectionId);
      }
      return newSet;
    });
  };

  // Compute collapsed state for CSS classes
  // Nav is "collapsed" when collapsibility is enabled AND not locked AND not currently hovered
  const isCollapsed = allowCollapsibility && !isLocked && !isHovered;

  return (
    <nav
      ref={navRef}
      className={`${styles.localNav} ${isCollapsed ? styles.navCollapsed : ''} ${isHovered && !isLocked ? styles.navHovered : ''} ${className || ''}`}
      onMouseEnter={handleMouseEnter}
      onMouseLeave={handleMouseLeave}
      onFocus={handleMouseEnter}
    >
      {/* Header */}
      <div className={styles.headerWrapper} ref={headerRef}>
        {/* Both buttons rendered for smooth animation - visibility controlled by CSS */}
        <div className={`${styles.headerButtonExpanded} ${isExpanded ? styles.visible : ''}`}>
          <Button
            kind="primary"
            size="medium"
            fullWidth
            onClick={handleHeaderClick}
            aria-expanded={headerMenuOpen}
            aria-haspopup={headerMenuItems && headerMenuItems.length > 0 ? 'menu' : undefined}
            tabIndex={isExpanded ? 0 : -1}
            endElement={
              <span
                className={`${styles.headerChevron} ${headerMenuOpen ? styles.headerChevronRotated : ''}`}
              >
                <Icon name="chevron-down" size="small" />
              </span>
            }
          >
            {headerLabel}
          </Button>
        </div>
        <div className={`${styles.headerButtonCollapsedWrapper} ${!isExpanded ? styles.visible : ''}`}>
          <IconButton
            icon={headerIcon}
            size="medium"
            variant="primary"
            onClick={handleHeaderClick}
            aria-label={headerLabel}
            tabIndex={isExpanded ? -1 : 0}
            className={styles.headerButtonCollapsed}
          />
        </div>

        {/* Header Dropdown Menu */}
        {headerMenuItems && headerMenuItems.length > 0 && headerMenuOpen && isExpanded && (
          <div className={styles.headerMenu} role="menu">
            {headerMenuItems.map((item) => (
              <button
                key={item.id}
                className={styles.headerMenuItem}
                onClick={() => handleMenuItemClick(item)}
                role="menuitem"
              >
                {item.icon && (
                  <span className={styles.headerMenuItemIcon}>
                    <Icon name={item.icon} size="medium" />
                  </span>
                )}
                <span className={styles.headerMenuItemLabel}>{item.label}</span>
                {item.hasSubmenu && (
                  <span className={styles.headerMenuItemArrow}>
                    <Icon name="chevron-right" size="small" />
                  </span>
                )}
              </button>
            ))}
          </div>
        )}
      </div>

      {/* Sections */}
      <div className={styles.sections}>
        {sections.map((section) => {
          const isSectionCollapsed = collapsedSections.has(section.id);

          // Helper to render an item (reused in multiple places)
          const renderItem = (item: LocalNavItem) => {
            const isItemActive = item.active || item.id === activeItemId;

            // Collapsed nav: show only icon items (skip nested items without icons)
            if (!isExpanded && !item.icon) {
              return null;
            }

            return (
              <li key={item.id}>
                {!isExpanded ? (
                  // Collapsed view: icon only with tooltip
                  <Tooltip content={item.label} side="right">
                    <button
                      className={`${styles.itemCollapsed} ${isItemActive ? styles.active : ''}`}
                      onClick={() => handleItemClick(item)}
                      aria-label={item.label}
                    >
                      {item.icon && <Icon name={item.icon} size="medium" />}
                      {isItemActive && <div className={styles.activeIndicator} />}
                    </button>
                  </Tooltip>
                ) : (
                  // Expanded view: full item (hover handled by CSS)
                  // Use div with role="button" when hasMenu to avoid button-in-button nesting
                  item.hasMenu ? (
                    <div
                      role="button"
                      tabIndex={0}
                      className={`${styles.item} ${isItemActive ? styles.active : ''} ${
                        item.nested ? styles.nested : ''
                      } ${styles.hasMenu} ${item.icon ? styles.hasIcon : ''}`}
                      onClick={() => handleItemClick(item)}
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' || e.key === ' ') {
                          e.preventDefault();
                          handleItemClick(item);
                        }
                      }}
                    >
                      {item.icon && (
                        <span className={styles.itemIcon}>
                          <Icon name={item.icon} size="medium" />
                        </span>
                      )}
                      <span className={styles.itemLabel}>{item.label}</span>
                      {item.badge && <Badge variant="subtle">{item.badge}</Badge>}
                      <IconButton
                        icon="overflow-horizontal"
                        size="small"
                        variant="tertiary"
                        onClick={(e) => {
                          e.stopPropagation();
                          item.onMenuClick?.(e);
                        }}
                        aria-label="More options"
                        className={styles.menuButton}
                      />
                      {isItemActive && <div className={styles.activeIndicator} />}
                    </div>
                  ) : (
                    <button
                      className={`${styles.item} ${isItemActive ? styles.active : ''} ${
                        item.nested ? styles.nested : ''
                      } ${item.icon ? styles.hasIcon : ''}`}
                      onClick={() => handleItemClick(item)}
                    >
                      {item.icon && (
                        <span className={styles.itemIcon}>
                          <Icon name={item.icon} size="medium" />
                        </span>
                      )}
                      <span className={styles.itemLabel}>{item.label}</span>
                      {item.badge && <Badge variant="subtle">{item.badge}</Badge>}
                      {isItemActive && <div className={styles.activeIndicator} />}
                    </button>
                  )
                )}
              </li>
            );
          };

          return (
            <div
              key={section.id}
              className={`${styles.section} ${section.hasDivider ? styles.sectionWithDivider : ''}`}
            >
              {section.headerLabel && section.title ? (
                <>
                  {isExpanded && (
                    <div className={styles.sectionLabelHeader}>
                      {section.icon && (
                        <span className={styles.sectionLabelIcon}>
                          <Icon name={section.icon} size="medium" />
                        </span>
                      )}
                      <span className={styles.sectionLabel}>{section.title}</span>
                      {section.headerAction && (
                        <IconButton
                          icon={section.headerAction.icon}
                          size="small"
                          variant="tertiary"
                          onClick={section.headerAction.onClick}
                          aria-label={section.headerAction.label}
                          title={section.headerAction.label}
                        />
                      )}
                    </div>
                  )}
                  <ul className={styles.itemList}>{section.items.map(renderItem)}</ul>
                </>
              ) : section.collapsible && section.title ? (
                <>
                  {isExpanded && (
                    <button
                      className={`${styles.sectionHeader} ${
                        isSectionCollapsed ? styles.collapsed : ''
                      }`}
                      onClick={() => toggleSection(section.id)}
                    >
                      {section.icon && (
                        <span className={styles.sectionIcon}>
                          <Icon name={section.icon} size="medium" />
                        </span>
                      )}
                      <span className={styles.sectionTitle}>{section.title}</span>
                      <span className={styles.sectionChevron}>
                        <Icon name="chevron-down" size="medium" />
                      </span>
                    </button>
                  )}
                  {isExpanded ? (
                    <div
                      className={`${styles.sectionItems} ${
                        isSectionCollapsed ? styles.collapsed : ''
                      }`}
                    >
                      <ul className={styles.itemList}>{section.items.map(renderItem)}</ul>
                    </div>
                  ) : (
                    <ul className={styles.itemList}>{section.items.map(renderItem)}</ul>
                  )}
                </>
              ) : (
                <>
                  {isExpanded && section.title && (
                    <div className={styles.sectionTitle}>{section.title}</div>
                  )}
                  <ul className={styles.itemList}>{section.items.map(renderItem)}</ul>
                </>
              )}
            </div>
          );
        })}
      </div>

      {/* Footer */}
      {(footerToggle || allowCollapsibility) && (
        <div className={`${styles.footer} ${!isExpanded ? styles.footerCollapsed : ''}`}>
          {isExpanded && footerToggle && (
            <>
              <div className={styles.footerToggle}>
                {footerToggle.icon && (
                  <span className={styles.footerIcon}>
                    <Icon name={footerToggle.icon} size="medium" />
                  </span>
                )}
                <span className={styles.footerLabel}>{footerToggle.label}</span>
              </div>
              <Switch checked={footerToggle.checked} onChange={footerToggle.onChange} />
            </>
          )}
          {allowCollapsibility && (
            <Tooltip content={isLocked ? 'Unlock sidebar' : 'Lock sidebar'} side="right">
              <IconButton
                icon={isLocked ? 'lock' : 'unlock'}
                size="medium"
                variant="tertiary"
                onClick={handleLockToggle}
                aria-label={isLocked ? 'Unlock navigation sidebar' : 'Lock navigation sidebar'}
              />
            </Tooltip>
          )}
        </div>
      )}
    </nav>
  );
};

LocalNav.displayName = 'LocalNav';

PageHeader:5.content:
/* PageHeader Pattern Styles */
@import '../../1-tokens/tokens.css';

.container {
  width: 100%;
}

.titleSection {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-150); /* 12px gap between title and badge */
}

.title {
  font-size: var(--ink-font-size-xxl); /* 32px */
  font-weight: var(--ink-font-weight-regular); /* DocuSign uses 400 for page titles */
  line-height: var(--ink-font-heading-m-line-height); /* 1.25  40px line height */
  color: var(--ink-font-default);
  margin: 0;
}

import React from 'react';
import styles from './PageHeader.module.css';
import { Heading } from '../../3-primitives/Typography';
import { AIBadge } from '../../4-composites/AIBadge';
import { Inline } from '../../2-utilities/Inline';

export interface PageHeaderProps {
  /** Page title (H1) */
  title: string;
  /** Show AIBadge next to title (defaults to true) */
  showAIBadge?: boolean;
  /** Custom AIBadge text (defaults to "AI-Assisted") */
  aiBadgeText?: string;
  /** Optional action buttons on the right */
  actions?: React.ReactNode;
  /** Additional className */
  className?: string;
}

/**
 * PageHeader - Page title with optional AI badge and actions
 *
 * Layer 5: Patterns
 *
 * DocuSign Layout Rules:
 * - Title: 32px, weight 400 (--ink-font-heading-m)
 * - AIBadge: Inline with title, 12px gap
 * - Actions: Right-aligned, 8px gap between buttons (secondary kind recommended)
 *
 * @example
 * ```tsx
 * <PageHeader
 *   title="Completed"
 *   showAIBadge
 *   actions={
 *     <>
 *       <IconButton icon="settings" variant="tertiary" />
 *       <Button kind="secondary" startElement={<Icon name="plus" />}>
 *         New
 *       </Button>
 *     </>
 *   }
 * />
 * ```
 */
export const PageHeader: React.FC<PageHeaderProps> = ({
  title,
  showAIBadge = false,
  aiBadgeText,
  actions,
  className,
}) => {
  const containerClasses = [styles.container, className].filter(Boolean).join(' ');

  return (
    <header className={containerClasses}>
      <Inline justify="between" align="center" gap="medium">
        {/* Left section: Title + AIBadge */}
        <div className={styles.titleSection}>
          <Heading level={1} className={styles.title}>
            {title}
          </Heading>
          {showAIBadge && (
            <AIBadge>
              {aiBadgeText}
            </AIBadge>
          )}
        </div>

        {/* Right section: Actions */}
        {actions && (
          <Inline gap="small" align="center">
            {actions}
          </Inline>
        )}
      </Inline>
    </header>
  );
};

PageHeader.displayName = 'PageHeader';

AIBadge:4.content:
/**
 * AIBadge Styles
 * AI denotation badge - matches Figma node-id=19467-181071
 * Uses Callout component for the info popup
 */

@import '../../1-tokens/tokens.css';

/* Container for relative positioning */
.container {
  position: relative;
  display: inline-flex;
}

/* Base badge styles */
.badge {
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-25);
  height: 24px;
  width: fit-content;
  padding: 2px 12px 2px 6px;
  background: var(--ink-ai-badge-bg);
  border-radius: var(--ink-radius-full);
  font-family: var(--ink-font-family-default);
  white-space: nowrap;
  border: 1px solid transparent;
  transition: all 150ms ease;
}

/* Hover state */
.badge:hover {
  background: var(--ink-ai-badge-bg-hover);
}

/* Interactive variant (has callout) */
.badge.interactive {
  cursor: pointer;
}

.badge.interactive:hover {
  background: var(--ink-ai-badge-bg-hover-interactive);
}

.badge.interactive:focus-visible {
  outline: 2px solid var(--ink-focus-ring-color);
  outline-offset: 2px;
}

/* Active/open state */
.badge.active {
  background: var(--ink-ai-badge-bg-active);
}

.icon {
  flex-shrink: 0;
  width: 16px;
  height: 16px;
}

.text {
  font-size: var(--ink-font-size-xs);
  font-weight: var(--ink-font-weight-semibold);
  line-height: 24px;
  color: var(--ink-font-color-default);
}

/* Callout wrapper for positioning */
.calloutWrapper {
  position: absolute;
  top: calc(100% + 10px);
  left: 0;
  z-index: var(--ink-z-popover, 1000);
  animation: calloutFadeIn 150ms ease;
}

/* Hide Callout's built-in caret completely */
.calloutWrapper [class*='caret'],
.calloutWrapper [class*='caret']::before,
.calloutWrapper [class*='caret']::after {
  display: none !important;
  visibility: hidden !important;
  opacity: 0 !important;
  border-color: transparent !important;
  background: transparent !important;
}

/* Also target by structure - hide first child if it's the caret */
.calloutWrapper > div > div:first-child:not([class*='callout']) {
  display: none !important;
}

/* Custom caret at top-left */
.calloutWrapper::before {
  content: '';
  position: absolute;
  top: -8px;
  left: 16px;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 0 8px 8px 8px;
  border-color: transparent transparent var(--ink-form-control-border-color-subtle) transparent;
  z-index: 1;
}

.calloutWrapper::after {
  content: '';
  position: absolute;
  top: -7px;
  left: 16px;
  width: 0;
  height: 0;
  border-style: solid;
  border-width: 0 8px 8px 8px;
  border-color: transparent transparent var(--ink-bg-color-default) transparent;
  z-index: 2;
}

@keyframes calloutFadeIn {
  from {
    opacity: 0;
    transform: translateY(-4px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/**
 * AIBadge Component
 *
 * AI denotation badge with Iris icon, used to indicate AI-assisted features.
 * Supports hover state and optional info callout on click.
 * Based on Figma design: node-id=19467-181071
 *
 * @layer 4-composites
 */

import React, { useState, useRef, useEffect, useCallback } from 'react';
import { Callout } from '../Callout';
import { Link } from '../../3-primitives';
import styles from './AIBadge.module.css';

// Iris Icon SVG Component - exact paths from Figma
// Exported for use in other components (e.g., Banner promo variant)
export const IrisIcon: React.FC<{ className?: string }> = ({ className }) => (
  <svg
    className={className}
    width="16"
    height="16"
    viewBox="0 0 14 14"
    fill="none"
    xmlns="http://www.w3.org/2000/svg"
  >
    {/* Corner petals - light purple */}
    <path
      d="M5.39143 3.21709C4.66395 2.31986 3.57272 1.87528 1.73785 1.50346C1.72168 1.50346 1.7136 1.50346 1.69743 1.50346C1.64085 1.50346 1.58427 1.52771 1.54385 1.57621C1.50344 1.62471 1.48727 1.68129 1.50344 1.73787C1.87526 3.57274 2.32792 4.67205 3.21706 5.39145C4.13046 4.80946 4.81753 4.1224 5.39951 3.209L5.39143 3.21709Z"
      fill="#CBC2FF"
    />
    <path
      d="M10.7906 5.39146C11.6878 4.66398 12.1324 3.57275 12.5042 1.73788C12.5204 1.6813 12.5042 1.61663 12.4638 1.56813C12.4234 1.51963 12.3668 1.49538 12.3102 1.49538C12.2941 1.49538 12.286 1.49538 12.2698 1.49538C10.435 1.86721 9.33564 2.31986 8.60816 3.20901C9.19015 4.12241 9.87721 4.80947 10.7906 5.39146Z"
      fill="#CBC2FF"
    />
    <path
      d="M3.21706 8.60854C2.31984 9.33602 1.87526 10.4272 1.50344 12.2621C1.48727 12.3187 1.50344 12.3834 1.54385 12.4319C1.59235 12.4884 1.6651 12.5208 1.73785 12.5046C3.57272 12.1328 4.67203 11.6801 5.39143 10.791C4.80944 9.87759 4.12238 9.19053 3.20898 8.60854H3.21706Z"
      fill="#CBC2FF"
    />
    <path
      d="M8.60854 10.791C9.33602 11.6882 10.4272 12.1328 12.2621 12.5046C12.3349 12.5208 12.4076 12.4884 12.4561 12.4319C12.4965 12.3834 12.5127 12.3268 12.4965 12.2621C12.1247 10.4272 11.6721 9.32794 10.7829 8.60854C9.86951 9.19053 9.18245 9.87759 8.60046 10.791H8.60854Z"
      fill="#CBC2FF"
    />
    {/* Center star with gradient */}
    <path
      d="M13.6197 6.45843C12.4638 6.03003 11.607 5.63395 10.9199 5.18938C10.0308 4.62356 9.38414 3.97691 8.81832 3.08776C8.38183 2.4007 7.97768 1.5358 7.54927 0.387994C7.46844 0.153583 7.25019 0.00808674 7.0077 0.00808674C6.76521 0.00808674 6.54696 0.153583 6.46613 0.387994C6.03772 1.54388 5.64165 2.4007 5.19708 3.08776C4.63126 3.97691 3.97652 4.62356 3.08738 5.18938C2.40031 5.62587 1.53541 6.03003 0.387608 6.45843C0.153197 6.53926 0.00770082 6.75751 0.00770082 7C0.00770082 7.2425 0.153197 7.46074 0.387608 7.54157C1.5435 7.9619 2.40031 8.36605 3.08738 8.81063C3.97652 9.37645 4.62317 10.0231 5.19708 10.9122C5.63357 11.6074 6.03772 12.4642 6.46613 13.612C6.55504 13.8464 6.76521 13.9919 7.0077 13.9919C7.25019 13.9919 7.46844 13.8383 7.54927 13.612C7.97768 12.4561 8.37375 11.5993 8.81832 10.9122C9.38414 10.0231 10.0308 9.37645 10.9199 8.81063C11.6151 8.37414 12.4719 7.96998 13.6197 7.54157C13.8541 7.45266 13.9996 7.2425 13.9996 7C13.9996 6.75751 13.846 6.53926 13.6197 6.45843ZM10.1116 7.08892C8.31717 7.72749 7.7271 8.31756 7.08853 10.112C7.0562 10.1928 6.93495 10.1928 6.9107 10.112C6.27213 8.31756 5.68207 7.72749 3.88761 7.08892C3.80678 7.05659 3.80678 6.93534 3.88761 6.91109C5.68207 6.27252 6.27213 5.68245 6.9107 3.88799C6.94304 3.79908 7.06428 3.79908 7.08853 3.88799C7.7271 5.68245 8.31717 6.27252 10.1116 6.91109C10.1925 6.94342 10.1925 7.06467 10.1116 7.08892Z"
      fill="url(#irisGradient)"
    />
    <defs>
      <linearGradient
        id="irisGradient"
        x1="0.0306558"
        y1="7"
        x2="14.028"
        y2="7"
        gradientUnits="userSpaceOnUse"
      >
        <stop stopColor="#D9155D" />
        <stop offset="0.501049" stopColor="#A02AAC" />
        <stop offset="1" stopColor="#4C06FF" />
      </linearGradient>
    </defs>
  </svg>
);

// Default callout content
const DefaultInfoContent: React.FC = () => (
  <>
    Docusign Iris provides this service. Learn more about how we use{' '}
    <Link href="https://www.docusign.com/trust/privacy/artificial-intelligence" target="_blank">
      AI at Docusign
    </Link>
    .
  </>
);

export interface AIBadgeProps extends React.HTMLAttributes<HTMLSpanElement> {
  /** Badge text - defaults to "AI-Assisted" */
  children?: React.ReactNode;
  /** Info content to show in callout when clicked. Set to false to disable callout. */
  infoContent?: React.ReactNode | false;
  /** Title for the info callout */
  infoTitle?: string;
  /** Position of the callout */
  calloutPosition?: 'top' | 'bottom';
  /** Whether the callout is open (controlled mode) */
  open?: boolean;
  /** Callback when callout open state changes */
  onOpenChange?: (open: boolean) => void;
}

export const AIBadge: React.FC<AIBadgeProps> = ({
  children = 'AI-Assisted',
  className,
  infoContent,
  infoTitle = 'AI-assisted',
  calloutPosition = 'bottom',
  open: controlledOpen,
  onOpenChange,
  onClick,
  ...props
}) => {
  const [internalOpen, setInternalOpen] = useState(false);
  const isControlled = controlledOpen !== undefined;
  const isOpen = isControlled ? controlledOpen : internalOpen;

  const badgeRef = useRef<HTMLSpanElement>(null);
  const calloutRef = useRef<HTMLDivElement>(null);

  // Default to showing callout unless explicitly disabled with false
  const hasCallout = infoContent !== false;
  const calloutContent =
    infoContent === undefined || infoContent === true ? <DefaultInfoContent /> : infoContent;

  const setOpen = useCallback(
    (newOpen: boolean) => {
      if (!isControlled) {
        setInternalOpen(newOpen);
      }
      onOpenChange?.(newOpen);
    },
    [isControlled, onOpenChange]
  );

  const handleClick = useCallback(
    (e: React.MouseEvent<HTMLSpanElement>) => {
      if (hasCallout) {
        e.stopPropagation();
        setOpen(!isOpen);
      }
      onClick?.(e);
    },
    [hasCallout, isOpen, setOpen, onClick]
  );

  const handleKeyDown = useCallback(
    (e: React.KeyboardEvent<HTMLSpanElement>) => {
      if (hasCallout && (e.key === 'Enter' || e.key === ' ')) {
        e.preventDefault();
        setOpen(!isOpen);
      }
      if (e.key === 'Escape' && isOpen) {
        setOpen(false);
      }
    },
    [hasCallout, isOpen, setOpen]
  );

  // Close on click outside
  useEffect(() => {
    if (!isOpen || !hasCallout) return;

    const handleClickOutside = (e: MouseEvent) => {
      if (
        badgeRef.current &&
        calloutRef.current &&
        !badgeRef.current.contains(e.target as Node) &&
        !calloutRef.current.contains(e.target as Node)
      ) {
        setOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen, hasCallout, setOpen]);

  // Close on escape
  useEffect(() => {
    if (!isOpen || !hasCallout) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        setOpen(false);
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen, hasCallout, setOpen]);

  const badgeClasses = [
    styles.badge,
    hasCallout && styles.interactive,
    isOpen && styles.active,
    className,
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <span className={styles.container}>
      <span
        ref={badgeRef}
        className={badgeClasses}
        onClick={handleClick}
        onKeyDown={handleKeyDown}
        role={hasCallout ? 'button' : undefined}
        tabIndex={hasCallout ? 0 : undefined}
        aria-expanded={hasCallout ? isOpen : undefined}
        aria-haspopup={hasCallout ? 'dialog' : undefined}
        {...props}
      >
        <IrisIcon className={styles.icon} />
        <span className={styles.text}>{children}</span>
      </span>

      {hasCallout && isOpen && (
        <div ref={calloutRef} className={styles.calloutWrapper}>
          <Callout heading={infoTitle} width="medium" onClose={() => setOpen(false)}>
            {calloutContent}
          </Callout>
        </div>
      )}
    </span>
  );
};

AIBadge.displayName = 'AIBadge';

Accordion:4.content:
/* Ink Design System - Accordion Component Styles */
@import '../../1-tokens/tokens.css';

.accordion {
  font-family: var(--ink-font-family);
}

/* Bordered variant: items have border-top separators */
.accordion.bordered .item {
  border-top: 1px solid var(--ink-border-color-subtle);
}

/* Card variant (not bordered): items have gap, radius, and selected bg */
.accordion:not(.bordered) {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1); /* 4px gap between items */
}

.accordion:not(.bordered) .item {
  border-radius: var(--ink-radius-size-s); /* 8px */
}

.accordion:not(.bordered) .itemOpen .header {
  background-color: var(--ink-item-bg-color-selected-subtle);
  border-radius: var(--ink-radius-size-s);
}

/* Item states */
.item.itemDisabled {
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
}

/* Header - base styles */
.header {
  width: 100%;
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-4); /* 16px between elements */
  padding: var(--ink-spacing-3) var(--ink-spacing-4); /* 12px vertical (default), 16px horizontal */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 16px */
  font-weight: var(--ink-font-weight-medium);
  line-height: 1.25;
  letter-spacing: -0.16px;
  color: var(--ink-font-color-default);
  background-color: transparent;
  border: none;
  text-align: left;
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
}

.header:hover:not(:disabled) {
  background-color: var(--ink-item-bg-color-hover);
}

.header:disabled {
  cursor: not-allowed;
}

/* Header height variants */
.accordion.compact .header {
  padding-top: var(--ink-spacing-2); /* 8px */
  padding-bottom: var(--ink-spacing-2);
}

.accordion.tall .header {
  padding-top: var(--ink-spacing-5); /* 20px */
  padding-bottom: var(--ink-spacing-5);
}

/* Start icon */
.startIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  color: var(--ink-icon-color-secondary);
}

/* Title wrapper - contains title and subtitle */
.titleWrapper {
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  min-width: 0; /* Allow text truncation */
}

/* Title row - for inline elements with title */
.titleRow {
  display: flex;
  align-items: center;
  gap: 0;
}

.title {
  line-height: 1.25;
  letter-spacing: -0.16px;
}

/* Display level variants for title */
.accordion.displayLevelXs .title {
  font-size: var(--ink-font-size-sm); /* 14px */
}

.accordion.displayLevelS .title {
  font-size: var(--ink-font-size-lg); /* 18px */
}

/* Subtitle */
.subtitle {
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium);
  line-height: 1.5;
  letter-spacing: 0.16px;
  color: var(--ink-font-color-subtle);
  width: 100%;
}

/* Metadata */
.metadata {
  flex-shrink: 0;
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium);
  line-height: 1.5;
  letter-spacing: 0.16px;
  color: var(--ink-font-color-subtle);
  text-align: right;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Chevron */
.chevron {
  flex-shrink: 0;
  width: 20px;
  height: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--ink-icon-color-secondary);
  transition: transform var(--ink-transition-normal);
}

.chevronOpen {
  transform: rotate(180deg);
}

/* Panel - collapsible container */
.panel {
  max-height: 0;
  overflow: hidden;
  transition: max-height var(--ink-transition-normal) ease-out;
}

.panelOpen {
  max-height: 2000px; /* Large enough for most content */
  transition: max-height var(--ink-transition-normal) ease-in;
}

/* Content - body padding */
.content {
  min-height: 0;
  padding: var(--ink-spacing-2) var(--ink-spacing-4) var(--ink-spacing-6); /* 8px top, 16px horizontal, 24px bottom (default) */
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-regular);
  color: var(--ink-font-color-default);
  line-height: 1.4;
}

/* Content height variants */
.accordion.compact .content {
  padding-bottom: var(--ink-spacing-4); /* 16px */
}

.accordion.tall .content {
  padding-bottom: var(--ink-spacing-8); /* 32px */
}

/* Card variant content padding */
.accordion:not(.bordered) .content {
  padding-top: var(--ink-spacing-3); /* 12px */
  padding-bottom: var(--ink-spacing-3); /* 12px */
}

/* No padding variant */
.content.noPadding {
  padding: 0;
}

/* Focus styles */
.header:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: -2px;
  z-index: 1;
}

/* Nested content spacing */
.content > *:first-child {
  margin-top: 0;
}

.content > *:last-child {
  margin-bottom: 0;
}

.content p {
  margin: var(--ink-spacing-2) 0;
}

.content ul,
.content ol {
  margin: var(--ink-spacing-2) 0;
  padding-left: var(--ink-spacing-5);
}

.content li {
  margin: var(--ink-spacing-1) 0;
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './Accordion.module.css';
import { Icon, IconName } from '../../3-primitives/Icon';

export interface AccordionItemData {
  /** Unique identifier */
  id: string;
  /** Title/header text */
  title: string;
  /** Content to display when expanded */
  content: React.ReactNode;
  /** Icon name to display before title */
  startIcon?: IconName;
  /** Subtitle text below title */
  subtitle?: string;
  /** Metadata text displayed on the right */
  metadata?: string;
  /** Disabled state */
  disabled?: boolean;
  /** Remove padding from content area */
  noPadding?: boolean;
}

export interface AccordionProps {
  /** Array of accordion items */
  items: AccordionItemData[];
  /** Allow multiple panels open at once */
  allowMultiple?: boolean;
  /** Default open items (controlled) */
  defaultOpenItems?: string[];
  /** Open items (controlled) */
  openItems?: string[];
  /** Callback when items change */
  onOpenItemsChange?: (openItems: string[]) => void;
  /** Additional className */
  className?: string;
  /** Show bordered variant (border-top separators) vs card variant (gap, radius, selected bg) */
  bordered?: boolean;
  /** Header height: compact (8px), default (12px), tall (20px) vertical padding */
  itemHeight?: 'compact' | 'default' | 'tall';
  /** Title display level for font size: xs (14px), s (18px), default (16px) */
  displayLevel?: 'xs' | 's';
}

const Accordion: React.FC<AccordionProps> = ({
  items,
  allowMultiple = false,
  defaultOpenItems = [],
  openItems: controlledOpenItems,
  onOpenItemsChange,
  className,
  bordered = true,
  itemHeight = 'default',
  displayLevel,
}) => {
  const [internalOpenItems, setInternalOpenItems] = React.useState<string[]>(defaultOpenItems);
  const isControlled = controlledOpenItems !== undefined;
  const openItems = isControlled ? controlledOpenItems : internalOpenItems;

  const toggleItem = (itemId: string) => {
    let newOpenItems: string[];

    if (openItems.includes(itemId)) {
      // Close the item
      newOpenItems = openItems.filter((id) => id !== itemId);
    } else {
      // Open the item
      if (allowMultiple) {
        newOpenItems = [...openItems, itemId];
      } else {
        newOpenItems = [itemId];
      }
    }

    if (!isControlled) {
      setInternalOpenItems(newOpenItems);
    }
    onOpenItemsChange?.(newOpenItems);
  };

  // Build accordion container classes
  const accordionClasses = cn(
    styles.accordion,
    bordered && styles.bordered,
    itemHeight === 'compact' && styles.compact,
    itemHeight === 'tall' && styles.tall,
    displayLevel === 'xs' && styles.displayLevelXs,
    displayLevel === 's' && styles.displayLevelS,
    className
  );

  return (
    <div className={accordionClasses}>
      {items.map((item) => {
        const isOpen = openItems.includes(item.id);

        return (
          <div
            key={item.id}
            className={cn(
              styles.item,
              isOpen && styles.itemOpen,
              item.disabled && styles.itemDisabled
            )}
          >
            <button
              type="button"
              className={styles.header}
              onClick={() => !item.disabled && toggleItem(item.id)}
              disabled={item.disabled}
              aria-expanded={isOpen}
              aria-controls={`accordion-content-${item.id}`}
              id={`accordion-header-${item.id}`}
            >
              {item.startIcon && (
                <span className={styles.startIcon}>
                  <Icon name={item.startIcon} size="medium" />
                </span>
              )}
              <div className={styles.titleWrapper}>
                <div className={styles.titleRow}>
                  <span className={styles.title}>{item.title}</span>
                </div>
                {item.subtitle && <span className={styles.subtitle}>{item.subtitle}</span>}
              </div>
              {item.metadata && <span className={styles.metadata}>{item.metadata}</span>}
              <span className={cn(styles.chevron, isOpen && styles.chevronOpen)}>
                <Icon name="chevron-down" size="small" />
              </span>
            </button>

            <div
              id={`accordion-content-${item.id}`}
              className={cn(styles.panel, isOpen && styles.panelOpen)}
              aria-labelledby={`accordion-header-${item.id}`}
              role="region"
            >
              <div className={cn(styles.content, item.noPadding && styles.noPadding)}>
                {item.content}
              </div>
            </div>
          </div>
        );
      })}
    </div>
  );
};

Accordion.displayName = 'InkAccordion';

export { Accordion };

