/* Ink Design System - Banner - Exact Figma Specs */

/* Banner Container - Figma Specs */
.banner {
  display: flex;
  align-items: center;
  justify-content: center; /* Figma: items centered */
  gap: var(--ink-spacing-100); /* 8px */
  padding: var(--ink-spacing-100) var(--ink-spacing-100) var(--ink-spacing-100) 48px; /* 8px 8px 8px 48px - Figma spec */
