import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './Breadcrumb.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export interface BreadcrumbItem {
  /** Label for the breadcrumb */
  label: string;
  /** URL to navigate to (optional - items without href are text-only) */
  href?: string;
  /** Is this the current page (styled with medium weight, no hover/active) */
  current?: boolean;
}

export interface BreadcrumbProps {
  /** Array of breadcrumb items */
  items: BreadcrumbItem[];
  /** Show home icon as first item instead of text */
  rootIcon?: boolean;
  /** Show or hide the current page item */
  showCurrentPage?: boolean;
  /** Enable overflow menu - collapses to: Root > ... > Current (max 3 items) */
  overflowMenu?: boolean;
  /** Additional className */
  className?: string;
  /** Callback when item is clicked */
  onItemClick?: (item: BreadcrumbItem, index: number) => void;
  /** Callback when overflow menu is clicked */
  onOverflowClick?: () => void;
}

const Breadcrumb: React.FC<BreadcrumbProps> = ({
  items,
  rootIcon = false,
  showCurrentPage = true,
  overflowMenu = false,
  className,
  onItemClick,
  onOverflowClick,
}) => {
  // Separator is always chevron-right per Figma spec
  const renderSeparator = (key: string | number) => (
    <span className={styles.separator} key={`sep-${key}`} aria-hidden="true">
      <Icon name="chevron-right" size="small" />
    </span>
  );

  const renderItems = () => {
    // Guard against undefined or empty items
    if (!items || items.length === 0) {
      return null;
    }

    // Filter out current page if showCurrentPage is false
    let displayItems = showCurrentPage
      ? [...items]
      : items.filter((item, idx) => idx !== items.length - 1 && !item.current);

    // Overflow menu mode: Root > Overflow Icon > Current Page (max 3 crumbs per Figma)
    if (overflowMenu && displayItems.length > 2) {
      const firstItem = displayItems[0];
      const lastItem = displayItems[displayItems.length - 1];

      return (
        <>
          <React.Fragment key="first-item">
            {renderItem(firstItem, 0, false)}
            {renderSeparator(0)}
          </React.Fragment>

          <React.Fragment key="overflow">
            <li className={styles.item}>
              <IconButton
                icon="overflow-horizontal"
                size="small"
                kind="tertiary"
                aria-label="Show more breadcrumbs"
                onClick={onOverflowClick}
                className={styles.overflowButton}
              />
            </li>
            {renderSeparator('overflow')}
          </React.Fragment>

          <React.Fragment key="current">
            {renderItem(lastItem, displayItems.length - 1, true)}
          </React.Fragment>
        </>
      );
    }

    return displayItems.map((item, index) => {
      const isLast = index === displayItems.length - 1;
      const isCurrent = item.current || isLast;

      return (
        <React.Fragment key={`${item.label}-${index}`}>
          {renderItem(item, index, isCurrent)}
          {!isLast && renderSeparator(index)}
        </React.Fragment>
      );
    });
  };

  const renderItem = (item: BreadcrumbItem, index: number, isCurrent: boolean) => {
    const isFirst = index === 0;
    const showAsRootIcon = rootIcon && isFirst;

    const handleClick = (e: React.MouseEvent) => {
      if (onItemClick && !isCurrent) {
        e.preventDefault();
        onItemClick(item, index);
      }
    };

    // Root icon for first item (home/folder icon per Figma)
    if (showAsRootIcon) {
      return (
        <li className={styles.item}>
          <IconButton
            icon="folder"
            size="small"
            kind="tertiary"
            aria-label={item.label}
            onClick={handleClick}
            className={styles.rootIconButton}
            href={item.href}
          />
        </li>
      );
    }

    // Regular item - current page has medium weight, no hover/active states
    return (
      <li className={styles.item}>
        {item.href && !isCurrent ? (
          <a href={item.href} className={styles.link} onClick={handleClick}>
            <span className={styles.itemText}>{item.label}</span>
          </a>
        ) : (
          <span
            className={cn(styles.text, isCurrent && styles.current)}
            aria-current={isCurrent ? 'page' : undefined}
          >
            <span className={styles.itemText}>{item.label}</span>
          </span>
        )}
      </li>
    );
  };

  return (
    <nav aria-label="Breadcrumb" className={cn(styles.breadcrumb, className)}>
      <ol className={styles.list}>{renderItems()}</ol>
    </nav>
  );
};

Breadcrumb.displayName = 'InkBreadcrumb';

export { Breadcrumb };

Callout:4.content:
/* Ink Design System - Callout - Exact Figma Specs */

/* Container */
.container {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: flex-end;
  position: relative;
}

/* Width Variants */
.width-small {
  width: 320px;
}

.width-medium {
  width: 384px;
}

.width-large {
  width: 480px;
}

.width-xlarge {
  width: 640px;
}

/* Callout Card */
.callout {
  width: 100%;
  background: var(--ink-bg-color-default);
  border: var(--ink-form-border-width) solid var(--ink-form-border-subtle);
  border-radius: var(--ink-radius-size-s); /* 8px */
  box-shadow: var(--ink-elevation-medium);
  overflow: hidden;
}

/* Glass Effects */
.glass-frost {
  backdrop-filter: blur(20px);
  background: var(--ink-purple-130);
  border-color: var(--ink-purple-110);
}

.glass-frost .heading,
.glass-frost .text {
  color: var(--ink-white-100);
}

.glass-tint {
  backdrop-filter: blur(12px);
  background: var(--ink-purple-120);
  border-color: var(--ink-purple-100);
}

.glass-tint .heading,
.glass-tint .text {
  color: var(--ink-white-100);
}

/* Glass Mode - Close Button Icon (white) */
.closeButtonGlass {
  color: var(--ink-white-100);
}

.closeButtonGlass:hover {
  background: var(--ink-white-fade-10);
}

/* Glass Mode - Footer */
.footerGlass {
  border-top-color: var(--ink-white-fade-20);
}

/* Glass Mode - Secondary Button */
.buttonGlassSecondary {
  background: transparent;
  border-color: var(--ink-white-fade-50);
  color: var(--ink-white-100);
}

.buttonGlassSecondary:hover {
  background: var(--ink-white-fade-10);
  border-color: var(--ink-white-fade-70);
}

/* Glass Mode - Primary Button */
.buttonGlassPrimary {
  background: var(--ink-neutral-20);
  border-color: var(--ink-neutral-20);
  color: var(--ink-font-color-default);
}

.buttonGlassPrimary:hover {
  background: var(--ink-neutral-30);
  border-color: var(--ink-neutral-30);
}

/* Content Wrapper */
.content {
  display: flex;
  align-items: flex-start;
  position: relative;
  width: 100%;
  border-radius: inherit;
  overflow: hidden;
}

/* Body Section */
.body {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-100); /* 8px */
  padding: var(--ink-spacing-250); /* 20px */
  flex: 1;
  min-width: 0;
}

/* Header */
.header {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-100); /* 8px */
  padding-right: var(--ink-spacing-400); /* 32px - space for close button */
}

.heading {
  flex: 1;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xl); /* 20px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  line-height: 1.25;
  letter-spacing: -0.16px;
  color: var(--ink-font-color-default);
  margin: 0;
  white-space: pre-wrap;
}

/* Body Text */
.text {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4;
  color: var(--ink-font-color-default);
  white-space: pre-wrap;
}

/* Footer */
.footer {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: var(--ink-spacing-200); /* 16px */
  padding-top: var(--ink-spacing-200); /* 16px */
}

/* Close Button Wrapper - positions IconButton in top-right */
.closeButtonWrapper {
  position: absolute;
  top: var(--ink-spacing-100); /* 8px */
  right: var(--ink-spacing-100); /* 8px */
}

/* Image */
.image {
  flex-shrink: 0;
}

/* Arrow Alignment - controls arrow position along edge */
/* Default: center alignment */
.caret,
.caretBefore,
.caretAfter {
  align-self: center;
}

/* Start alignment */
.align-start .caret {
  align-self: flex-start;
  margin-left: var(--ink-spacing-300); /* 24px offset from edge */
}

.align-start .caretBefore,
.align-start .caretAfter {
  align-self: flex-start;
  margin-top: var(--ink-spacing-300);
}

/* End alignment */
.align-end .caret {
  align-self: flex-end;
  margin-right: var(--ink-spacing-300);
}

.align-end .caretBefore,
.align-end .caretAfter {
  align-self: flex-end;
  margin-bottom: var(--ink-spacing-300);
}

/* Caret/Arrow */
.caret,
.caretBefore,
.caretAfter {
  width: 16px;
  height: 10px;
  position: relative;
  align-self: center;
}

.caret::before,
.caret::after,
.caretBefore::before,
.caretBefore::after,
.caretAfter::before,
.caretAfter::after {
  content: '';
  position: absolute;
  width: 0;
  height: 0;
  border-style: solid;
}

/* Caret for above/below */
/* location="above" = callout is ABOVE trigger, arrow at BOTTOM pointing DOWN */
.location-above .caret {
  order: 2; /* Move caret to bottom */
}

.location-above .caret::before {
  left: 50%;
  transform: translateX(-50%);
  border-width: 10px 8px 0 8px;
  border-color: var(--ink-form-border-subtle) transparent transparent transparent;
}

.location-above .caret::after {
  left: 50%;
  transform: translateX(-50%);
  top: -1px;
  border-width: 9px 7.5px 0 7.5px;
  border-color: var(--ink-bg-color-default) transparent transparent transparent;
}

/* location="below" = callout is BELOW trigger, arrow at TOP pointing UP */
.location-below .caret {
  order: -1; /* Move caret to top */
}

.location-below .caret::before {
  left: 50%;
  bottom: 0;
  transform: translateX(-50%);
  border-width: 0 8px 10px 8px;
  border-color: transparent transparent var(--ink-form-border-subtle) transparent;
}

.location-below .caret::after {
  left: 50%;
  bottom: -1px;
  transform: translateX(-50%);
  border-width: 0 7.5px 9px 7.5px;
  border-color: transparent transparent var(--ink-bg-color-default) transparent;
}

/* Caret for before/after - horizontal */
.location-before .container {
  flex-direction: row;
}

.location-after .container {
  flex-direction: row;
}

.caretBefore,
.caretAfter {
  width: 10px;
  height: 16px;
  align-self: center;
}

.location-before .caretBefore {
  order: 2;
}

.caretBefore::before {
  top: 50%;
  transform: translateY(-50%);
  border-width: 8px 10px 8px 0;
  border-color: transparent var(--ink-form-border-subtle) transparent transparent;
}

.caretBefore::after {
  top: 50%;
  left: 1px;
  transform: translateY(-50%);
  border-width: 7.5px 9px 7.5px 0;
  border-color: transparent var(--ink-bg-color-default) transparent transparent;
}

.caretAfter::before {
  top: 50%;
  transform: translateY(-50%);
  border-width: 8px 0 8px 10px;
  border-color: transparent transparent transparent var(--ink-form-border-subtle);
}

.caretAfter::after {
  top: 50%;
  right: 1px;
  transform: translateY(-50%);
  border-width: 7.5px 0 7.5px 9px;
  border-color: transparent transparent transparent var(--ink-bg-color-default);
}

/* Glass Container Variants - for caret colors */
.container-glassfrost .caret::before,
.container-glassfrost .caretBefore::before,
.container-glassfrost .caretAfter::before {
  border-color: var(--ink-purple-110) transparent transparent transparent;
}

.container-glassfrost .caret::after {
  border-color: var(--ink-purple-130) transparent transparent transparent;
}

.container-glassfrost .caretBefore::after {
  border-color: transparent var(--ink-purple-130) transparent transparent;
}

.container-glassfrost .caretAfter::after {
  border-color: transparent transparent transparent var(--ink-purple-130);
}

/* Below location needs different border positions */
.container-glassfrost.location-below .caret::before {
  border-color: transparent transparent var(--ink-purple-110) transparent;
}

.container-glassfrost.location-below .caret::after {
  border-color: transparent transparent var(--ink-purple-130) transparent;
}

.container-glasstint .caret::before,
.container-glasstint .caretBefore::before,
.container-glasstint .caretAfter::before {
  border-color: var(--ink-purple-100) transparent transparent transparent;
}

.container-glasstint .caret::after {
  border-color: var(--ink-purple-120) transparent transparent transparent;
}

.container-glasstint .caretBefore::after {
  border-color: transparent var(--ink-purple-120) transparent transparent;
}

.container-glasstint .caretAfter::after {
  border-color: transparent transparent transparent var(--ink-purple-120);
}

.container-glasstint.location-below .caret::before {
  border-color: transparent transparent var(--ink-purple-100) transparent;
}

.container-glasstint.location-below .caret::after {
  border-color: transparent transparent var(--ink-purple-120) transparent;
}

import React from 'react';
import styles from './Callout.module.css';
import { Button, IconButton } from '../../3-primitives';

export type CalloutWidth = 'small' | 'medium' | 'large' | 'xlarge';
export type CalloutAlignment = 'start' | 'center' | 'end';
export type CalloutLocation = 'above' | 'below' | 'before' | 'after';
export type CalloutImagePosition = 'start' | 'top';
export type CalloutGlass = 'None' | 'glassFrost' | 'glassTint';

export interface CalloutAction {
  label: string;
  onClick: () => void;
}

export interface CalloutProps {
  /** Heading text */
  heading: string;
  /** Body content */
  children: React.ReactNode;
  /** Width variant: xlarge (640px), large (480px), medium (384px), small (320px) */
  width?: CalloutWidth;
  /** Arrow alignment position along edge */
  alignment?: CalloutAlignment;
  /** Caret/arrow location relative to trigger */
  location?: CalloutLocation;
  /** Image position (when image is provided) */
  imagePosition?: CalloutImagePosition;
  /** Glass effect: None, glassFrost, glassTint */
  glass?: CalloutGlass;
  /** Show action buttons footer (Figma: actions) */
  actions?: boolean;
  /** Primary action button */
  primaryAction?: CalloutAction;
  /** Secondary action button */
  secondaryAction?: CalloutAction;
  /** Show close button (Figma: closeButton) */
  closeButton?: boolean;
  /** Close handler */
  onClose?: () => void;
  /** Show arrow/caret pointing to trigger (Figma: enableArrow) */
  enableArrow?: boolean;
  /** Optional image */
  image?: React.ReactNode;
  /** Additional className */
  className?: string;
}

export const Callout = React.forwardRef<HTMLDivElement, CalloutProps>(
  (
    {
      heading,
      children,
      width = 'large',
      alignment = 'start',
      location = 'above',
      imagePosition = 'start',
      glass = 'None',
      actions = false,
      primaryAction,
      secondaryAction,
      closeButton = true,
      onClose,
      enableArrow = true,
      image,
      className,
    },
    ref
  ) => {
    // Check if we're in glass mode
    const isGlass = glass !== 'None';
    // Map glass values to CSS class names
    const glassClassMap: Record<CalloutGlass, string> = {
      None: '',
      glassFrost: styles['glass-frost'],
      glassTint: styles['glass-tint'],
    };

    const containerClasses = [
      styles.container,
      styles[`width-${width}`],
      styles[`location-${location}`],
      styles[`align-${alignment}`],
      glass !== 'None' && styles[`container-${glass.toLowerCase()}`],
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const calloutClasses = [styles.callout, glassClassMap[glass]].filter(Boolean).join(' ');

    return (
      <div ref={ref} className={containerClasses}>
        {enableArrow && location === 'above' && <div className={styles.caret} />}
        {enableArrow && location === 'before' && <div className={styles.caretBefore} />}

        <div className={calloutClasses}>
          <div className={styles.content}>
            {image && <div className={styles.image}>{image}</div>}

            <div className={styles.body}>
              <div className={styles.header}>
                <h3 className={styles.heading}>{heading}</h3>
              </div>

              <div className={styles.text}>{children}</div>

              {actions && (primaryAction || secondaryAction) && (
                <div className={`${styles.footer} ${isGlass ? styles.footerGlass : ''}`}>
                  {secondaryAction && (
                    <Button
                      kind={isGlass ? 'secondary' : 'tertiary'}
                      size="medium"
                      onClick={secondaryAction.onClick}
                      className={isGlass ? styles.buttonGlassSecondary : undefined}
                    >
                      {secondaryAction.label}
                    </Button>
                  )}
                  {primaryAction && (
                    <Button
                      kind={isGlass ? 'secondary' : 'primary'}
                      size="medium"
                      onClick={primaryAction.onClick}
                      className={isGlass ? styles.buttonGlassPrimary : undefined}
                    >
                      {primaryAction.label}
                    </Button>
                  )}
                </div>
              )}
            </div>

            {closeButton && (
              <IconButton
                icon="close"
                size="medium"
                kind="tertiary"
                onClick={onClose}
                aria-label="Close callout"
                className={`${styles.closeButtonWrapper} ${isGlass ? styles.closeButtonGlass : ''}`}
              />
            )}
          </div>
        </div>

        {enableArrow && location === 'below' && <div className={styles.caret} />}
        {enableArrow && location === 'after' && <div className={styles.caretAfter} />}
      </div>
    );
  }
);

Callout.displayName = 'Callout';

Chip:4.content:
/* Ink Design System - Chip Component */
@import '../../1-tokens/tokens.css';

.chip {
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-50); /* 4px - gap50 from Figma */
  height: var(--ink-spacing-400); /* 32px */
  padding: 0 var(--ink-spacing-100); /* 0 8px - gap100 from Figma */
  background-color: var(
    --ink-neutral-fade-5
  ); /* rgba(19,0,50,0.05) - Figma ctaBgColorTertiaryDefault */
  border-radius: var(--ink-radius-size-xs); /* 4px - radiusSizeXS from Figma */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4;
  color: var(--ink-font-color-default);
  border: none;
  transition: background-color var(--ink-transition-fast);
  user-select: none;
  white-space: nowrap;
}

/* Clickable state */
.chip.clickable {
  cursor: pointer;
}

.chip.clickable:hover {
  background-color: var(
    --ink-neutral-fade-10
  ); /* rgba(19,0,50,0.1) - Figma ctaBgColorTertiaryHover */
}

.chip.clickable:active {
  background-color: var(
    --ink-neutral-fade-15
  ); /* rgba(19,0,50,0.15) - Figma ctaBgColorTertiaryActive */
}

/* Focus state for keyboard navigation */
.chip.clickable:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 2px;
}

/* Disabled state */
.chip.disabled {
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
  pointer-events: none;
}

/* Selected state */
.chip.selected {
  background-color: var(--ink-cobalt-10);
  color: var(--ink-cobalt-100);
  box-shadow: inset 0 0 0 1px var(--ink-cobalt-30);
}

.chip.selected.clickable:hover {
  background-color: var(--ink-cobalt-20);
}

.chip.selected.clickable:active {
  background-color: var(--ink-cobalt-30);
}

/* Start element (avatar, icon, etc.) */
.startElement {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}

/* Text content */
.text {
  display: flex;
  align-items: center;
  padding: 0 var(--ink-spacing-50); /* 4px horizontal padding per Figma */
  flex: 0 1 auto;
  min-width: 0;
}

/* Remove/close button */
.removeButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--ink-icon-size-s); /* 20px */
  height: var(--ink-icon-size-s); /* 20px */
  padding: 0;
  background: transparent;
  border: none;
  color: var(--ink-font-color-default);
  cursor: pointer;
  flex-shrink: 0;
}

/* No hover or active states - the icon is static, only the chip changes */

.removeButton:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 1px;
}

.removeButton:disabled {
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
  pointer-events: none;
}

import React from 'react';
import { Icon } from '../../3-primitives';
import { cn } from '@/lib/utils';
import styles from './Chip.module.css';

export interface ChipProps {
  /** The text content of the chip */
  children: React.ReactNode;
  /** Optional element to display before the text (e.g., Avatar, Icon) */
  startElement?: React.ReactNode;
  /** Show the close/remove button */
  onRemove?: () => void;
  /** Click handler for the chip */
  onClick?: () => void;
  /** Additional className */
  className?: string;
  /** Disabled state */
  disabled?: boolean;
  /** Selected state for selectable chips */
  selected?: boolean;
}

export const Chip = React.forwardRef<HTMLDivElement, ChipProps>(
  ({ children, startElement, onRemove, onClick, className, disabled = false, selected = false }, ref) => {
    const isClickable = !disabled && (onClick || onRemove);

    const handleClick = (e: React.MouseEvent<HTMLDivElement>) => {
      if (disabled) return;
      onClick?.(e);
    };

    const handleRemoveClick = (e: React.MouseEvent<HTMLButtonElement>) => {
      e.stopPropagation();
      if (disabled) return;
      onRemove?.();
    };

    return (
      <div
        ref={ref}
        className={cn(
          styles.chip,
          isClickable && styles.clickable,
          disabled && styles.disabled,
          selected && styles.selected,
          className
        )}
        onClick={handleClick}
        role={onClick ? 'button' : undefined}
        tabIndex={onClick && !disabled ? 0 : undefined}
        onKeyDown={(e) => {
          if (onClick && !disabled && (e.key === 'Enter' || e.key === ' ')) {
            e.preventDefault();
            onClick(e as any);
          }
        }}
      >
        {startElement && <span className={styles.startElement}>{startElement}</span>}

        <span className={styles.text}>{children}</span>

        {onRemove && (
          <button
            type="button"
            className={styles.removeButton}
            onClick={handleRemoveClick}
            disabled={disabled}
            aria-label="Remove"
          >
            <Icon name="close" size="small" />
          </button>
        )}
      </div>
    );
  }
);

Chip.displayName = 'Chip';

ComboBox:4.content:
/* Ink Design System - ComboBox Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50); /* 4px per Figma */
  width: 100%;
}

/* Label */
.label {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-default);
  line-height: 1.4;
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
}

.labelDisabled {
  opacity: var(--ink-opacity-disabled);
}

.required {
  color: var(--ink-form-text-required);
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Description */
.description {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm);
  color: var(--ink-font-color-neutral);
  line-height: var(--ink-line-height-normal);
}

/* Input Container */
.inputContainer {
  position: relative;
  display: inline-block;
  width: 100%;
}

/* Input */
.input {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-m-size); /* 16px - fontBodyM from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  color: var(--ink-font-color-default);
  background: var(--ink-form-control-bg-color-default);
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-default);
  border-radius: var(--ink-radius-size-xs); /* 4px - radiusSizeXS */
  padding: var(--ink-spacing-100) 46px var(--ink-spacing-100) var(--ink-spacing-200); /* 8px top, 46px right (for icon), 8px bottom, 16px left */
  transition: all var(--ink-transition-fast);
  width: 100%;
  height: 40px; /* Medium input height matching Input component */
  line-height: var(--ink-font-body-m-line-height); /* 1.5 */
  outline: none;
  cursor: text;
  box-sizing: border-box;
}

/* Hover state */
.input:hover:not(:disabled):not(.error) {
  border-color: var(--ink-form-control-border-color-hover);
}

/* Focus/Active state - 2px border */
.input:focus:not(.error) {
  border: var(--ink-form-border-width-active) solid var(--ink-form-control-border-color-active);
  /* Adjust padding to compensate for thicker border */
  padding: calc(var(--ink-spacing-100) - 1px) calc(46px - 1px) calc(var(--ink-spacing-100) - 1px)
    calc(var(--ink-spacing-200) - 1px);
}

/* Error state */
.input.error {
  border-color: var(--ink-form-control-border-color-error);
}

.input.error:focus {
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-error);
  /* Keep same padding for error state */
  padding: var(--ink-spacing-100) 46px var(--ink-spacing-100) var(--ink-spacing-200);
}

/* Disabled state */
.input:disabled {
  background: var(--ink-form-bg-disabled);
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
  border-color: var(--ink-form-control-border-color-default);
}

/* Placeholder */
.input::placeholder {
  color: var(--ink-font-color-neutral);
  opacity: 1;
}

/* Icon Container - absolute positioned for precise centering */
.iconContainer {
  position: absolute;
  right: 0;
  top: 50%;
  transform: translateY(-50%);
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 100%;
  pointer-events: all;
  z-index: 1;
  cursor: pointer;
  background: transparent;
  border-radius: var(--ink-radius-size-xs);
  transition: background-color var(--ink-transition-fast);
}

.iconContainer:hover {
  background-color: var(--ink-cta-bg-color-tertiary-hover); /* rgba(19,0,50,0.1) */
}

.iconContainer:active {
  background-color: var(--ink-cta-bg-color-tertiary-active); /* rgba(19,0,50,0.15) */
}

/* Clear Button - IconButton tertiary style (no opacity changes) */
.clearButton {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  padding: 4px;
  background: transparent;
  border: none;
  border-radius: var(--ink-radius-size-xs);
  color: var(--ink-font-color-default);
  cursor: pointer;
  pointer-events: all;
  transition: background-color var(--ink-transition-fast);
  flex-shrink: 0;
}

.clearButton:hover {
  background-color: var(--ink-cta-bg-color-tertiary-hover); /* rgba(19,0,50,0.1) */
}

.clearButton:active {
  background-color: var(--ink-cta-bg-color-tertiary-active); /* rgba(19,0,50,0.15) */
}

.clearButton:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 2px;
}

/* Chevron Icon */
.chevronIcon {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  color: var(--ink-font-color-default);
  transition: transform var(--ink-transition-fast);
  flex-shrink: 0;
}

.chevronOpen {
  transform: rotate(180deg);
}

.input:disabled ~ .iconContainer .chevronIcon {
  opacity: var(--ink-opacity-disabled);
}

/* Listbox (Dropdown) */
.listbox {
  position: absolute;
  top: calc(100% + var(--ink-spacing-50));
  left: 0;
  right: 0;
  z-index: var(--ink-z-dropdown);
  max-height: 300px;
  overflow-y: auto;
  background-color: var(--ink-bg-glass-frost); /* bgColorGlassFrost - frosted glass effect */
  backdrop-filter: blur(10px); /* Glass frost effect */
  -webkit-backdrop-filter: blur(10px); /* Safari support */
  border: var(--ink-form-border-width-active) solid var(--ink-neutral-fade-10); /* 2px borderColorSubtle from Figma */
  border-radius: var(--ink-radius-size-m); /* 12px - radiusSizeM from Figma */
  box-shadow: none; /* No shadow in Figma design */
  list-style: none;
  margin: 0;
  padding: var(--ink-spacing-100);
}

/* Listbox Option */
.option {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding-left: var(--ink-spacing-200); /* 16px per Figma */
  padding-right: var(--ink-spacing-100); /* 8px per Figma */
  padding-top: var(--ink-spacing-100); /* 8px */
  padding-bottom: var(--ink-spacing-100); /* 8px */
  border-radius: var(--ink-radius-size-xs);
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px per Figma */
  color: var(--ink-font-color-default);
  line-height: 1.4; /* 1.4 per Figma */
  user-select: none;
}

.option:hover:not(.optionDisabled),
.optionHighlighted:not(.optionDisabled) {
  background-color: var(--ink-item-bg-color-hover);
}

.optionSelected {
  font-weight: var(--ink-font-weight-medium);
}

.optionDisabled {
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
}

/* Check Icon */
.checkIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--ink-icon-color-accent);
  flex-shrink: 0;
  margin-left: var(--ink-spacing-100);
}

/* No Results */
.noResults {
  padding: var(--ink-spacing-200);
  text-align: center;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm);
  color: var(--ink-font-color-neutral);
  line-height: 1.5;
}

/* Error message */
.errorMessage {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-error);
  line-height: 1.5;
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-50);
}

.errorIcon {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Scrollbar styling */
.listbox::-webkit-scrollbar {
  width: 8px;
}

.listbox::-webkit-scrollbar-track {
  background: transparent;
}

.listbox::-webkit-scrollbar-thumb {
  background: var(--ink-neutral-30);
  border-radius: var(--ink-radius-size-full);
}

.listbox::-webkit-scrollbar-thumb:hover {
  background: var(--ink-neutral-40);
}

import React, { forwardRef, useEffect, useRef, useState } from 'react';
import styles from './ComboBox.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export interface ComboBoxOption {
  value: string;
  label: string;
  disabled?: boolean;
}

export interface ComboBoxProps {
  label: string;
  hideLabel?: boolean;
  description?: string;
  error?: string;
  options: ComboBoxOption[];
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  disabled?: boolean;
  required?: boolean;
  id?: string;
  width?: string;
}

export const ComboBox = forwardRef<HTMLInputElement, ComboBoxProps>(
  (
    {
      label,
      hideLabel = false,
      description,
      error,
      options,
      value,
      onChange,
      placeholder = 'Select an option...',
      disabled = false,
      required = false,
      id,
      width,
    },
    ref
  ) => {
    const generatedId = React.useId();
    const comboBoxId = id || generatedId;
    const errorId = `${comboBoxId}-error`;
    const descriptionId = `${comboBoxId}-description`;
    const listboxId = `${comboBoxId}-listbox`;

    const [isOpen, setIsOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [highlightedIndex, setHighlightedIndex] = useState(-1);
    const [inputValue, setInputValue] = useState('');

    const containerRef = useRef<HTMLDivElement>(null);
    const inputRef = useRef<HTMLInputElement>(null);
    const listboxRef = useRef<HTMLUListElement>(null);

    // Find selected option label
    const selectedOption = options.find((opt) => opt.value === value);

    // Filter options based on search term
    const filteredOptions = options.filter((option) =>
      option.label.toLowerCase().includes(searchTerm.toLowerCase())
    );

    // Update input value when value prop changes
    useEffect(() => {
      if (selectedOption) {
        setInputValue(selectedOption.label);
        setSearchTerm('');
      } else {
        setInputValue('');
        setSearchTerm('');
      }
    }, [value, selectedOption]);

    // Close dropdown when clicking outside
    useEffect(() => {
      const handleClickOutside = (event: MouseEvent) => {
        if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
          setIsOpen(false);
          // Reset input to selected value
          if (selectedOption) {
            setInputValue(selectedOption.label);
            setSearchTerm('');
          } else {
            setInputValue('');
            setSearchTerm('');
          }
          setHighlightedIndex(-1);
        }
      };

      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }, [selectedOption]);

    // Scroll highlighted option into view
    useEffect(() => {
      if (highlightedIndex >= 0 && listboxRef.current) {
        const highlightedElement = listboxRef.current.children[highlightedIndex] as HTMLElement;
        if (highlightedElement) {
          highlightedElement.scrollIntoView({
            block: 'nearest',
          });
        }
      }
    }, [highlightedIndex]);

    const ariaDescribedBy = [
      error ? errorId : null,
      description ? descriptionId : null,
    ]
      .filter(Boolean)
      .join(' ');

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const newValue = e.target.value;
      setInputValue(newValue);
      setSearchTerm(newValue);
      setIsOpen(true);
      setHighlightedIndex(-1);
    };

    const handleInputClick = () => {
      if (!disabled) {
        setIsOpen(!isOpen);
      }
    };

    const handleChevronClick = (e: React.MouseEvent) => {
      e.stopPropagation();
      if (!disabled) {
        setIsOpen(!isOpen);
        inputRef.current?.focus();
      }
    };

    const handleOptionClick = (option: ComboBoxOption) => {
      if (!option.disabled) {
        onChange(option.value);
        setInputValue(option.label);
        setSearchTerm('');
        setIsOpen(false);
        setHighlightedIndex(-1);
      }
    };

    const handleClear = () => {
      onChange('');
      setInputValue('');
      setSearchTerm('');
      setHighlightedIndex(-1);
      inputRef.current?.focus();
    };

    const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          if (!isOpen) {
            setIsOpen(true);
          } else {
            let nextIndex = highlightedIndex;
            do {
              nextIndex = nextIndex < filteredOptions.length - 1 ? nextIndex + 1 : nextIndex;
            } while (
              nextIndex < filteredOptions.length - 1 &&
              filteredOptions[nextIndex]?.disabled
            );
            setHighlightedIndex(nextIndex);
          }
          break;

        case 'ArrowUp':
          e.preventDefault();
          if (!isOpen) {
            setIsOpen(true);
          } else {
            let prevIndex = highlightedIndex;
            do {
              prevIndex = prevIndex > 0 ? prevIndex - 1 : -1;
            } while (prevIndex > 0 && filteredOptions[prevIndex]?.disabled);
            setHighlightedIndex(prevIndex);
          }
          break;

        case 'Enter':
          e.preventDefault();
          if (isOpen && highlightedIndex >= 0 && filteredOptions[highlightedIndex]) {
            handleOptionClick(filteredOptions[highlightedIndex]);
          }
          break;

        case 'Escape':
          e.preventDefault();
          setIsOpen(false);
          // Reset input to selected value
          if (selectedOption) {
            setInputValue(selectedOption.label);
            setSearchTerm('');
          } else {
            setInputValue('');
            setSearchTerm('');
          }
          setHighlightedIndex(-1);
          break;

        case 'Tab':
          setIsOpen(false);
          // Reset input to selected value on tab
          if (selectedOption) {
            setInputValue(selectedOption.label);
            setSearchTerm('');
          } else {
            setInputValue('');
            setSearchTerm('');
          }
          setHighlightedIndex(-1);
          break;
      }
    };

    const inputClasses = [
      styles.input,
      error && styles.error,
      disabled && styles.disabled,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={styles.wrapper} style={{ width }} ref={containerRef}>
        <label
          htmlFor={comboBoxId}
          className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
            disabled ? styles.labelDisabled : ''
          }`}
        >
          {label}
          {required && <span className={styles.required}> *</span>}
        </label>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <div className={styles.inputContainer}>
          <input
            ref={(node) => {
              if (typeof ref === 'function') {
                ref(node);
              } else if (ref) {
                ref.current = node;
              }
              (inputRef as React.MutableRefObject<HTMLInputElement | null>).current = node;
            }}
            id={comboBoxId}
            type="text"
            role="combobox"
            aria-autocomplete="list"
            aria-expanded={isOpen}
            aria-controls={listboxId}
            aria-activedescendant={
              highlightedIndex >= 0
                ? `${comboBoxId}-option-${highlightedIndex}`
                : undefined
            }
            aria-describedby={ariaDescribedBy || undefined}
            aria-invalid={error ? true : undefined}
            aria-required={required || undefined}
            className={inputClasses}
            value={inputValue}
            onChange={handleInputChange}
            onClick={handleInputClick}
            onKeyDown={handleKeyDown}
            placeholder={placeholder}
            disabled={disabled}
            autoComplete="off"
          />

          <div className={styles.iconContainer} onClick={handleChevronClick}>
            {value && !disabled && (
              <IconButton
                icon="close"
                size="small"
                variant="tertiary"
                onClick={(e) => {
                  e.stopPropagation();
                  handleClear();
                }}
                aria-label="Clear selection"
                tabIndex={-1}
                className={styles.clearButton}
              />
            )}
            <div
              className={`${styles.chevronIcon} ${isOpen ? styles.chevronOpen : ''}`}
            >
              <Icon name="chevron-down" size="small" />
            </div>
          </div>

          {isOpen && !disabled && (
            <ul
              ref={listboxRef}
              id={listboxId}
              role="listbox"
              className={styles.listbox}
              aria-label={label}
            >
              {filteredOptions.length === 0 ? (
                <li className={styles.noResults}>No options found</li>
              ) : (
                filteredOptions.map((option, index) => {
                  const isSelected = option.value === value;
                  const isHighlighted = index === highlightedIndex;

                  return (
                    <li
                      key={option.value}
                      id={`${comboBoxId}-option-${index}`}
                      role="option"
                      aria-selected={isSelected}
                      aria-disabled={option.disabled}
                      className={`${styles.option} ${
                        isSelected ? styles.optionSelected : ''
                      } ${isHighlighted ? styles.optionHighlighted : ''} ${
                        option.disabled ? styles.optionDisabled : ''
                      }`}
                      onClick={() => handleOptionClick(option)}
                      onMouseEnter={() => !option.disabled && setHighlightedIndex(index)}
                    >
                      {option.label}
                      {isSelected && (
                        <span className={styles.checkIcon}>
                          <Icon name="check" size="small" />
                        </span>
                      )}
                    </li>
                  );
                })
              )}
            </ul>
          )}
        </div>

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            <span className={styles.errorIcon}>
              <Icon name="status-error" size="small" />
            </span>
            <span>{error}</span>
          </div>
        )}
      </div>
    );
  }
);

ComboBox.displayName = 'ComboBox';

ComboButton:4.content:
/* Ink Design System - ComboButton Component Styles */
@import '../../1-tokens/tokens.css';

/* Wrapper */
.comboButton {
  display: inline-flex;
  align-items: center;
  position: relative;
}

/* Main Button (Left) */
.mainButton {
  display: flex;
  align-items: center;
  justify-content: center;
  gap: var(--ink-spacing-2);
  font-family: var(--ink-font-family);
  font-weight: var(--ink-font-weight-medium);
  border: none;
  outline: none;
  cursor: pointer;
  text-decoration: none;
  position: relative;
  transition: all var(--ink-transition-fast);
  white-space: nowrap;
  user-select: none;
  min-width: 80px;
  border-top-left-radius: var(--ink-radius-size-xs);
  border-bottom-left-radius: var(--ink-radius-size-xs);
  border-top-right-radius: 0;
  border-bottom-right-radius: 0;
}

/* Dropdown Button (Right) */
.dropdownButton {
  display: flex;
  align-items: center;
  justify-content: center;
  font-family: var(--ink-font-family);
  font-weight: var(--ink-font-weight-medium);
  border: none;
  outline: none;
  cursor: pointer;
  position: relative;
  transition: all var(--ink-transition-fast);
  user-select: none;
  border-top-left-radius: 0;
  border-bottom-left-radius: 0;
  border-top-right-radius: var(--ink-radius-size-xs);
  border-bottom-right-radius: var(--ink-radius-size-xs);
}

/* Size Variants */
.mainButton.medium {
  height: 40px;
  padding: var(--ink-spacing-2) var(--ink-spacing-3);
  font-size: var(--ink-font-size-md); /* 16px */
  line-height: 1.5;
}

.dropdownButton.medium {
  width: 40px;
  height: 40px;
}

.mainButton.small {
  height: 32px;
  padding: 6px var(--ink-spacing-2);
  font-size: var(--ink-font-size-sm); /* 14px */
  line-height: 1.4;
  gap: var(--ink-spacing-1);
}

.dropdownButton.small {
  width: 32px;
  height: 32px;
}

/* Compact Mode (icon-only for tertiary) */
.comboButton.compact .mainButton {
  min-width: auto;
  padding: 0;
}

.comboButton.compact.medium .mainButton {
  width: 40px;
}

.comboButton.compact.small .mainButton {
  width: 32px;
}

.comboButton.compact.medium .dropdownButton {
  width: 20px;
}

.comboButton.compact.small .dropdownButton {
  width: 20px;
}

/* Brand Variant */
.mainButton.brand,
.dropdownButton.brand {
  background-color: var(--ink-button-brand-bg);
  color: var(--ink-button-brand-text);
}

.dropdownButton.brand {
  border-left: 1px solid var(--ink-white-100);
}

.mainButton.brand:hover:not(:disabled),
.dropdownButton.brand:hover:not(:disabled) {
  background-color: var(--ink-button-brand-bg-hover);
}

.mainButton.brand:active:not(:disabled),
.dropdownButton.brand:active:not(:disabled) {
  background-color: var(--ink-button-brand-bg-active);
}

/* Primary Variant */
.mainButton.primary,
.dropdownButton.primary {
  background-color: var(--ink-cta-bg-color-primary-default);
  color: var(--ink-button-primary-text);
}

.dropdownButton.primary {
  border-left: 1px solid var(--ink-white-100);
}

.mainButton.primary:hover:not(:disabled),
.dropdownButton.primary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-primary-hover);
}

.mainButton.primary:active:not(:disabled),
.dropdownButton.primary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-primary-active);
}

/* Secondary Variant */
.mainButton.secondary,
.dropdownButton.secondary {
  background-color: var(--ink-cta-bg-color-secondary-default);
  color: var(--ink-button-secondary-text);
  border: 1px solid var(--ink-cta-border-color-secondary-default);
}

.mainButton.secondary {
  border-right: none;
}

.mainButton.secondary:hover:not(:disabled),
.dropdownButton.secondary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-secondary-hover);
  border-color: var(--ink-cta-border-color-secondary-hover);
}

.mainButton.secondary:active:not(:disabled),
.dropdownButton.secondary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-secondary-active);
  border-color: var(--ink-cta-border-color-secondary-active);
}

/* Tertiary Variant */
.mainButton.tertiary,
.dropdownButton.tertiary {
  background-color: var(--ink-cta-bg-color-tertiary-default);
  color: var(--ink-button-tertiary-text);
}

/* Tertiary Compact Mode - Transparent backgrounds by default */
.comboButton.compact.tertiary .mainButton,
.comboButton.compact.tertiary .dropdownButton {
  background-color: transparent;
}

/* Tertiary Compact - Individual button hovers */
.comboButton.compact.tertiary .mainButton:hover:not(:disabled),
.comboButton.compact.tertiary .dropdownButton:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-tertiary-hover);
}

/* Tertiary Non-Compact Hover */
.comboButton:not(.compact) .mainButton.tertiary:hover:not(:disabled),
.comboButton:not(.compact) .dropdownButton.tertiary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-tertiary-hover);
}

.mainButton.tertiary:active:not(:disabled),
.dropdownButton.tertiary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-tertiary-active);
}

/* Focus States */
.mainButton:focus-visible,
.dropdownButton:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 2px;
  z-index: 1;
}

/* Disabled State */
.comboButton.disabled {
  opacity: var(--ink-opacity-disabled);
  pointer-events: none;
}

.mainButton:disabled,
.dropdownButton:disabled {
  cursor: not-allowed;
}

/* Text */
.text {
  display: inline-flex;
  align-items: center;
}

/* Inverted Variant (for dark backgrounds) */
.comboButton.inverted .mainButton,
.comboButton.inverted .dropdownButton {
  background-color: var(--ink-bg-color-default);
  color: var(--ink-font-color-default);
  border-color: var(--ink-bg-color-default);
}

.comboButton.inverted .mainButton:hover:not(:disabled),
.comboButton.inverted .dropdownButton:hover:not(:disabled) {
  background-color: var(--ink-neutral-10);
}

.comboButton.inverted .mainButton:active:not(:disabled),
.comboButton.inverted .dropdownButton:active:not(:disabled) {
  background-color: var(--ink-neutral-20);
}

import * as React from 'react';
import { Icon } from '../../3-primitives/Icon';
import { cn } from '@/lib/utils';
import styles from './ComboButton.module.css';

export type ComboButtonVariant = 'brand' | 'primary' | 'secondary' | 'tertiary';
export type ComboButtonSize = 'small' | 'medium';

export interface ComboButtonProps {
  /** The variant/kind of the ComboButton */
  variant?: ComboButtonVariant;
  /** Size of the combo button */
  size?: ComboButtonSize;
  /** Main button text (ignored for tertiary which is icon-only) */
  children?: React.ReactNode;
  /** Disabled state */
  disabled?: boolean;
  /** Click handler for main button */
  onClick?: (e: React.MouseEvent<HTMLButtonElement>) => void;
  /** Click handler for dropdown trigger */
  onDropdownClick?: (e: React.MouseEvent<HTMLButtonElement>) => void;
  /** Icon to display in main button (required for tertiary variant) */
  startIcon?: string;
  /** Custom className */
  className?: string;
  /** Compact mode (icon-only). Note: tertiary variant is always compact per Figma spec */
  compact?: boolean;
  /** Inverts colors for dark backgrounds */
  inverted?: boolean;
}

export const ComboButton = React.forwardRef<HTMLDivElement, ComboButtonProps>(
  (
    {
      variant = 'brand',
      size = 'medium',
      children,
      disabled = false,
      onClick,
      onDropdownClick,
      startIcon,
      className,
      compact: compactProp = false,
      inverted = false,
    },
    ref
  ) => {
    // Tertiary variant is always compact (icon-only) per Figma spec
    const compact = variant === 'tertiary' ? true : compactProp;

    const wrapperClasses = cn(
      styles.comboButton,
      styles[variant],
      styles[size],
      disabled && styles.disabled,
      compact && styles.compact,
      inverted && styles.inverted,
      className
    );

    const mainButtonClasses = cn(styles.mainButton, styles[variant], styles[size]);

    const dropdownButtonClasses = cn(styles.dropdownButton, styles[variant], styles[size]);

    return (
      <div ref={ref} className={wrapperClasses}>
        <button type="button" className={mainButtonClasses} onClick={onClick} disabled={disabled}>
          {startIcon && !compact && (
            <Icon name={startIcon} size={size === 'small' ? 'small' : 'medium'} />
          )}
          {compact && startIcon ? (
            <Icon name={startIcon} size={size === 'small' ? 'small' : 'medium'} />
          ) : (
            <span className={styles.text}>{children}</span>
          )}
        </button>
        <button
          type="button"
          className={dropdownButtonClasses}
          onClick={onDropdownClick}
          disabled={disabled}
          aria-label="Show options"
        >
          <Icon name="chevron-down" size="small" />
        </button>
      </div>
    );
  }
);

ComboButton.displayName = 'ComboButton';

DatePicker:4.content:
/* Ink Design System - DatePicker Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1);
  font-family: var(--ink-font-family);
}

.label {
  font-size: var(--ink-font-size-sm);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  display: block;
}

.container {
  position: relative;
  width: 100%;
}

.inputWrapper {
  position: relative;
  display: flex;
  align-items: center;
  cursor: pointer;
}

.input {
  width: 100%;
  height: 40px;
  padding: 0 var(--ink-spacing-4);
  padding-right: var(--ink-spacing-10);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md);
  color: var(--ink-neutral-100);
  background-color: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-md);
  outline: none;
  transition: all var(--ink-transition-fast);
  cursor: pointer;
}

.input::placeholder {
  color: var(--ink-neutral-50);
}

.input:hover:not(:disabled) {
  border-color: var(--ink-neutral-40);
}

.input:focus {
  border-color: var(--ink-cobalt-60);
  box-shadow: 0 0 0 3px var(--ink-cobalt-10);
}

.input:disabled {
  background-color: var(--ink-neutral-10);
  color: var(--ink-neutral-50);
  cursor: not-allowed;
}

.error .input {
  border-color: var(--ink-red-60);
}

.error .input:focus {
  box-shadow: 0 0 0 3px var(--ink-red-10);
}

.icon {
  position: absolute;
  right: var(--ink-spacing-4);
  color: var(--ink-neutral-60);
  pointer-events: none;
}

.disabled .icon {
  color: var(--ink-neutral-40);
}

/* Calendar Popup */
.calendar {
  position: absolute;
  top: calc(100% + 4px);
  left: 0;
  z-index: var(--ink-z-popover);
  background-color: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-lg);
  box-shadow: var(--ink-shadow-lg);
  padding: var(--ink-spacing-4);
  min-width: 280px;
  animation: slideDown 150ms ease-out;
}

@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-8px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.calendarHeader {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--ink-spacing-3);
}

.monthYear {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-neutral-100);
}

.navButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 0;
  background-color: transparent;
  border: none;
  border-radius: var(--ink-radius-md);
  color: var(--ink-neutral-70);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
}

.navButton:hover {
  background-color: var(--ink-neutral-10);
  color: var(--ink-neutral-100);
}

.navButton:active {
  background-color: var(--ink-neutral-20);
}

/* Days Header */
.daysHeader {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: var(--ink-spacing-1);
  margin-bottom: var(--ink-spacing-2);
}

.dayLabel {
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: var(--ink-font-size-xs);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-neutral-60);
  height: 32px;
}

/* Days Grid */
.daysGrid {
  display: grid;
  grid-template-columns: repeat(7, 1fr);
  gap: var(--ink-spacing-1);
}

.day {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 32px;
  width: 32px;
  padding: 0;
  font-size: var(--ink-font-size-sm);
  font-weight: var(--ink-font-weight-regular);
  color: var(--ink-neutral-100);
  background-color: transparent;
  border: none;
  border-radius: var(--ink-radius-md);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  position: relative;
}

.day:hover:not(:disabled):not(.selected) {
  background-color: var(--ink-neutral-10);
}

.day:active:not(:disabled) {
  background-color: var(--ink-neutral-20);
}

.day.selected {
  background-color: var(--ink-cobalt-60);
  color: var(--ink-white-100);
  font-weight: var(--ink-font-weight-semibold);
}

.day.today:not(.selected) {
  color: var(--ink-cobalt-60);
  font-weight: var(--ink-font-weight-semibold);
}

.day.today:not(.selected)::after {
  content: '';
  position: absolute;
  bottom: 4px;
  left: 50%;
  transform: translateX(-50%);
  width: 4px;
  height: 4px;
  border-radius: 50%;
  background-color: var(--ink-cobalt-60);
}

.day.otherMonth {
  color: var(--ink-neutral-40);
}

.day.dayDisabled {
  color: var(--ink-neutral-30);
  cursor: not-allowed;
  background-color: transparent;
}

.day.dayDisabled:hover {
  background-color: transparent;
}

/* Helper Text */
.helperText {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
  margin-top: var(--ink-spacing-1);
}

.helperTextError {
  color: var(--ink-red-60);
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './DatePicker.module.css';
import { Icon } from '@/design-system';
import { IconButton } from '../../3-primitives/IconButton';

export interface DatePickerProps
  extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'onChange' | 'value'> {
  /** Selected date value */
  value?: Date | null;
  /** Callback when date changes */
  onChange?: (date: Date | null) => void;
  /** Minimum selectable date */
  minDate?: Date;
  /** Maximum selectable date */
  maxDate?: Date;
  /** Date format for display */
  format?: 'MM/DD/YYYY' | 'DD/MM/YYYY' | 'YYYY-MM-DD';
  /** Placeholder text */
  placeholder?: string;
  /** Whether the input is disabled */
  disabled?: boolean;
  /** Whether the input has an error */
  error?: boolean;
  /** Helper text */
  helperText?: string;
  /** Label for the input */
  label?: string;
  /** Custom className */
  className?: string;
}

const MONTHS = [
  'January',
  'February',
  'March',
  'April',
  'May',
  'June',
  'July',
  'August',
  'September',
  'October',
  'November',
  'December',
];

const DAYS = ['Su', 'Mo', 'Tu', 'We', 'Th', 'Fr', 'Sa'];

const DatePicker = React.forwardRef<HTMLInputElement, DatePickerProps>(
  (
    {
      value = null,
      onChange,
      minDate,
      maxDate,
      format = 'MM/DD/YYYY',
      placeholder = 'Select date',
      disabled = false,
      error = false,
      helperText,
      label,
      className,
      ...props
    },
    ref
  ) => {
    const [isOpen, setIsOpen] = React.useState(false);
    const [currentMonth, setCurrentMonth] = React.useState(value ? new Date(value) : new Date());
    const containerRef = React.useRef<HTMLDivElement>(null);

    // Close calendar when clicking outside
    React.useEffect(() => {
      const handleClickOutside = (event: MouseEvent) => {
        if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
          setIsOpen(false);
        }
      };

      if (isOpen) {
        document.addEventListener('mousedown', handleClickOutside);
      }

      return () => {
        document.removeEventListener('mousedown', handleClickOutside);
      };
    }, [isOpen]);

    const formatDate = (date: Date | null): string => {
      if (!date) return '';

      const day = String(date.getDate()).padStart(2, '0');
      const month = String(date.getMonth() + 1).padStart(2, '0');
      const year = date.getFullYear();

      switch (format) {
        case 'DD/MM/YYYY':
          return `${day}/${month}/${year}`;
        case 'YYYY-MM-DD':
          return `${year}-${month}-${day}`;
        case 'MM/DD/YYYY':
        default:
          return `${month}/${day}/${year}`;
      }
    };

    const isDateDisabled = (date: Date): boolean => {
      if (minDate && date < minDate) return true;
      if (maxDate && date > maxDate) return true;
      return false;
    };

    const isSameDay = (date1: Date | null, date2: Date): boolean => {
      if (!date1) return false;
      return (
        date1.getDate() === date2.getDate() &&
        date1.getMonth() === date2.getMonth() &&
        date1.getFullYear() === date2.getFullYear()
      );
    };

    const getDaysInMonth = (date: Date): Date[] => {
      const year = date.getFullYear();
      const month = date.getMonth();
      const firstDay = new Date(year, month, 1);
      const lastDay = new Date(year, month + 1, 0);
      const daysInMonth = lastDay.getDate();
      const startingDayOfWeek = firstDay.getDay();

      const days: Date[] = [];

      // Add empty slots for days before month starts
      for (let i = 0; i < startingDayOfWeek; i++) {
        const prevMonthDay = new Date(year, month, -startingDayOfWeek + i + 1);
        days.push(prevMonthDay);
      }

      // Add days of current month
      for (let i = 1; i <= daysInMonth; i++) {
        days.push(new Date(year, month, i));
      }

      // Add days to fill the last week
      const remainingDays = 7 - (days.length % 7);
      if (remainingDays < 7) {
        for (let i = 1; i <= remainingDays; i++) {
          days.push(new Date(year, month + 1, i));
        }
      }

      return days;
    };

    const handleDateSelect = (date: Date) => {
      if (isDateDisabled(date)) return;
      onChange?.(date);
      setIsOpen(false);
    };

    const handlePrevMonth = () => {
      setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() - 1));
    };

    const handleNextMonth = () => {
      setCurrentMonth(new Date(currentMonth.getFullYear(), currentMonth.getMonth() + 1));
    };

    const handleInputClick = () => {
      if (!disabled) {
        setIsOpen(!isOpen);
      }
    };

    const days = getDaysInMonth(currentMonth);
    const isCurrentMonth = (date: Date) => date.getMonth() === currentMonth.getMonth();

    return (
      <div className={cn(styles.wrapper, className)}>
        {label && <label className={styles.label}>{label}</label>}
        <div
          ref={containerRef}
          className={cn(styles.container, disabled && styles.disabled, error && styles.error)}
        >
          <div className={styles.inputWrapper} onClick={handleInputClick}>
            <input
              ref={ref}
              type="text"
              className={styles.input}
              value={formatDate(value)}
              placeholder={placeholder}
              disabled={disabled}
              readOnly
              {...props}
            />
            <Icon name="calendar" size="small" className={styles.icon} />
          </div>

          {isOpen && !disabled && (
            <div className={styles.calendar}>
              <div className={styles.calendarHeader}>
                <IconButton
                  icon="chevron-left"
                  size="small"
                  variant="tertiary"
                  onClick={handlePrevMonth}
                  aria-label="Previous month"
                  className={styles.navButton}
                />
                <div className={styles.monthYear}>
                  {MONTHS[currentMonth.getMonth()]} {currentMonth.getFullYear()}
                </div>
                <IconButton
                  icon="chevron-right"
                  size="small"
                  variant="tertiary"
                  onClick={handleNextMonth}
                  aria-label="Next month"
                  className={styles.navButton}
                />
              </div>

              <div className={styles.daysHeader}>
                {DAYS.map((day) => (
                  <div key={day} className={styles.dayLabel}>
                    {day}
                  </div>
                ))}
              </div>

              <div className={styles.daysGrid}>
                {days.map((date, index) => {
                  const isSelected = isSameDay(value, date);
                  const isToday = date.toDateString() === new Date().toDateString();
                  const isDisabled = isDateDisabled(date);
                  const isOtherMonth = !isCurrentMonth(date);

                  return (
                    <button
                      key={index}
                      type="button"
                      className={cn(
                        styles.day,
                        isSelected && styles.selected,
                        isToday && styles.today,
                        isDisabled && styles.dayDisabled,
                        isOtherMonth && styles.otherMonth
                      )}
                      onClick={() => handleDateSelect(date)}
                      disabled={isDisabled}
                    >
                      {date.getDate()}
                    </button>
                  );
                })}
              </div>
            </div>
          )}
        </div>
        {helperText && (
          <div className={cn(styles.helperText, error && styles.helperTextError)}>{helperText}</div>
        )}
      </div>
    );
  }
);

DatePicker.displayName = 'InkDatePicker';

export { DatePicker };

Drawer:4.content:
/* Container and Overlay */
.container {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  pointer-events: none;
}

.overlay {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: var(--ink-neutral-fade-50);
  pointer-events: auto;
  animation: fadeIn 0.2s ease-out;
}

/* Drawer Base */
.drawer {
  position: absolute;
  background: white;
  box-shadow: var(--ink-shadow-xl);
  display: flex;
  flex-direction: column;
  pointer-events: auto;
  max-width: 100vw;
  max-height: 100vh;
  overflow: hidden;
}

/* Position Variants */
.drawer.left {
  top: 0;
  left: 0;
  bottom: 0;
  animation: slideInLeft 0.3s ease-out;
}

.drawer.right {
  top: 0;
  right: 0;
  bottom: 0;
  animation: slideInRight 0.3s ease-out;
}

.drawer.top {
  top: 0;
  left: 0;
  right: 0;
  animation: slideInTop 0.3s ease-out;
}

.drawer.bottom {
  bottom: 0;
  left: 0;
  right: 0;
  animation: slideInBottom 0.3s ease-out;
}

/* Size Variants - Horizontal (left/right) */
.drawer.left.small,
.drawer.right.small {
  width: 300px;
}

.drawer.left.medium,
.drawer.right.medium {
  width: 400px;
}

.drawer.left.large,
.drawer.right.large {
  width: 600px;
}

.drawer.left.full,
.drawer.right.full {
  width: calc(100vw - 60px);
}

/* Size Variants - Vertical (top/bottom) */
.drawer.top.small,
.drawer.bottom.small {
  height: 200px;
}

.drawer.top.medium,
.drawer.bottom.medium {
  height: 300px;
}

.drawer.top.large,
.drawer.bottom.large {
  height: 500px;
}

.drawer.top.full,
.drawer.bottom.full {
  height: calc(100vh - 60px);
}

/* Header */
.header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--ink-spacing-lg) var(--ink-spacing-xl);
  border-bottom: 1px solid var(--ink-neutral-30);
  flex-shrink: 0;
}

.title {
  flex: 1;
  min-width: 0;
}

.title h2 {
  margin: 0;
  font-size: var(--ink-font-size-lg);
  font-weight: var(--ink-font-weight-semibold);
  color: var(--ink-neutral-100);
  line-height: var(--ink-line-height-tight);
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}

.headerActions {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-md);
  margin-left: var(--ink-spacing-lg);
  flex-shrink: 0;
}

.closeButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 0;
  background: transparent;
  border: none;
  border-radius: var(--ink-radius-md);
  cursor: pointer;
  color: var(--ink-neutral-70);
  transition:
    background-color 0.2s,
    color 0.2s;
}

.closeButton:hover {
  background: var(--ink-neutral-20);
  color: var(--ink-neutral-100);
}

.closeButton:focus {
  outline: 2px solid var(--ink-cobalt-60);
  outline-offset: 2px;
}

/* Body */
.body {
  flex: 1;
  padding: var(--ink-spacing-xl);
  overflow-y: auto;
  overflow-x: hidden;
  overscroll-behavior: contain;
}

/* Custom scrollbar */
.body::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

.body::-webkit-scrollbar-track {
  background: var(--ink-neutral-10);
}

.body::-webkit-scrollbar-thumb {
  background: var(--ink-neutral-40);
  border-radius: 4px;
}

.body::-webkit-scrollbar-thumb:hover {
  background: var(--ink-neutral-50);
}

/* Footer */
.footer {
  padding: var(--ink-spacing-lg) var(--ink-spacing-xl);
  border-top: 1px solid var(--ink-neutral-30);
  background: var(--ink-neutral-10);
  flex-shrink: 0;
}

/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

@keyframes slideInLeft {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

@keyframes slideInRight {
  from {
    transform: translateX(100%);
  }
  to {
    transform: translateX(0);
  }
}

@keyframes slideInTop {
  from {
    transform: translateY(-100%);
  }
  to {
    transform: translateY(0);
  }
}

@keyframes slideInBottom {
  from {
    transform: translateY(100%);
  }
  to {
    transform: translateY(0);
  }
}

/* Mobile Responsiveness */
@media (max-width: 640px) {
  .drawer.left.small,
  .drawer.left.medium,
  .drawer.left.large,
  .drawer.left.full,
  .drawer.right.small,
  .drawer.right.medium,
  .drawer.right.large,
  .drawer.right.full {
    width: calc(100vw - 20px);
  }

  .drawer.top,
  .drawer.bottom {
    height: auto;
    max-height: calc(100vh - 20px);
  }

  .header {
    padding: var(--ink-spacing-md) var(--ink-spacing-lg);
  }

  .body {
    padding: var(--ink-spacing-lg);
  }

  .footer {
    padding: var(--ink-spacing-md) var(--ink-spacing-lg);
  }
}

import React, { useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import styles from './Drawer.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export type DrawerPosition = 'left' | 'right' | 'top' | 'bottom';
export type DrawerSize = 'small' | 'medium' | 'large' | 'full';

export interface DrawerProps {
  /** Whether the drawer is open */
  open: boolean;
  /** Callback when drawer should close */
  onClose: () => void;
  /** Position of the drawer */
  position?: DrawerPosition;
  /** Size of the drawer */
  size?: DrawerSize;
  /** Title for the drawer header */
  title?: React.ReactNode;
  /** Actions for the drawer header */
  headerActions?: React.ReactNode;
  /** Footer content */
  footer?: React.ReactNode;
  /** Whether to show overlay backdrop */
  overlay?: boolean;
  /** Whether drawer can be closed by clicking overlay */
  closeOnOverlayClick?: boolean;
  /** Whether drawer can be closed by pressing Escape */
  closeOnEscape?: boolean;
  /** Whether to show close button */
  showCloseButton?: boolean;
  /** Additional className */
  className?: string;
  /** Drawer content */
  children: React.ReactNode;
  /** Custom width (for left/right) or height (for top/bottom) */
  customSize?: string;
  /** Z-index for the drawer */
  zIndex?: number;
}

export const Drawer: React.FC<DrawerProps> = ({
  open,
  onClose,
  position = 'right',
  size = 'medium',
  title,
  headerActions,
  footer,
  overlay = true,
  closeOnOverlayClick = true,
  closeOnEscape = true,
  showCloseButton = true,
  className = '',
  children,
  customSize,
  zIndex = 1000,
}) => {
  const drawerRef = useRef<HTMLDivElement>(null);

  // Handle escape key
  useEffect(() => {
    if (!open || !closeOnEscape) return;

    const handleEscape = (e: KeyboardEvent) => {
      if (e.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [open, closeOnEscape, onClose]);

  // Lock body scroll when drawer is open
  useEffect(() => {
    if (!open) return;

    const originalOverflow = document.body.style.overflow;
    document.body.style.overflow = 'hidden';

    return () => {
      document.body.style.overflow = originalOverflow;
    };
  }, [open]);

  // Focus trap
  useEffect(() => {
    if (!open || !drawerRef.current) return;

    const drawer = drawerRef.current;
    const focusableElements = drawer.querySelectorAll(
      'button, [href], input, select, textarea, [tabindex]:not([tabindex="-1"])'
    );

    if (focusableElements.length === 0) return;

    const firstFocusable = focusableElements[0] as HTMLElement;
    const lastFocusable = focusableElements[focusableElements.length - 1] as HTMLElement;

    // Focus first element
    setTimeout(() => firstFocusable?.focus(), 100);

    const handleTab = (e: KeyboardEvent) => {
      if (e.key !== 'Tab') return;

      if (e.shiftKey) {
        if (document.activeElement === firstFocusable) {
          e.preventDefault();
          lastFocusable.focus();
        }
      } else {
        if (document.activeElement === lastFocusable) {
          e.preventDefault();
          firstFocusable.focus();
        }
      }
    };

    drawer.addEventListener('keydown', handleTab);
    return () => drawer.removeEventListener('keydown', handleTab);
  }, [open]);

  if (!open) return null;

  const drawerStyle: React.CSSProperties = customSize
    ? {
        ...(position === 'left' || position === 'right' ? { width: customSize } : { height: customSize }),
        zIndex: zIndex + 1,
      }
    : { zIndex: zIndex + 1 };

  const content = (
    <div className={styles.container} style={{ zIndex }}>
      {overlay && (
        <div
          className={styles.overlay}
          onClick={closeOnOverlayClick ? onClose : undefined}
          aria-hidden="true"
        />
      )}

      <div
        ref={drawerRef}
        className={`${styles.drawer} ${styles[position]} ${styles[size]} ${className}`}
        style={drawerStyle}
        role="dialog"
        aria-modal="true"
        aria-label={typeof title === 'string' ? title : 'Drawer'}
      >
        {(title || showCloseButton || headerActions) && (
          <div className={styles.header}>
            {title && (
              <div className={styles.title}>
                {typeof title === 'string' ? <h2>{title}</h2> : title}
              </div>
            )}

            <div className={styles.headerActions}>
              {headerActions}
              {showCloseButton && (
                <IconButton
                  icon="close"
                  variant="tertiary"
                  size="medium"
                  onClick={onClose}
                  aria-label="Close drawer"
                  className={styles.closeButton}
                />
              )}
            </div>
          </div>
        )}

        <div className={styles.body}>
          {children}
        </div>

        {footer && (
          <div className={styles.footer}>
            {footer}
          </div>
        )}
      </div>
    </div>
  );

  return createPortal(content, document.body);
};

Drawer.displayName = 'Drawer';

Dropdown:4.content:
/* Wrapper provides inline context for trigger */
.wrapper {
  display: inline-block;
}

.dropdown {
  /* Position handled by inline styles via Portal */
  --dropdown-align-x: 0%; /* Default no horizontal offset, overridden by alignCenter/alignEnd */
  background-color: var(
    --ink-bg-glass-frost
  ); /* bgColorGlassFrost from Figma - semi-transparent white */
  backdrop-filter: blur(10px); /* Glass frost effect */
  -webkit-backdrop-filter: blur(10px); /* Safari support */
  border: var(--ink-form-border-width-active) solid var(--ink-neutral-fade-10); /* 2px borderColorSubtle from Figma */
  border-radius: var(--ink-radius-size-m); /* 12px - radiusSizeM from Figma */
  box-shadow: var(
    --ink-shadow-elevated
  ); /* shadowElevated from Figma: 0px 4px 8px rgba(19,0,50,0.15) */
  padding: var(--ink-spacing-100); /* 8px all around - gap100 from Figma */
  z-index: var(--ink-z-dropdown);
  animation: dropdownFadeIn var(--ink-transition-fast) forwards;
  min-width: 200px;
  max-width: 380px; /* Increased for boxed icons with descriptions */
  max-height: 400px;
  overflow-y: auto;
  transform: translateX(var(--dropdown-align-x));
}

/* Alignment transforms (applied via JS-calculated left position) */
.alignCenter {
  --dropdown-align-x: -50%;
}

.alignEnd {
  --dropdown-align-x: -100%;
}

/* Section header - matches Figma "fontSectionHeadline" specs */
.header {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px from Figma */
  font-weight: var(--ink-font-weight-semibold); /* 600 from Figma */
  line-height: 1.5; /* Figma spec */
  letter-spacing: 0.16px; /* Figma spec */
  color: var(--ink-font-color-neutral-subtle); /* fontColorSubtle from Figma */
  padding: var(--ink-spacing-100) var(--ink-spacing-200);
  margin-bottom: var(--ink-spacing-50);
}

@keyframes dropdownFadeIn {
  from {
    opacity: 0;
    transform: translateX(var(--dropdown-align-x)) translateY(-4px);
  }
  to {
    opacity: 1;
    transform: translateX(var(--dropdown-align-x)) translateY(0);
  }
}

.item {
  width: 100%;
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-100); /* 8px - gap100 from Figma */
  padding: var(--ink-spacing-100) var(--ink-spacing-100) var(--ink-spacing-100)
    var(--ink-spacing-200); /* 8px 8px 8px 16px from Figma */
  border: none;
  background: none;
  border-radius: var(--ink-radius-size-xs); /* 4px - radiusSizeXS from Figma */
  text-align: left;
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 from Figma */
  line-height: 1.4; /* Exact Figma spec */
  color: var(--ink-font-color-default); /* fontColorDefault from Figma */
  position: relative;
}

.item:hover:not(.disabled),
.item.active {
  background-color: var(--ink-neutral-fade-5); /* Subtle hover from Figma */
}

.item:active:not(.disabled) {
  background-color: var(--ink-neutral-fade-10); /* Active state */
}

.item.disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  width: var(--ink-icon-size-s);
  height: var(--ink-icon-size-s);
  color: var(--ink-icon-color-default);
}

/* Boxed icon style - icons in a background box */
.iconBoxed {
  width: 40px;
  height: 40px;
  background-color: var(--ink-neutral-fade-5);
  border-radius: var(--ink-radius-size-xs);
  color: var(--ink-icon-color-default);
}

/* Items with boxed icons need different alignment */
.item:has(.iconBoxed) {
  align-items: center;
  padding: var(--ink-spacing-100);
  gap: var(--ink-spacing-150);
}

.content {
  flex: 1;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1);
  min-width: 0;
}

.label {
  font-weight: var(--ink-font-weight-regular); /* 400 from Figma */
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Make label bold when there's a description */
.content:has(.description) .label {
  font-weight: var(--ink-font-weight-medium);
}

.description {
  font-size: var(--ink-font-size-xs); /* 12px from Figma */
  font-weight: var(--ink-font-weight-medium); /* 500 from Figma */
  line-height: 1.5; /* Figma spec */
  letter-spacing: 0.16px; /* Figma spec */
  color: var(--ink-font-color-neutral-subtle); /* fontColorSubtle from Figma */
  white-space: normal;
}

.shortcut {
  flex-shrink: 0;
  font-size: var(--ink-font-size-sm); /* 14px from Figma */
  color: var(--ink-font-color-neutral-subtle); /* fontColorSubtle from Figma */
  margin-left: auto;
  padding-left: var(--ink-spacing-3);
}

.arrow {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  color: var(--ink-font-color-neutral-subtle); /* fontColorSubtle from Figma */
  margin-left: auto;
}

/* Checkmark for selected items */
.checkmark {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  color: var(--ink-font-color-default);
  margin-left: auto;
}

/* Selected item state */
.item.selected {
  background-color: transparent;
}

.item.selected .label {
  font-weight: var(--ink-font-weight-medium);
}

.divider {
  height: 1px;
  background-color: var(--ink-neutral-fade-10); /* borderColorSubtle */
  margin: var(--ink-spacing-100) 0; /* 8px top/bottom, 0 left/right since container has padding */
}

/* Scrollbar styling */
.dropdown::-webkit-scrollbar {
  width: 8px;
}

.dropdown::-webkit-scrollbar-track {
  background: transparent;
}

.dropdown::-webkit-scrollbar-thumb {
  background: var(--ink-neutral-30);
  border-radius: var(--ink-radius-full);
}

.dropdown::-webkit-scrollbar-thumb:hover {
  background: var(--ink-neutral-40);
}

import React, { useState, useRef, useEffect, useLayoutEffect } from 'react';
import styles from './Dropdown.module.css';
import { Icon } from '../../3-primitives/Icon';
import { Portal } from '../../2-utilities/Portal';

export type DropdownPosition = 'top' | 'bottom' | 'left' | 'right';
export type DropdownAlign = 'start' | 'center' | 'end';

interface Coords {
  top: number;
  left: number;
}

export interface DropdownItemProps {
  /** Item label */
  label: string;
  /** Item icon */
  icon?: React.ReactNode;
  /** Item description (secondary text) */
  description?: string;
  /** Click handler */
  onClick?: () => void;
  /** Whether the item is disabled */
  disabled?: boolean;
  /** Whether the item is a divider */
  divider?: boolean;
  /** Keyboard shortcut hint */
  shortcut?: string;
  /** Sub-menu items */
  children?: DropdownItemProps[];
  /** Whether this item is selected (shows checkmark) */
  selected?: boolean;
}

export interface DropdownProps {
  /** Menu items */
  items: DropdownItemProps[];
  /** The element that triggers the dropdown */
  children: React.ReactElement;
  /** Position of the dropdown relative to the trigger */
  position?: DropdownPosition;
  /** Alignment of the dropdown */
  align?: DropdownAlign;
  /** Whether the dropdown is open (controlled) */
  open?: boolean;
  /** Default open state (uncontrolled) */
  defaultOpen?: boolean;
  /** Callback when open state changes */
  onOpenChange?: (open: boolean) => void;
  /** Close on item click */
  closeOnItemClick?: boolean;
  /** Section header text (e.g., "Select a view") */
  header?: string;
  /** Show icons with background box styling */
  iconStyle?: 'default' | 'boxed';
  /** Data QA attribute for testing */
  'data-qa'?: string;
}

export const Dropdown: React.FC<DropdownProps> = ({
  items,
  children,
  position = 'bottom',
  align = 'start',
  open: controlledOpen,
  defaultOpen = false,
  onOpenChange,
  closeOnItemClick = true,
  header,
  iconStyle = 'default',
  'data-qa': dataQa,
}) => {
  const [internalOpen, setInternalOpen] = useState(defaultOpen);
  const [activeIndex, setActiveIndex] = useState<number>(-1);
  const [submenuOpenIndex, setSubmenuOpenIndex] = useState<number>(-1);
  const isControlled = controlledOpen !== undefined;
  const open = isControlled ? controlledOpen : internalOpen;

  const wrapperRef = useRef<HTMLDivElement>(null);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const itemRefs = useRef<(HTMLButtonElement | null)[]>([]);

  // Calculate position based on wrapper element
  const calculatePosition = (wrapperEl: HTMLElement): Coords => {
    const triggerRect = wrapperEl.getBoundingClientRect();
    const gap = 4; // Gap between trigger and dropdown

    // Parse combined position values like "bottom-start" into separate position and align
    // This supports both combined format (e.g., "bottom-start") and separate props
    let effectivePosition = position;
    let effectiveAlign = align;

    if (position.includes('-')) {
      const [pos, al] = position.split('-') as [DropdownPosition, DropdownAlign];
      effectivePosition = pos;
      effectiveAlign = al;
    }

    let top = 0;
    let left = 0;

    // Calculate based on position prop
    switch (effectivePosition) {
      case 'bottom':
        top = triggerRect.bottom + gap;
        break;
      case 'top':
        top = triggerRect.top - gap;
        break;
      case 'left':
        left = triggerRect.left - gap;
        top = triggerRect.top;
        break;
      case 'right':
        left = triggerRect.right + gap;
        top = triggerRect.top;
        break;
    }

    // Calculate based on align prop (for top/bottom positions)
    if (effectivePosition === 'top' || effectivePosition === 'bottom') {
      switch (effectiveAlign) {
        case 'start':
          left = triggerRect.left;
          break;
        case 'center':
          left = triggerRect.left + triggerRect.width / 2;
          break;
        case 'end':
          left = triggerRect.right;
          break;
      }
    }

    return { top, left };
  };

  // Position dropdown after it mounts using double RAF to ensure layout is complete
  useLayoutEffect(() => {
    if (!open || !dropdownRef.current || !wrapperRef.current) return;

    // Double RAF ensures the DOM is fully laid out
    requestAnimationFrame(() => {
      requestAnimationFrame(() => {
        if (dropdownRef.current && wrapperRef.current) {
          // Use the trigger button (first child) for accurate positioning
          const triggerElement =
            (wrapperRef.current.firstElementChild as HTMLElement) || wrapperRef.current;
          const coords = calculatePosition(triggerElement);

          dropdownRef.current.style.top = `${coords.top}px`;
          dropdownRef.current.style.left = `${coords.left}px`;
          dropdownRef.current.style.visibility = 'visible';
        }
      });
    });
  }, [open, position, align]);

  const setOpen = (newOpen: boolean) => {
    if (!isControlled) {
      setInternalOpen(newOpen);
    }
    onOpenChange?.(newOpen);
    if (!newOpen) {
      setActiveIndex(-1);
      setSubmenuOpenIndex(-1);
    }
  };

  const handleTriggerClick = (e: React.MouseEvent) => {
    e.stopPropagation();
    setOpen(!open);
    // Call original onClick if it exists
    const childProps = children.props as { onClick?: (e: React.MouseEvent) => void };
    childProps.onClick?.(e);
  };

  const handleItemClick = (item: DropdownItemProps, index: number) => {
    if (item.disabled) return;

    if (item.children && item.children.length > 0) {
      setSubmenuOpenIndex(submenuOpenIndex === index ? -1 : index);
      return;
    }

    item.onClick?.();
    if (closeOnItemClick) {
      setOpen(false);
    }
  };

  const handleKeyDown = (e: React.KeyboardEvent) => {
    const validItems = items.filter((item) => !item.divider && !item.disabled);

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        setActiveIndex((prev) => {
          const next = prev + 1;
          return next >= validItems.length ? 0 : next;
        });
        break;
      case 'ArrowUp':
        e.preventDefault();
        setActiveIndex((prev) => {
          const next = prev - 1;
          return next < 0 ? validItems.length - 1 : next;
        });
        break;
      case 'Enter':
      case ' ':
        e.preventDefault();
        if (activeIndex >= 0 && activeIndex < validItems.length) {
          const actualIndex = items.indexOf(validItems[activeIndex]);
          handleItemClick(validItems[activeIndex], actualIndex);
        }
        break;
      case 'Escape':
        e.preventDefault();
        setOpen(false);
        // Focus the first focusable element in wrapper (the trigger)
        const firstFocusable = wrapperRef.current?.querySelector('button, [tabindex]');
        (firstFocusable as HTMLElement)?.focus();
        break;
    }
  };

  useEffect(() => {
    if (open && activeIndex >= 0 && itemRefs.current[activeIndex]) {
      itemRefs.current[activeIndex]?.focus();
    }
  }, [activeIndex, open]);

  useEffect(() => {
    if (!open) return;

    const handleClickOutside = (e: MouseEvent) => {
      if (
        dropdownRef.current &&
        wrapperRef.current &&
        !dropdownRef.current.contains(e.target as Node) &&
        !wrapperRef.current.contains(e.target as Node)
      ) {
        setOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [open]);

  // Update position on scroll/resize
  useEffect(() => {
    if (!open || !dropdownRef.current || !wrapperRef.current) return;

    const handleUpdate = () => {
      if (dropdownRef.current && wrapperRef.current) {
        const newCoords = calculatePosition(wrapperRef.current);
        dropdownRef.current.style.top = `${newCoords.top}px`;
        dropdownRef.current.style.left = `${newCoords.left}px`;
      }
    };

    window.addEventListener('scroll', handleUpdate, true);
    window.addEventListener('resize', handleUpdate);

    return () => {
      window.removeEventListener('scroll', handleUpdate, true);
      window.removeEventListener('resize', handleUpdate);
    };
  }, [open, position, align]);

  const renderItem = (item: DropdownItemProps, index: number) => {
    if (item.divider) {
      return <div key={`divider-${index}`} className={styles.divider} />;
    }

    const hasSubmenu = item.children && item.children.length > 0;
    const isSubmenuOpen = submenuOpenIndex === index;
    const iconClassName =
      iconStyle === 'boxed' ? `${styles.icon} ${styles.iconBoxed}` : styles.icon;

    return (
      <button
        key={index}
        ref={(el) => {
          itemRefs.current[index] = el;
        }}
        className={`${styles.item} ${item.disabled ? styles.disabled : ''} ${
          activeIndex === index ? styles.active : ''
        } ${item.selected ? styles.selected : ''}`}
        onClick={() => handleItemClick(item, index)}
        disabled={item.disabled}
        type="button"
        role="menuitem"
        aria-selected={item.selected}
      >
        {item.icon && <span className={iconClassName}>{item.icon}</span>}
        <div className={styles.content}>
          <span className={styles.label}>{item.label}</span>
          {item.description && <span className={styles.description}>{item.description}</span>}
        </div>
        {item.shortcut && <span className={styles.shortcut}>{item.shortcut}</span>}
        {hasSubmenu && (
          <span className={styles.arrow}>
            <Icon name="chevron-right" size="small" />
          </span>
        )}
        {item.selected && (
          <span className={styles.checkmark}>
            <Icon name="check" size="small" />
          </span>
        )}
      </button>
    );
  };

  // Clone element to add aria attributes (but NOT onClick - that goes on wrapper)
  const trigger = React.cloneElement(children, {
    'aria-expanded': open,
    'aria-haspopup': 'menu' as const,
  });

  // Build alignment class for transform-based centering
  const alignClass = styles[`align${align.charAt(0).toUpperCase() + align.slice(1)}`] || '';

  return (
    <div
      className={styles.wrapper}
      ref={wrapperRef}
      onClick={handleTriggerClick}
      style={{ cursor: 'pointer' }}
    >
      {trigger}
      {open && (
        <Portal>
          <div
            ref={dropdownRef}
            className={`${styles.dropdown} ${alignClass}`}
            style={{ position: 'fixed', visibility: 'hidden' }}
            role="menu"
            onKeyDown={handleKeyDown}
            data-qa={dataQa}
          >
            {header && <div className={styles.header}>{header}</div>}
            {items.map((item, index) => renderItem(item, index))}
          </div>
        </Portal>
      )}
    </div>
  );
};

Dropdown.displayName = 'Dropdown';

export { Dropdown } from './Dropdown';
export type {
  DropdownProps,
  DropdownItemProps,
  DropdownPosition,
  DropdownAlign,
} from './Dropdown';

FileInput:4.content:
/* Ink Design System - FileInput Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-3);
  font-family: var(--ink-font-family);
  width: 100%;
}

/* Label */
.label {
  font-size: var(--ink-font-size-sm);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  display: block;
}

.labelDisabled {
  opacity: 0.5;
  cursor: not-allowed;
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border-width: 0;
}

.required {
  color: var(--ink-red-60);
}

/* Description */
.description {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
  margin-top: calc(var(--ink-spacing-3) * -0.5);
}

/* Dropzone */
.dropzone {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 160px;
  padding: var(--ink-spacing-8);
  border: 2px dashed var(--ink-neutral-30);
  border-radius: var(--ink-radius-lg);
  background-color: var(--ink-neutral-10);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  outline: none;
}

.dropzone:hover:not(.disabled) {
  border-color: var(--ink-cobalt-60);
  background-color: var(--ink-cobalt-10);
}

.dropzone:focus-visible:not(.disabled) {
  border-color: var(--ink-cobalt-60);
  background-color: var(--ink-cobalt-10);
  box-shadow: 0 0 0 3px var(--ink-cobalt-20);
}

.dropzone.dragging {
  border-color: var(--ink-cobalt-60);
  background-color: var(--ink-cobalt-10);
  border-style: solid;
}

.dropzone.disabled {
  opacity: 0.5;
  cursor: not-allowed;
  background-color: var(--ink-neutral-20);
}

.dropzone.error {
  border-color: var(--ink-red-60);
  background-color: var(--ink-red-10);
}

.input {
  display: none;
}

/* Dropzone Content */
.dropzoneContent {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--ink-spacing-2);
  text-align: center;
  pointer-events: none;
}

.uploadIcon {
  color: var(--ink-neutral-60);
  transition: color var(--ink-transition-fast);
}

.dropzone:hover:not(.disabled) .uploadIcon,
.dropzone.dragging .uploadIcon,
.dropzone:focus-visible:not(.disabled) .uploadIcon {
  color: var(--ink-cobalt-60);
}

.dropzoneText {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
}

.dropzoneHelper {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
  max-width: 400px;
}

/* Error Message */
.errorMessage {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  font-size: var(--ink-font-size-sm);
  color: var(--ink-red-60);
  margin-top: calc(var(--ink-spacing-3) * -0.5);
}

.errorIcon {
  display: flex;
  align-items: center;
  flex-shrink: 0;
}

/* File List */
.fileList {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-2);
}

.fileItem {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-3);
  padding: var(--ink-spacing-3);
  background-color: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-20);
  border-radius: var(--ink-radius-md);
  transition: all var(--ink-transition-fast);
}

.fileItem:hover {
  border-color: var(--ink-neutral-30);
  box-shadow: var(--ink-shadow-sm);
}

/* File Preview */
.filePreview {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: var(--ink-radius-md);
  background-color: var(--ink-neutral-10);
  flex-shrink: 0;
}

.fileIcon {
  color: var(--ink-neutral-60);
}

/* File Info */
.fileInfo {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1);
}

.fileName {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.fileSize {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
}

/* Remove Button */
.removeButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 28px;
  height: 28px;
  padding: 0;
  background-color: transparent;
  border: none;
  border-radius: var(--ink-radius-md);
  color: var(--ink-neutral-60);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  flex-shrink: 0;
}

.removeButton:hover:not(:disabled) {
  background-color: var(--ink-neutral-10);
  color: var(--ink-neutral-100);
}

.removeButton:active:not(:disabled) {
  background-color: var(--ink-neutral-20);
}

.removeButton:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Responsive */
@media (max-width: 640px) {
  .dropzone {
    min-height: 120px;
    padding: var(--ink-spacing-6);
  }

  .dropzoneContent {
    gap: var(--ink-spacing-1);
  }

  .dropzoneText {
    font-size: var(--ink-font-size-sm);
  }

  .filePreview {
    width: 36px;
    height: 36px;
  }
}

import React, { useRef, useState } from 'react';
import styles from './FileInput.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export interface FileInputFile {
  file: File;
  id: string;
}

export interface FileInputProps {
  label: string;
  hideLabel?: boolean;
  description?: string;
  error?: string;
  value: FileInputFile[];
  onChange: (files: FileInputFile[]) => void;
  accept?: string; // e.g., ".pdf,.doc,.docx" or "image/*"
  multiple?: boolean;
  maxSize?: number; // in bytes
  disabled?: boolean;
  required?: boolean;
  id?: string;
  width?: string;
}

export const FileInput = React.forwardRef<HTMLInputElement, FileInputProps>(
  (
    {
      label,
      hideLabel = false,
      description,
      error,
      value,
      onChange,
      accept,
      multiple = false,
      maxSize,
      disabled = false,
      required = false,
      id,
      width,
    },
    ref
  ) => {
    const generatedId = React.useId();
    const fileInputId = id || generatedId;
    const errorId = `${fileInputId}-error`;
    const descriptionId = `${fileInputId}-description`;

    const [isDragging, setIsDragging] = useState(false);
    const inputRef = useRef<HTMLInputElement>(null);
    const dragCounter = useRef(0);

    // Merge refs
    React.useImperativeHandle(ref, () => inputRef.current!);

    const formatFileSize = (bytes: number): string => {
      if (bytes === 0) return '0 Bytes';
      const k = 1024;
      const sizes = ['Bytes', 'KB', 'MB', 'GB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + ' ' + sizes[i];
    };

    const validateFile = (file: File): string | null => {
      if (maxSize && file.size > maxSize) {
        return `File size exceeds ${formatFileSize(maxSize)}`;
      }

      if (accept) {
        const acceptedTypes = accept.split(',').map((type) => type.trim());
        const fileType = file.type;
        const fileName = file.name;
        const fileExtension = '.' + fileName.split('.').pop()?.toLowerCase();

        const isValid = acceptedTypes.some((acceptedType) => {
          if (acceptedType.startsWith('.')) {
            return fileExtension === acceptedType.toLowerCase();
          }
          if (acceptedType.endsWith('/*')) {
            const category = acceptedType.split('/')[0];
            return fileType.startsWith(category + '/');
          }
          return fileType === acceptedType;
        });

        if (!isValid) {
          return `File type not accepted. Allowed: ${accept}`;
        }
      }

      return null;
    };

    const handleFiles = (newFiles: FileList | File[]) => {
      const fileArray = Array.from(newFiles);

      if (!multiple && fileArray.length > 1) {
        return;
      }

      const validFiles: File[] = [];

      for (const file of fileArray) {
        const validationError = validateFile(file);
        if (validationError) {
          // Validation error will be shown in error prop from parent
          return;
        }
        validFiles.push(file);
      }

      const fileInputFiles: FileInputFile[] = validFiles.map((file) => ({
        file,
        id: `${file.name}-${Date.now()}-${Math.random()}`,
      }));

      const newFilesList = multiple ? [...value, ...fileInputFiles] : fileInputFiles;
      onChange(newFilesList);
    };

    const handleRemoveFile = (fileId: string) => {
      const newFiles = value.filter((f) => f.id !== fileId);
      onChange(newFiles);
    };

    const handleDragEnter = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      dragCounter.current += 1;
      if (!disabled) {
        setIsDragging(true);
      }
    };

    const handleDragLeave = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      dragCounter.current -= 1;
      if (dragCounter.current === 0) {
        setIsDragging(false);
      }
    };

    const handleDragOver = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
    };

    const handleDrop = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      setIsDragging(false);
      dragCounter.current = 0;

      if (disabled) return;

      const droppedFiles = e.dataTransfer.files;
      if (droppedFiles.length > 0) {
        handleFiles(droppedFiles);
      }
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const selectedFiles = e.target.files;
      if (selectedFiles && selectedFiles.length > 0) {
        handleFiles(selectedFiles);
      }
      // Reset input value to allow selecting the same file again
      e.target.value = '';
    };

    const handleClick = () => {
      if (!disabled) {
        inputRef.current?.click();
      }
    };

    const getFileIcon = (file: File): 'document' | 'image' | 'file-text' => {
      if (file.type.startsWith('image/')) {
        return 'image';
      }
      if (
        file.type.includes('pdf') ||
        file.type.includes('word') ||
        file.type.includes('document')
      ) {
        return 'document';
      }
      return 'file-text';
    };

    const ariaDescribedBy = [error ? errorId : null, description ? descriptionId : null]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={styles.wrapper} style={{ width }}>
        <label
          htmlFor={fileInputId}
          className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
            disabled ? styles.labelDisabled : ''
          }`}
        >
          {label}
          {required && <span className={styles.required}> *</span>}
        </label>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <div
          className={`${styles.dropzone} ${isDragging ? styles.dragging : ''} ${
            disabled ? styles.disabled : ''
          } ${error ? styles.error : ''}`}
          onDragEnter={handleDragEnter}
          onDragLeave={handleDragLeave}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onClick={handleClick}
          role="button"
          tabIndex={disabled ? -1 : 0}
          aria-describedby={ariaDescribedBy || undefined}
          onKeyDown={(e) => {
            if ((e.key === 'Enter' || e.key === ' ') && !disabled) {
              e.preventDefault();
              handleClick();
            }
          }}
        >
          <input
            ref={inputRef}
            id={fileInputId}
            type="file"
            className={styles.input}
            onChange={handleInputChange}
            accept={accept}
            multiple={multiple}
            disabled={disabled}
            required={required}
            aria-invalid={error ? true : undefined}
            aria-required={required || undefined}
          />

          <div className={styles.dropzoneContent}>
            <Icon name="upload" size={32} className={styles.uploadIcon} />
            <div className={styles.dropzoneText}>
              Click to upload or drag and drop
            </div>
            <div className={styles.dropzoneHelper}>
              {accept
                ? `Accepted formats: ${accept}`
                : 'All file types accepted'}
              {maxSize && `  Max file size: ${formatFileSize(maxSize)}`}
            </div>
          </div>
        </div>

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            <span className={styles.errorIcon}>
              <Icon name="status-error" size="small" />
            </span>
            <span>{error}</span>
          </div>
        )}

        {value.length > 0 && (
          <div className={styles.fileList}>
            {value.map((fileInputFile) => {
              const iconName = getFileIcon(fileInputFile.file);

              return (
                <div key={fileInputFile.id} className={styles.fileItem}>
                  <div className={styles.filePreview}>
                    <Icon name={iconName} size="medium" className={styles.fileIcon} />
                  </div>

                  <div className={styles.fileInfo}>
                    <div className={styles.fileName}>{fileInputFile.file.name}</div>
                    <div className={styles.fileSize}>
                      {formatFileSize(fileInputFile.file.size)}
                    </div>
                  </div>

                  <IconButton
                    icon="close"
                    size="small"
                    variant="tertiary"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleRemoveFile(fileInputFile.id);
                    }}
                    aria-label={`Remove ${fileInputFile.file.name}`}
                    disabled={disabled}
                    className={styles.removeButton}
                  />
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  }
);

FileInput.displayName = 'InkFileInput';

FileUpload:4.content:
/* Ink Design System - FileUpload Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-3);
  font-family: var(--ink-font-family);
  width: 100%;
}

.label {
  font-size: var(--ink-font-size-sm);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  display: block;
}

/* Dropzone */
.dropzone {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  min-height: 200px;
  padding: var(--ink-spacing-8);
  border: 2px dashed var(--ink-neutral-30);
  border-radius: var(--ink-radius-lg);
  background-color: var(--ink-neutral-10);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
}

.dropzone:hover:not(.disabled) {
  border-color: var(--ink-cobalt-60);
  background-color: var(--ink-cobalt-10);
}

.dropzone.dragging {
  border-color: var(--ink-cobalt-60);
  background-color: var(--ink-cobalt-10);
  border-style: solid;
}

.dropzone.disabled {
  opacity: 0.5;
  cursor: not-allowed;
  background-color: var(--ink-neutral-20);
}

.dropzone.error {
  border-color: var(--ink-red-60);
  background-color: var(--ink-red-10);
}

.input {
  display: none;
}

/* Dropzone Content */
.dropzoneContent {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: var(--ink-spacing-3);
  text-align: center;
  pointer-events: none;
}

.uploadIcon {
  color: var(--ink-neutral-60);
  transition: color var(--ink-transition-fast);
}

.dropzone:hover:not(.disabled) .uploadIcon,
.dropzone.dragging .uploadIcon {
  color: var(--ink-cobalt-60);
}

.dropzoneText {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
}

.dropzoneHelper {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
}

.errorText {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-red-60);
  font-weight: var(--ink-font-weight-medium);
}

/* File List */
.fileList {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-3);
}

.fileItem {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-3);
  padding: var(--ink-spacing-3);
  background-color: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-20);
  border-radius: var(--ink-radius-md);
  transition: all var(--ink-transition-fast);
}

.fileItem:hover {
  border-color: var(--ink-neutral-30);
  box-shadow: var(--ink-shadow-sm);
}

/* File Preview */
.filePreview {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 48px;
  border-radius: var(--ink-radius-md);
  background-color: var(--ink-neutral-10);
  flex-shrink: 0;
  overflow: hidden;
}

.fileImage {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.fileIconDefault {
  color: var(--ink-neutral-60);
}

/* File Info */
.fileInfo {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1);
}

.fileName {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-neutral-100);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.fileSize {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
}

.fileError {
  font-size: var(--ink-font-size-sm);
  color: var(--ink-red-60);
  font-weight: var(--ink-font-weight-medium);
}

/* Progress Bar */
.progressBar {
  width: 100%;
  height: 4px;
  background-color: var(--ink-neutral-20);
  border-radius: var(--ink-radius-full);
  overflow: hidden;
  margin-top: var(--ink-spacing-1);
}

.progressFill {
  height: 100%;
  background: linear-gradient(90deg, var(--ink-cobalt-60) 0%, var(--ink-cobalt-50) 100%);
  border-radius: var(--ink-radius-full);
  transition: width 200ms ease-out;
  animation: shimmer 1.5s ease-in-out infinite;
  background-size: 200% 100%;
}

@keyframes shimmer {
  0% {
    background-position: 100% 0;
  }
  100% {
    background-position: -100% 0;
  }
}

/* File Actions */
.fileActions {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-2);
  flex-shrink: 0;
}

.statusIcon {
  flex-shrink: 0;
}

.removeButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 28px;
  height: 28px;
  padding: 0;
  background-color: transparent;
  border: none;
  border-radius: var(--ink-radius-md);
  color: var(--ink-neutral-60);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
}

.removeButton:hover {
  background-color: var(--ink-neutral-10);
  color: var(--ink-neutral-100);
}

.removeButton:active {
  background-color: var(--ink-neutral-20);
}

/* Responsive */
@media (max-width: 640px) {
  .dropzone {
    min-height: 150px;
    padding: var(--ink-spacing-6);
  }

  .dropzoneContent {
    gap: var(--ink-spacing-2);
  }

  .uploadIcon {
    width: 24px;
    height: 24px;
  }

  .dropzoneText {
    font-size: var(--ink-font-size-sm);
  }

  .filePreview {
    width: 40px;
    height: 40px;
  }
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './FileUpload.module.css';
import { Icon } from '@/design-system';
import { IconButton } from '../../3-primitives/IconButton';

export type FileStatus = 'uploading' | 'success' | 'error';

export interface UploadedFile {
  file: File;
  id: string;
  status: FileStatus;
  progress: number;
  error?: string;
}

export interface FileUploadProps {
  /** Callback when files are selected */
  onFilesSelect?: (files: File[]) => void;
  /** Callback when file upload status changes */
  onFileChange?: (files: UploadedFile[]) => void;
  /** Callback when a file is removed */
  onFileRemove?: (fileId: string) => void;
  /** Accepted file types (e.g., 'image/*', '.pdf', etc.) */
  accept?: string;
  /** Maximum file size in bytes */
  maxSize?: number;
  /** Maximum number of files */
  maxFiles?: number;
  /** Whether to allow multiple files */
  multiple?: boolean;
  /** Whether the component is disabled */
  disabled?: boolean;
  /** Custom placeholder text */
  placeholder?: string;
  /** Helper text */
  helperText?: string;
  /** Label */
  label?: string;
  /** Show file list */
  showFileList?: boolean;
  /** Custom className */
  className?: string;
}

const FileUpload = React.forwardRef<HTMLInputElement, FileUploadProps>(
  (
    {
      onFilesSelect,
      onFileChange,
      onFileRemove,
      accept,
      maxSize = 10 * 1024 * 1024, // 10MB default
      maxFiles = 5,
      multiple = true,
      disabled = false,
      placeholder = 'Drag and drop files here or click to browse',
      helperText,
      label,
      showFileList = true,
      className,
    },
    ref
  ) => {
    const [isDragging, setIsDragging] = React.useState(false);
    const [files, setFiles] = React.useState<UploadedFile[]>([]);
    const [error, setError] = React.useState<string>('');
    const inputRef = React.useRef<HTMLInputElement>(null);
    const dragCounter = React.useRef(0);

    React.useImperativeHandle(ref, () => inputRef.current!);

    const formatFileSize = (bytes: number): string => {
      if (bytes === 0) return '0 Bytes';
      const k = 1024;
      const sizes = ['Bytes', 'KB', 'MB', 'GB'];
      const i = Math.floor(Math.log(bytes) / Math.log(k));
      return Math.round((bytes / Math.pow(k, i)) * 100) / 100 + ' ' + sizes[i];
    };

    const validateFile = (file: File): string | null => {
      if (maxSize && file.size > maxSize) {
        return `File size exceeds ${formatFileSize(maxSize)}`;
      }

      if (accept) {
        const acceptedTypes = accept.split(',').map((type) => type.trim());
        const fileType = file.type;
        const fileName = file.name;
        const fileExtension = '.' + fileName.split('.').pop()?.toLowerCase();

        const isValid = acceptedTypes.some((acceptedType) => {
          if (acceptedType.startsWith('.')) {
            return fileExtension === acceptedType.toLowerCase();
          }
          if (acceptedType.endsWith('/*')) {
            const category = acceptedType.split('/')[0];
            return fileType.startsWith(category + '/');
          }
          return fileType === acceptedType;
        });

        if (!isValid) {
          return `File type not accepted. Allowed: ${accept}`;
        }
      }

      return null;
    };

    const handleFiles = (newFiles: FileList | File[]) => {
      setError('');
      const fileArray = Array.from(newFiles);

      if (!multiple && fileArray.length > 1) {
        setError('Only one file allowed');
        return;
      }

      if (files.length + fileArray.length > maxFiles) {
        setError(`Maximum ${maxFiles} files allowed`);
        return;
      }

      const validFiles: File[] = [];
      let hasError = false;

      for (const file of fileArray) {
        const validationError = validateFile(file);
        if (validationError) {
          setError(validationError);
          hasError = true;
          break;
        }
        validFiles.push(file);
      }

      if (hasError) return;

      const uploadedFiles: UploadedFile[] = validFiles.map((file) => ({
        file,
        id: `${file.name}-${Date.now()}-${Math.random()}`,
        status: 'uploading' as FileStatus,
        progress: 0,
      }));

      const newFilesList = multiple ? [...files, ...uploadedFiles] : uploadedFiles;
      setFiles(newFilesList);
      onFilesSelect?.(validFiles);

      // Simulate upload progress
      uploadedFiles.forEach((uploadedFile) => {
        simulateUpload(uploadedFile.id);
      });
    };

    const simulateUpload = (fileId: string) => {
      let progress = 0;
      const interval = setInterval(() => {
        progress += 10;

        setFiles((prevFiles) => {
          const updatedFiles = prevFiles.map((f) => {
            if (f.id === fileId) {
              if (progress >= 100) {
                clearInterval(interval);
                return { ...f, status: 'success' as FileStatus, progress: 100 };
              }
              return { ...f, progress };
            }
            return f;
          });
          onFileChange?.(updatedFiles);
          return updatedFiles;
        });
      }, 200);
    };

    const handleRemoveFile = (fileId: string) => {
      const newFiles = files.filter((f) => f.id !== fileId);
      setFiles(newFiles);
      onFileRemove?.(fileId);
      onFileChange?.(newFiles);
    };

    const handleDragEnter = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      dragCounter.current += 1;
      if (!disabled) {
        setIsDragging(true);
      }
    };

    const handleDragLeave = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      dragCounter.current -= 1;
      if (dragCounter.current === 0) {
        setIsDragging(false);
      }
    };

    const handleDragOver = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
    };

    const handleDrop = (e: React.DragEvent) => {
      e.preventDefault();
      e.stopPropagation();
      setIsDragging(false);
      dragCounter.current = 0;

      if (disabled) return;

      const droppedFiles = e.dataTransfer.files;
      if (droppedFiles.length > 0) {
        handleFiles(droppedFiles);
      }
    };

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const selectedFiles = e.target.files;
      if (selectedFiles && selectedFiles.length > 0) {
        handleFiles(selectedFiles);
      }
      // Reset input value to allow selecting the same file again
      e.target.value = '';
    };

    const handleClick = () => {
      if (!disabled) {
        inputRef.current?.click();
      }
    };

    const getFileIcon = (file: File) => {
      if (file.type.startsWith('image/')) {
        return URL.createObjectURL(file);
      }
      return null;
    };

    return (
      <div className={cn(styles.wrapper, className)}>
        {label && <label className={styles.label}>{label}</label>}

        <div
          className={cn(
            styles.dropzone,
            isDragging && styles.dragging,
            disabled && styles.disabled,
            error && styles.error
          )}
          onDragEnter={handleDragEnter}
          onDragLeave={handleDragLeave}
          onDragOver={handleDragOver}
          onDrop={handleDrop}
          onClick={handleClick}
        >
          <input
            ref={inputRef}
            type="file"
            className={styles.input}
            onChange={handleInputChange}
            accept={accept}
            multiple={multiple}
            disabled={disabled}
          />

          <div className={styles.dropzoneContent}>
            <Icon name="upload" size="lg" className={styles.uploadIcon} />
            <div className={styles.dropzoneText}>{placeholder}</div>
            {helperText && !error && <div className={styles.dropzoneHelper}>{helperText}</div>}
            {error && <div className={styles.errorText}>{error}</div>}
            {maxSize && (
              <div className={styles.dropzoneHelper}>Max file size: {formatFileSize(maxSize)}</div>
            )}
          </div>
        </div>

        {showFileList && files.length > 0 && (
          <div className={styles.fileList}>
            {files.map((uploadedFile) => {
              const fileIcon = getFileIcon(uploadedFile.file);

              return (
                <div key={uploadedFile.id} className={styles.fileItem}>
                  <div className={styles.filePreview}>
                    {fileIcon ? (
                      <img
                        src={fileIcon}
                        alt={uploadedFile.file.name}
                        className={styles.fileImage}
                      />
                    ) : (
                      <Icon name="document" size="medium" className={styles.fileIconDefault} />
                    )}
                  </div>

                  <div className={styles.fileInfo}>
                    <div className={styles.fileName}>{uploadedFile.file.name}</div>
                    <div className={styles.fileSize}>{formatFileSize(uploadedFile.file.size)}</div>

                    {uploadedFile.status === 'uploading' && (
                      <div className={styles.progressBar}>
                        <div
                          className={styles.progressFill}
                          style={{ width: `${uploadedFile.progress}%` }}
                        />
                      </div>
                    )}

                    {uploadedFile.error && (
                      <div className={styles.fileError}>{uploadedFile.error}</div>
                    )}
                  </div>

                  <div className={styles.fileActions}>
                    {uploadedFile.status === 'success' && (
                      <Icon
                        name="status-check"
                        size="small"
                        className={styles.statusIcon}
                        style={{ color: 'var(--ink-green-60)' }}
                      />
                    )}
                    {uploadedFile.status === 'error' && (
                      <Icon
                        name="status-error"
                        size="small"
                        className={styles.statusIcon}
                        style={{ color: 'var(--ink-red-60)' }}
                      />
                    )}
                    <IconButton
                      icon="close"
                      size="small"
                      variant="tertiary"
                      onClick={(e) => {
                        e.stopPropagation();
                        handleRemoveFile(uploadedFile.id);
                      }}
                      aria-label="Remove file"
                      className={styles.removeButton}
                    />
                  </div>
                </div>
              );
            })}
          </div>
        )}
      </div>
    );
  }
);

FileUpload.displayName = 'InkFileUpload';

export { FileUpload };

FilterTag:4.content:
/* Ink Design System - FilterTag - Exact Figma Specs */

/* Base FilterTag */
.filterTag {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 32px;
  max-width: 280px;
  padding: 0 var(--ink-spacing-100) 0 var(--ink-spacing-150); /* 0 8px 0 12px */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4;
  color: var(--ink-font-color-default);
  background: transparent;
  border: 1px solid var(--ink-form-control-border-color-default); /* rgba(19,0,50,0.5) */
  border-radius: var(--ink-radius-size-xs); /* 4px */
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  white-space: nowrap;
  overflow: hidden;
}

/* Label */
.label {
  flex: 1;
  min-width: 0;
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  padding-right: var(--ink-spacing-50); /* 4px gap before trigger */
  text-align: center;
  max-width: 240px;
}

/* Trigger Icon */
.trigger {
  flex-shrink: 0;
  width: 20px;
  height: 20px;
}

/* Hover State - Inactive */
.filterTag:hover:not(.active):not(.disabled) {
  border-color: var(--ink-font-color-default); /* Emphasis border */
}

/* Active State - Inactive (pressed) */
.filterTag:active:not(.active):not(.disabled) {
  background: var(--ink-neutral-fade-15); /* var(--ctaBgColorTertiaryActive) */
  border-width: 2px;
  border-color: var(--ink-font-color-default);
}

/* Active Filter */
.filterTag.active {
  background: var(--ink-neutral-fade-5); /* var(--ctaBgColorTertiaryDefault) */
  border: none;
}

/* Active - Hover */
.filterTag.active:hover:not(.disabled) {
  background: var(--ink-neutral-fade-10); /* var(--ctaBgColorTertiaryHover) */
}

/* Active - Active/Pressed */
.filterTag.active:active:not(.disabled) {
  background: var(--ink-neutral-fade-15); /* var(--ctaBgColorTertiaryActive) */
}

/* Disabled State */
.filterTag.disabled {
  opacity: 0.25;
  cursor: not-allowed;
}

/* Dismissible Variant */
.filterTag.dismissible {
  padding: 0;
  overflow: hidden;
  display: flex;
  border: none;
  background: transparent;
}

/* Tag Button (in dismissible variant) */
.tagButton {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 32px;
  max-width: 232px;
  padding: 0 var(--ink-spacing-150); /* 12px horizontal */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4;
  color: var(--ink-font-color-default);
  background: var(--ink-neutral-fade-5); /* Active background */
  border: none;
  border-radius: 0;
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
  flex: 1;
}

.tagButton .label {
  padding-right: 0;
  max-width: none;
}

/* Dismiss Button */
.dismissButton {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 0;
  background: var(--ink-neutral-fade-5);
  border: none;
  border-left: 1px solid var(--ink-white-100); /* White divider */
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
  flex-shrink: 0;
  color: var(--ink-font-color-default);
}

/* Dismissible Hover States */
.filterTag.dismissible .tagButton:hover:not(:disabled) {
  background: var(--ink-neutral-fade-10); /* var(--ctaBgColorTertiaryHover) */
}

.filterTag.dismissible .tagButton:active:not(:disabled) {
  background: var(--ink-neutral-fade-15); /* var(--ctaBgColorTertiaryActive) */
}

.filterTag.dismissible .dismissButton:hover:not(:disabled) {
  background: var(--ink-neutral-fade-10); /* var(--ctaBgColorTertiaryHover) */
}

.filterTag.dismissible .dismissButton:active:not(:disabled) {
  background: var(--ink-neutral-fade-15); /* var(--ctaBgColorTertiaryActive) */
}

/* Dismissible with border radius */
.filterTag.dismissible {
  border-radius: var(--ink-radius-size-xs); /* 4px */
}

/* Disabled Dismissible */
.filterTag.dismissible.disabled {
  opacity: 0.25;
}

.filterTag.dismissible.disabled .tagButton,
.filterTag.dismissible.disabled .dismissButton {
  cursor: not-allowed;
}

/* Focus States */
.filterTag:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 2px;
}

.filterTag.dismissible .tagButton:focus-visible,
.filterTag.dismissible .dismissButton:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: -2px;
}

import React from 'react';
import styles from './FilterTag.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export interface FilterTagProps {
  /** Label text */
  label: string;
  /** Whether the filter is active */
  active?: boolean;
  /** Whether the tag can be dismissed/removed */
  dismissible?: boolean;
  /** Whether the tag is disabled */
  disabled?: boolean;
  /** Show dropdown trigger icon */
  showTrigger?: boolean;
  /** Click handler for the tag */
  onClick?: () => void;
  /** Dismiss handler (only used when dismissible=true) */
  onDismiss?: () => void;
  /** Additional className */
  className?: string;
}

export const FilterTag = React.forwardRef<HTMLDivElement, FilterTagProps>(
  (
    {
      label,
      active = false,
      dismissible = false,
      disabled = false,
      showTrigger = true,
      onClick,
      onDismiss,
      className,
    },
    ref
  ) => {
    const containerClasses = [
      styles.filterTag,
      active && styles.active,
      dismissible && styles.dismissible,
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const handleTagClick = (e: React.MouseEvent) => {
      if (disabled) return;
      onClick?.();
    };

    const handleDismissClick = (e: React.MouseEvent) => {
      e.stopPropagation();
      if (disabled) return;
      onDismiss?.();
    };

    if (dismissible) {
      return (
        <div ref={ref} className={containerClasses}>
          <button
            className={styles.tagButton}
            onClick={handleTagClick}
            disabled={disabled}
            type="button"
          >
            <span className={styles.label}>{label}</span>
          </button>
          <IconButton
            icon="close"
            size="small"
            variant="tertiary"
            onClick={handleDismissClick}
            disabled={disabled}
            aria-label="Remove filter"
            className={styles.dismissButton}
          />
        </div>
      );
    }

    return (
      <button
        ref={ref}
        className={containerClasses}
        onClick={handleTagClick}
        disabled={disabled}
        type="button"
      >
        <span className={styles.label}>{label}</span>
        {showTrigger && (
          <Icon name="chevron-down" size="small" className={styles.trigger} />
        )}
      </button>
    );
  }
);

FilterTag.displayName = 'FilterTag';

List:4.content:
/* List Container */
.list {
  margin: 0;
  padding: 0;
  list-style: none;
  width: 100%;
}

/* List Variants */
.list.bordered {
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-lg);
  overflow: hidden;
}

.list.divided .listItemWrapper:not(:last-child) .item {
  border-bottom: 1px solid var(--ink-neutral-20);
}

/* List Size Modifiers */
.list.small .item {
  padding: var(--ink-spacing-sm) var(--ink-spacing-md);
  min-height: 40px;
}

.list.medium .item {
  padding: var(--ink-spacing-md) var(--ink-spacing-lg);
  min-height: 56px;
}

.list.large .item {
  padding: var(--ink-spacing-lg) var(--ink-spacing-xl);
  min-height: 72px;
}

/* Hoverable List */
.list.hoverable .item.clickable:hover:not(.disabled) {
  background: var(--ink-neutral-10);
}

/* List Item Wrapper */
.listItemWrapper {
  margin: 0;
  padding: 0;
  list-style: none;
}

/* Ordered list numbering */
.list[role='list'] {
  counter-reset: list-counter;
}

ol.list .listItemWrapper {
  counter-increment: list-counter;
}

ol.list .item::before {
  content: counter(list-counter) '.';
  margin-right: var(--ink-spacing-md);
  color: var(--ink-neutral-60);
  font-weight: var(--ink-font-weight-medium);
}

/* List Item */
.item {
  display: flex;
  align-items: center;
  width: 100%;
  padding: var(--ink-spacing-md) var(--ink-spacing-lg);
  background: transparent;
  border: none;
  text-align: left;
  font-family: inherit;
  font-size: var(--ink-font-size-md);
  color: var(--ink-neutral-100);
  transition: background-color 0.2s;
  position: relative;
}

/* Alignment Variants */
.item.align-top {
  align-items: flex-start;
}

.item.align-center {
  align-items: center;
}

.item.align-bottom {
  align-items: flex-end;
}

/* Clickable Item */
.item.clickable {
  cursor: pointer;
  user-select: none;
}

.item.clickable:hover:not(.disabled) {
  background: var(--ink-neutral-10);
}

.item.clickable:active:not(.disabled) {
  background: var(--ink-neutral-20);
}

.item.clickable:focus {
  outline: 2px solid var(--ink-cobalt-60);
  outline-offset: -2px;
}

/* Selected Item */
.item.selected {
  background: var(--ink-cobalt-10);
  color: var(--ink-cobalt-100);
}

.item.selected:hover:not(.disabled) {
  background: var(--ink-cobalt-20);
}

.item.selected::before {
  content: '';
  position: absolute;
  left: 0;
  top: 0;
  bottom: 0;
  width: 3px;
  background: var(--ink-cobalt-60);
}

/* Disabled Item */
.item.disabled {
  opacity: 0.5;
  cursor: not-allowed;
  pointer-events: none;
}

/* Item Elements */
.startElement {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-right: var(--ink-spacing-md);
  flex-shrink: 0;
  color: var(--ink-neutral-70);
}

.endElement {
  display: flex;
  align-items: center;
  justify-content: center;
  margin-left: var(--ink-spacing-md);
  flex-shrink: 0;
  color: var(--ink-neutral-70);
}

/* Content Area */
.content {
  flex: 1;
  min-width: 0;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-xs);
}

.primary {
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-regular);
  line-height: var(--ink-line-height-normal);
  color: inherit;
  word-break: break-word;
}

.description {
  font-size: var(--ink-font-size-sm);
  font-weight: var(--ink-font-weight-regular);
  line-height: var(--ink-line-height-normal);
  color: var(--ink-neutral-60);
  word-break: break-word;
}

/* Meta */
.meta {
  margin-left: auto;
  padding-left: var(--ink-spacing-md);
  flex-shrink: 0;
  font-size: var(--ink-font-size-sm);
  color: var(--ink-neutral-60);
  display: flex;
  align-items: center;
}

/* Icon sizing in list items */
.startElement svg,
.endElement svg {
  width: 20px;
  height: 20px;
}

.list.small .startElement svg,
.list.small .endElement svg {
  width: 16px;
  height: 16px;
}

.list.large .startElement svg,
.list.large .endElement svg {
  width: 24px;
  height: 24px;
}

/* Avatar in list items */
.startElement > img,
.startElement > [class*='avatar'] {
  width: 32px;
  height: 32px;
  border-radius: 50%;
}

.list.small .startElement > img,
.list.small .startElement > [class*='avatar'] {
  width: 24px;
  height: 24px;
}

.list.large .startElement > img,
.list.large .startElement > [class*='avatar'] {
  width: 40px;
  height: 40px;
}

/* Responsive */
@media (max-width: 640px) {
  .item {
    padding: var(--ink-spacing-sm) var(--ink-spacing-md);
  }

  .list.large .item {
    padding: var(--ink-spacing-md) var(--ink-spacing-lg);
  }

  .meta {
    display: none;
  }

  .content {
    gap: var(--ink-spacing-25);
  }
}

import React from 'react';
import styles from './List.module.css';

export type ListVariant = 'default' | 'bordered' | 'divided';
export type ListSize = 'small' | 'medium' | 'large';
export type ListItemAlign = 'top' | 'center' | 'bottom';

// List Item Props
export interface ListItemProps {
  /** Primary content */
  children: React.ReactNode;
  /** Secondary text/description */
  description?: React.ReactNode;
  /** Meta text (shown on the right) */
  meta?: React.ReactNode;
  /** Start element (icon, avatar, etc.) */
  startElement?: React.ReactNode;
  /** End element (action, badge, etc.) */
  endElement?: React.ReactNode;
  /** Whether the item is clickable */
  clickable?: boolean;
  /** Whether the item is selected */
  selected?: boolean;
  /** Whether the item is disabled */
  disabled?: boolean;
  /** Click handler */
  onClick?: () => void;
  /** Alignment of content */
  align?: ListItemAlign;
  /** Additional className */
  className?: string;
}

// List Props
export interface ListProps {
  /** List items */
  children: React.ReactNode;
  /** Visual variant */
  variant?: ListVariant;
  /** Size of list items */
  size?: ListSize;
  /** Whether list is ordered (numbered) */
  ordered?: boolean;
  /** Whether to show hover state on items */
  hoverable?: boolean;
  /** Additional className */
  className?: string;
  /** Role for accessibility */
  role?: string;
}

// List Item Component
export const ListItem: React.FC<ListItemProps> = ({
  children,
  description,
  meta,
  startElement,
  endElement,
  clickable = false,
  selected = false,
  disabled = false,
  onClick,
  align = 'center',
  className = '',
}) => {
  const Component = clickable ? 'button' : 'div';

  const handleClick = () => {
    if (!disabled && onClick) {
      onClick();
    }
  };

  return (
    <Component
      className={`
        ${styles.item}
        ${styles[`align-${align}`]}
        ${clickable ? styles.clickable : ''}
        ${selected ? styles.selected : ''}
        ${disabled ? styles.disabled : ''}
        ${className}
      `.trim()}
      onClick={handleClick}
      disabled={disabled && clickable}
      type={clickable ? 'button' : undefined}
      role={clickable ? 'button' : undefined}
      aria-selected={selected ? true : undefined}
      aria-disabled={disabled ? true : undefined}
    >
      {startElement && (
        <div className={styles.startElement}>
          {startElement}
        </div>
      )}

      <div className={styles.content}>
        <div className={styles.primary}>
          {children}
        </div>
        {description && (
          <div className={styles.description}>
            {description}
          </div>
        )}
      </div>

      {meta && (
        <div className={styles.meta}>
          {meta}
        </div>
      )}

      {endElement && (
        <div className={styles.endElement}>
          {endElement}
        </div>
      )}
    </Component>
  );
};

// List Component
export const List: React.FC<ListProps> & {
  Item: typeof ListItem;
} = ({
  children,
  variant = 'default',
  size = 'medium',
  ordered = false,
  hoverable = false,
  className = '',
  role = 'list',
}) => {
  const Component = ordered ? 'ol' : 'ul';

  return (
    <Component
      className={`
        ${styles.list}
        ${styles[variant]}
        ${styles[size]}
        ${hoverable ? styles.hoverable : ''}
        ${className}
      `.trim()}
      role={role}
    >
      {React.Children.map(children, (child, index) => {
        if (React.isValidElement(child)) {
          // If it's already a list item, wrap it in <li>
          if (child.type === ListItem || child.type === 'li') {
            return <li className={styles.listItemWrapper}>{child}</li>;
          }
          // For other elements, wrap them directly
          return <li className={styles.listItemWrapper}>{child}</li>;
        }
        // For text or other content, wrap in a basic list item
        return (
          <li className={styles.listItemWrapper}>
            <div className={styles.item}>{child}</div>
          </li>
        );
      })}
    </Component>
  );
};

// Attach Item component to List
List.Item = ListItem;

// Display names for dev tools
List.displayName = 'List';
ListItem.displayName = 'List.Item';

Modal:4.content:
/* Ink Design System - Modal - Exact Figma Specs */
.overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--ink-bg-scrim); /* bgColorScrim from Figma */
  backdrop-filter: blur(4px);
  -webkit-backdrop-filter: blur(4px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: var(--ink-z-modal);
  padding: 80px 0; /* 80px top/bottom padding from Figma */
  animation: fadeIn var(--ink-transition-fast);
}

@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

/* Contained mode - positions within parent container instead of viewport */
.contained {
  position: absolute;
}

.modal {
  background: var(--ink-white-100); /* #ffffff */
  border-radius: var(--ink-radius-size-l); /* 16px - radiusSizeL from Figma */
  box-shadow: var(--ink-shadow-xl);
  display: flex;
  flex-direction: column;
  max-height: calc(100vh - 160px); /* Account for 80px top/bottom padding */
  width: 100%;
  position: relative;
  animation: slideUp var(--ink-transition-normal);
  outline: none;
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

/* Size variants - From Figma */
.small {
  max-width: 480px;
  min-width: 480px;
}

.medium {
  max-width: 480px;
  min-width: 480px;
}

.large {
  max-width: 640px;
  min-width: 640px;
}

.xlarge {
  max-width: 800px;
  min-width: 800px;
}

/* Header - From Figma: 32px top, 16px bottom, 32px horizontal, no border */
.header {
  display: flex;
  align-items: center;
  padding: var(--ink-spacing-400) var(--ink-spacing-400) var(--ink-spacing-200)
    var(--ink-spacing-400); /* 32px 32px 16px 32px */
  position: relative;
}

.title {
  margin: 0;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-heading-s-size); /* 24px fontHeadingS from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-heading-s-line-height); /* 1.25 */
  letter-spacing: -0.16px; /* cozy tracking from Figma */
  color: var(--ink-neutral-fade-90); /* fontColorDefault rgba(19,0,50,0.9) */
  flex: 1;
}

/* Close button - Positioned absolutely in top-right corner */
.closeButton {
  position: absolute;
  top: var(--ink-spacing-200); /* 16px from top */
  right: var(--ink-spacing-200); /* 16px from right */
  background: transparent;
  border: none;
  padding: var(--ink-spacing-100); /* 8px */
  cursor: pointer;
  color: var(--ink-neutral-fade-90);
  border-radius: var(--ink-radius-sm); /* 4px */
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  transition: all var(--ink-transition-fast);
}

.closeButton:hover {
  background-color: var(--ink-neutral-fade-5);
}

.closeButton:active {
  background-color: var(--ink-neutral-fade-10);
}

/* Body - From Figma: 32px horizontal padding, no top/bottom padding */
.body {
  padding: 0 var(--ink-spacing-400); /* 0 32px */
  overflow-y: auto;
  flex: 1;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-s-size); /* 14px fontBodyS from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-body-s-line-height); /* 1.4 */
  color: var(--ink-neutral-fade-90);
}

/* Footer - From Figma: 32px horizontal, 32px bottom, 24px top padding, 16px gap, no border */
.footer {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: var(--ink-spacing-200); /* 16px gap from Figma */
  padding: var(--ink-spacing-300) var(--ink-spacing-400) var(--ink-spacing-400)
    var(--ink-spacing-400); /* 24px 32px 32px 32px */
}

/* Responsive */
@media (max-width: 640px) {
  .overlay {
    padding: 0;
  }

  .modal {
    max-height: 100vh;
    border-radius: 0;
  }
}

import React, { useEffect, useRef } from 'react';
import { createPortal } from 'react-dom';
import styles from './Modal.module.css';
import { IconButton } from '../../3-primitives/IconButton';

export type ModalSize = 'small' | 'medium' | 'large' | 'xlarge';

export interface ModalProps {
  /** Whether the modal is visible */
  open: boolean;
  /** Callback when modal requests to close */
  onClose?: () => void;
  /** Modal title */
  title?: string;
  /** Modal content */
  children: React.ReactNode;
  /** Footer content (typically buttons) */
  footer?: React.ReactNode;
  /** Modal size variant */
  size?: ModalSize;
  /** Whether clicking backdrop closes the modal */
  closeOnBackdropClick?: boolean;
  /** Whether pressing ESC closes the modal */
  closeOnEscape?: boolean;
  /** Custom className for the modal content */
  className?: string;
  /** Data QA attribute for testing */
  'data-qa'?: string;
  /** When true, modal is positioned within its parent container instead of viewport */
  contained?: boolean;
}

export const Modal: React.FC<ModalProps> = ({
  open,
  onClose,
  title,
  children,
  footer,
  size = 'medium',
  closeOnBackdropClick = true,
  closeOnEscape = true,
  className,
  'data-qa': dataQa,
  contained = false,
}) => {
  const modalRef = useRef<HTMLDivElement>(null);
  const previousActiveElement = useRef<HTMLElement | null>(null);

  // Handle ESC key
  useEffect(() => {
    if (!open || !closeOnEscape || !onClose) return;

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        onClose();
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [open, closeOnEscape, onClose]);

  // Handle focus management
  useEffect(() => {
    if (open) {
      previousActiveElement.current = document.activeElement as HTMLElement;
      modalRef.current?.focus();

      // Prevent body scroll
      document.body.style.overflow = 'hidden';
    } else {
      // Restore focus
      previousActiveElement.current?.focus();
      // Restore body scroll
      document.body.style.overflow = '';
    }

    return () => {
      document.body.style.overflow = '';
    };
  }, [open]);

  // Handle backdrop click
  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget && closeOnBackdropClick && onClose) {
      onClose();
    }
  };

  if (!open) return null;

  const overlayClasses = [styles.overlay, contained && styles.contained].filter(Boolean).join(' ');

  const modalContent = (
    <div className={overlayClasses} onClick={handleBackdropClick} data-qa={dataQa}>
      <div
        ref={modalRef}
        className={`${styles.modal} ${styles[size]} ${className || ''}`}
        role="dialog"
        aria-modal="true"
        aria-labelledby={title ? 'modal-title' : undefined}
        tabIndex={-1}
      >
        {title && (
          <div className={styles.header}>
            <h2 id="modal-title" className={styles.title}>
              {title}
            </h2>
            {onClose && (
              <IconButton
                icon="close"
                variant="tertiary"
                size="medium"
                onClick={onClose}
                aria-label="Close modal"
                className={styles.closeButton}
              />
            )}
          </div>
        )}

        <div className={styles.body}>{children}</div>

        {footer && <div className={styles.footer}>{footer}</div>}
      </div>
    </div>
  );

  // When contained, render in place. Otherwise, use portal to escape stacking contexts.
  if (contained) {
    return modalContent;
  }

  return createPortal(modalContent, document.body);
};

Modal.displayName = 'Modal';

