.clearButton {
  position: absolute;
  right: var(--ink-spacing-100);
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--ink-spacing-50);
  background: transparent;
  border: none;
  border-radius: var(--ink-radius-size-xs);
  color: var(--ink-font-color-default);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
}

.clearButton:hover {
  color: var(--ink-font-color-default);
  background-color: var(--ink-neutral-fade-5);
}

.clearButton:active {
  background-color: var(--ink-neutral-fade-10);
}

/* Suggestions dropdown */
.suggestions {
  position: absolute;
  top: calc(100% + var(--ink-spacing-50));
  left: 0;
  right: 0;
  z-index: 1000;
  max-height: 300px;
  overflow-y: auto;
  background-color: var(--ink-white-100);
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-default);
  border-radius: var(--ink-radius-size-xs);
  box-shadow: 0 4px 12px var(--ink-neutral-fade-10);
  list-style: none;
  margin: 0;
  padding: var(--ink-spacing-50);
}

.suggestion {
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-100);
  padding: var(--ink-spacing-100) var(--ink-spacing-150);
  border-radius: var(--ink-radius-size-xs);
  cursor: pointer;
  transition: background-color var(--ink-transition-fast);
}

.suggestion:hover,
.suggestion.selected {
  background-color: var(--ink-neutral-fade-5);
}

.suggestionIcon {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
  width: 20px;
  height: 20px;
  margin-top: var(--ink-spacing-25);
  color: var(--ink-font-color-default);
}

.suggestionContent {
  display: flex;
  flex-direction: column;
  gap: 0;
  min-width: 0;
}

.suggestionLabel {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-font-color-default);
  line-height: 1.4;
}

.suggestionDescription {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs);
  color: var(--ink-font-color-neutral);
  line-height: 1.5;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

/* Scrollbar styling */
.suggestions::-webkit-scrollbar {
  width: 8px;
}

.suggestions::-webkit-scrollbar-track {
  background: transparent;
}

.suggestions::-webkit-scrollbar-thumb {
  background: var(--ink-form-control-border-color-default);
  border-radius: var(--ink-radius-size-full);
}

.suggestions::-webkit-scrollbar-thumb:hover {
  background: var(--ink-font-color-neutral);
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './SearchInput.module.css';
import { Icon } from '../../3-primitives/Icon';
import { IconButton } from '../../3-primitives/IconButton';

export interface SearchSuggestion {
  id: string;
  label: string;
  description?: string;
  icon?: React.ReactNode;
}

export interface SearchInputProps extends Omit<
  React.InputHTMLAttributes<HTMLInputElement>,
  'size'
> {
  /** Input value */
  value?: string;
  /** Default value for uncontrolled component */
  defaultValue?: string;
  /** Placeholder text */
  placeholder?: string;
  /** Show loading state */
  loading?: boolean;
  /** Show clear button */
  clearable?: boolean;
  /** Suggestions list */
  suggestions?: SearchSuggestion[];
  /** Callback when value changes */
  onChange?: (value: string) => void;
  /** Callback when clear is clicked */
  onClear?: () => void;
  /** Callback when suggestion is selected */
  onSuggestionSelect?: (suggestion: SearchSuggestion) => void;
  /** Callback when search is submitted */
  onSearch?: (value: string) => void;
  /** Debounce delay in ms */
  debounceMs?: number;
  /** Disabled state */
  disabled?: boolean;
  /** Additional className */
  className?: string;
  /** Custom search icon */
  searchIcon?: React.ReactNode;
  /** Size variant - small (32px) matches button heights */
  size?: 'small' | 'medium';
}

const SearchInput = React.forwardRef<HTMLInputElement, SearchInputProps>(
  (
    {
      value,
      defaultValue,
      placeholder = 'Search...',
      loading = false,
      clearable = true,
      suggestions = [],
      onChange,
      onClear,
      onSuggestionSelect,
      onSearch,
      debounceMs = 0,
      disabled = false,
      className,
      searchIcon,
      size = 'medium',
      ...props
    },
    ref
  ) => {
    const [internalValue, setInternalValue] = React.useState(defaultValue || '');
    const [showSuggestions, setShowSuggestions] = React.useState(false);
    const [selectedIndex, setSelectedIndex] = React.useState(-1);
    const debounceTimer = React.useRef<NodeJS.Timeout>();
    const containerRef = React.useRef<HTMLDivElement>(null);

    const isControlled = value !== undefined;
    const currentValue = isControlled ? value : internalValue;

    // Click outside to close suggestions
    React.useEffect(() => {
      const handleClickOutside = (event: MouseEvent) => {
        if (containerRef.current && !containerRef.current.contains(event.target as Node)) {
          setShowSuggestions(false);
        }
      };

      document.addEventListener('mousedown', handleClickOutside);
      return () => document.removeEventListener('mousedown', handleClickOutside);
    }, []);

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
      const newValue = e.target.value;

      if (!isControlled) {
        setInternalValue(newValue);
      }

      if (debounceMs > 0) {
        if (debounceTimer.current) {
          clearTimeout(debounceTimer.current);
        }
        debounceTimer.current = setTimeout(() => {
          onChange?.(newValue);
        }, debounceMs);
      } else {
        onChange?.(newValue);
      }

      setShowSuggestions(true);
      setSelectedIndex(-1);
    };

    const handleClear = () => {
      if (!isControlled) {
        setInternalValue('');
      }
      onChange?.('');
      onClear?.();
      setShowSuggestions(false);
    };

    const handleSuggestionClick = (suggestion: SearchSuggestion) => {
      if (!isControlled) {
        setInternalValue(suggestion.label);
      }
      onChange?.(suggestion.label);
      onSuggestionSelect?.(suggestion);
      setShowSuggestions(false);
    };

    const handleKeyDown = (e: React.KeyboardEvent<HTMLInputElement>) => {
      if (suggestions.length === 0) {
        if (e.key === 'Enter' && onSearch) {
          e.preventDefault();
          onSearch(currentValue);
        }
        return;
      }

      switch (e.key) {
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex((prev) => (prev < suggestions.length - 1 ? prev + 1 : prev));
          break;
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex((prev) => (prev > 0 ? prev - 1 : -1));
          break;
        case 'Enter':
          e.preventDefault();
          if (selectedIndex >= 0) {
            handleSuggestionClick(suggestions[selectedIndex]);
          } else if (onSearch) {
            onSearch(currentValue);
            setShowSuggestions(false);
          }
          break;
        case 'Escape':
          setShowSuggestions(false);
          setSelectedIndex(-1);
          break;
      }
    };

    const defaultSearchIcon = <Icon name="search" size="small" />;

    return (
      <div ref={containerRef} className={cn(styles.container, className)}>
        <div className={cn(styles.inputWrapper, styles[size], disabled && styles.disabled)}>
          <span className={styles.searchIcon}>{searchIcon || defaultSearchIcon}</span>
          <input
            ref={ref}
            type="text"
            className={styles.input}
            value={currentValue}
            placeholder={placeholder}
            disabled={disabled}
            onChange={handleChange}
            onKeyDown={handleKeyDown}
            onFocus={() => setShowSuggestions(true)}
            {...props}
          />
          {loading && (
            <span className={styles.loadingIcon}>
              <svg className={styles.spinner} viewBox="0 0 24 24">
                <circle
                  cx="12"
                  cy="12"
                  r="10"
                  stroke="currentColor"
                  strokeWidth="3"
                  fill="none"
                  opacity="0.25"
                />
                <path fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4z" />
              </svg>
            </span>
          )}
          {clearable && currentValue && !loading && (
            <IconButton
              icon="close"
              size="small"
              variant="tertiary"
              onClick={handleClear}
              aria-label="Clear search"
              className={styles.clearButton}
            />
          )}
        </div>

        {showSuggestions && suggestions.length > 0 && (
          <ul className={styles.suggestions}>
            {suggestions.map((suggestion, index) => (
              <li
                key={suggestion.id}
                className={cn(styles.suggestion, index === selectedIndex && styles.selected)}
                onClick={() => handleSuggestionClick(suggestion)}
                onMouseEnter={() => setSelectedIndex(index)}
              >
                {suggestion.icon && (
                  <span className={styles.suggestionIcon}>{suggestion.icon}</span>
                )}
                <div className={styles.suggestionContent}>
                  <span className={styles.suggestionLabel}>{suggestion.label}</span>
                  {suggestion.description && (
                    <span className={styles.suggestionDescription}>{suggestion.description}</span>
                  )}
                </div>
              </li>
            ))}
          </ul>
        )}
      </div>
    );
  }
);

SearchInput.displayName = 'InkSearchInput';

export { SearchInput };

Stepper:4.content:
/* Ink Design System - Stepper Component Styles */
@import '../../1-tokens/tokens.css';

.stepper {
  display: flex;
  font-family: var(--ink-font-family);
}

.stepper.horizontal {
  flex-direction: row;
  align-items: flex-start;
  width: 100%;
}

.stepper.vertical {
  flex-direction: column;
  align-items: flex-start;
}

/* Step Container */
.step {
  display: flex;
  position: relative;
  flex: 1;
  min-width: 0;
}

.horizontal .step {
  flex-direction: column;
  align-items: center;
  text-align: center;
  justify-content: center;
}

.horizontal .step:not(:last-child)::after {
  content: '';
  display: block;
  height: 2px;
  position: absolute;
  right: -50%;
  margin-right: var(--ink-spacing-3);
  top: 16px;
  width: calc(100% - 48px);
  background-color: var(--ink-neutral-30);
  transition: background-color var(--ink-transition-fast);
}

.horizontal .completed:not(:last-child)::after {
  background-color: var(--ink-cobalt-20);
}

.vertical .step {
  flex-direction: row;
  align-items: flex-start;
  text-align: left;
  margin-bottom: var(--ink-spacing-6);
}

.vertical .step:last-child {
  margin-bottom: 0;
}

/* Step Indicator */
.stepIndicator {
  display: flex;
  position: relative;
}

.horizontal .stepIndicator {
  flex-direction: column;
  align-items: center;
  width: 100%;
  margin-bottom: var(--ink-spacing-100); /* 8px from Figma */
}

.vertical .stepIndicator {
  flex-direction: column;
  align-items: center;
  margin-right: var(--ink-spacing-150); /* 12px from Figma */
}

/* Step Icon Wrapper */
.stepIconWrapper {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 32px; /* Smaller circle from Figma */
  height: 32px;
  border-radius: var(--ink-radius-full);
  background-color: var(--ink-neutral-20);
  color: var(--ink-neutral-fade-70);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-s-size); /* 14px from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1;
  transition: all var(--ink-transition-fast);
  border: 2px solid transparent;
  flex-shrink: 0;
}

/* Status Styles */
.completed .stepIconWrapper {
  background-color: var(--ink-cobalt-20); /* #e9e6fd - Light purple from Figma */
  color: var(--ink-neutral-140); /* #130032 - Dark text for checkmark */
  border-color: var(--ink-cobalt-20);
}

.active .stepIconWrapper {
  background-color: var(--ink-cobalt-100); /* #4c00fb - filled purple */
  color: var(--ink-white-100); /* White text/number */
  border-color: var(--ink-cobalt-100);
  box-shadow: 0 0 0 4px var(--ink-cobalt-10); /* #f3f2fe - light purple glow */
}

.upcoming .stepIconWrapper {
  background-color: transparent; /* No background for incomplete steps */
  color: var(--ink-neutral-fade-90);
  border-color: var(--ink-neutral-30); /* borderColorSubtle */
}

.error .stepIconWrapper {
  background-color: var(--ink-red-90);
  color: var(--ink-white-100);
  border-color: var(--ink-red-90);
}

.disabled .stepIconWrapper {
  opacity: 0.5;
  cursor: not-allowed;
}

/* Icons */
.stepNumber {
  line-height: 1;
}

.checkIcon,
.errorIcon,
.customIcon {
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Connector Line - Vertical (div-based for vertical orientation) */
.connector {
  position: absolute;
  background-color: var(--ink-neutral-30);
  transition: background-color var(--ink-transition-fast);
}

.horizontal .connector {
  display: none; /* Using ::after pseudo-element for horizontal */
}

.vertical .connector {
  top: 32px; /* Adjusted for 32px circle */
  left: 15px; /* Center of 32px circle minus 1px (half of 2px width) */
  width: 2px;
  height: calc(100% + var(--ink-spacing-6));
}

.completed .connector {
  background-color: var(--ink-cobalt-20); /* Light purple for completed connectors */
}

.step:last-child .connector {
  display: none;
}

/* Step Content */
.stepContent {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-1);
  min-width: 0;
}

.horizontal .stepContent {
  width: 100%;
  padding: 0 var(--ink-spacing-2);
}

.vertical .stepContent {
  flex: 1;
  padding-top: var(--ink-spacing-1);
}

.stepTitle {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-s-size); /* 14px from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-body-s-line-height); /* 1.4 */
  color: var(--ink-neutral-fade-90);
  transition: color var(--ink-transition-fast);
}

.active .stepTitle {
  color: var(--ink-neutral-fade-90);
  font-weight: var(--ink-font-weight-regular); /* Keep regular weight */
}

.completed .stepTitle {
  color: var(--ink-neutral-fade-70); /* fontColorSubtle */
}

.upcoming .stepTitle {
  color: var(--ink-neutral-fade-70);
}

.error .stepTitle {
  color: var(--ink-red-90);
}

.stepDescription {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-detail-s-size); /* 12px from Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-detail-s-line-height); /* 1.5 */
  color: var(--ink-neutral-fade-70);
  transition: color var(--ink-transition-fast);
}

.active .stepDescription {
  color: var(--ink-neutral-70);
}

.upcoming .stepDescription {
  color: var(--ink-neutral-40);
}

.error .stepDescription {
  color: var(--ink-red-70);
}

/* Clickable Steps */
.clickable {
  cursor: pointer;
  user-select: none;
}

.clickable:hover .stepIconWrapper {
  transform: scale(1.05);
  box-shadow: var(--ink-shadow-md);
}

.clickable:focus-visible {
  outline: 2px solid var(--ink-cobalt-100); /* #4c00fb */
  outline-offset: 2px;
  border-radius: var(--ink-radius-md);
}

.clickable:active .stepIconWrapper {
  transform: scale(0.95);
}

.disabled {
  cursor: not-allowed;
  opacity: 0.5;
}

.disabled:hover .stepIconWrapper {
  transform: none;
  box-shadow: none;
}

/* Responsive */
@media (max-width: 768px) {
  .horizontal {
    flex-direction: column;
  }

  .horizontal .step {
    flex-direction: row;
    align-items: flex-start;
    text-align: left;
    margin-bottom: var(--ink-spacing-6);
  }

  .horizontal .stepIndicator {
    flex-direction: column;
    margin-right: var(--ink-spacing-4);
    margin-bottom: 0;
  }

  .horizontal .stepContent {
    padding: var(--ink-spacing-1) 0 0 0;
  }

  .horizontal .connector {
    top: 32px; /* Adjusted for 32px circle */
    left: 15px; /* Center of 32px circle minus 1px */
    width: 2px;
    height: calc(100% + var(--ink-spacing-6));
  }
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './Stepper.module.css';
import { Icon } from '../../3-primitives';

export type StepperOrientation = 'horizontal' | 'vertical';
export type StepStatus = 'completed' | 'active' | 'upcoming' | 'error';

export interface Step {
  /** Unique identifier for the step */
  id: string;
  /** Title of the step */
  title: string;
  /** Optional description */
  description?: string;
  /** Optional icon */
  icon?: React.ReactNode;
  /** Status of the step */
  status?: StepStatus;
  /** Optional custom number/label */
  label?: string | number;
  /** Disable the step */
  disabled?: boolean;
}

export interface StepperProps {
  /** Array of steps */
  steps: Step[];
  /** Current active step index */
  activeStep?: number;
  /** Orientation of the stepper */
  orientation?: StepperOrientation;
  /** Whether completed steps are clickable */
  clickable?: boolean;
  /** Whether to show step descriptions */
  showDescription?: boolean;
  /** Whether to show connector lines */
  showConnector?: boolean;
  /** Callback when a step is clicked */
  onStepClick?: (stepIndex: number, step: Step) => void;
  /** Custom className */
  className?: string;
}

const Stepper = React.forwardRef<HTMLDivElement, StepperProps>((props, ref) => {
  const {
    steps,
    activeStep = 0,
    orientation = 'horizontal',
    clickable = false,
    showDescription = true,
    showConnector = true,
    onStepClick,
    className,
    ...restProps
  } = props;
  const getStepStatus = (index: number, step: Step): StepStatus => {
    if (step.status) return step.status;
    if (index < activeStep) return 'completed';
    if (index === activeStep) return 'active';
    return 'upcoming';
  };

  const handleStepClick = (index: number, step: Step) => {
    if (step.disabled) return;
    const status = getStepStatus(index, step);
    if (clickable && (status === 'completed' || status === 'active')) {
      onStepClick?.(index, step);
    }
  };

  const isStepClickable = (index: number, step: Step): boolean => {
    if (step.disabled) return false;
    const status = getStepStatus(index, step);
    return clickable && (status === 'completed' || status === 'active');
  };

  const renderStepIcon = (index: number, step: Step, status: StepStatus) => {
    if (step.icon) {
      return <span className={styles.customIcon}>{step.icon}</span>;
    }

    if (status === 'completed') {
      return (
        <span className={styles.checkIcon}>
          <Icon name="check" size="small" />
        </span>
      );
    }

    if (status === 'error') {
      return (
        <span className={styles.errorIcon}>
          <Icon name="status-error" size="small" />
        </span>
      );
    }

    return (
      <span className={styles.stepNumber}>{step.label !== undefined ? step.label : index + 1}</span>
    );
  };

  const renderStep = (step: Step, index: number) => {
    const status = getStepStatus(index, step);
    const isClickable = isStepClickable(index, step);

    const stepClasses = cn(
      styles.step,
      styles[status],
      isClickable && styles.clickable,
      step.disabled && styles.disabled
    );

    return (
      <div
        key={step.id}
        className={stepClasses}
        onClick={() => handleStepClick(index, step)}
        role={isClickable ? 'button' : undefined}
        tabIndex={isClickable ? 0 : undefined}
        onKeyDown={(e) => {
          if (isClickable && (e.key === 'Enter' || e.key === ' ')) {
            e.preventDefault();
            handleStepClick(index, step);
          }
        }}
      >
        <div className={styles.stepIndicator}>
          <div className={styles.stepIconWrapper}>{renderStepIcon(index, step, status)}</div>
          {showConnector && index < steps.length - 1 && <div className={styles.connector} />}
        </div>
        <div className={styles.stepContent}>
          <div className={styles.stepTitle}>{step.title}</div>
          {showDescription && step.description && (
            <div className={styles.stepDescription}>{step.description}</div>
          )}
        </div>
      </div>
    );
  };

  return (
    <div ref={ref} className={cn(styles.stepper, styles[orientation], className)} {...restProps}>
      {steps.map((step, index) => renderStep(step, index))}
    </div>
  );
});

Stepper.displayName = 'InkStepper';

export { Stepper };

Table:4.content:
/* Ink Design System - Table - Production Match */
@import '../../1-tokens/tokens.css';

/* ==========================================================================
   Table Wrapper (for action bar + table + pagination)
   ========================================================================== */

.tableWrapper {
  display: flex;
  flex-direction: column;
  width: 100%;
}

/* ==========================================================================
   Container
   ========================================================================== */

.container {
  width: 100%;
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
}

/* Table Base */
.table {
  width: 100%;
  border-collapse: separate;
  border-spacing: 0;
  background: var(--ink-white-100); /* #ffffff */
  font-family: var(--ink-font-family);
}

/* Table Variants */
.table.bordered {
  border: 1px solid var(--ink-neutral-30); /* #e5e4e5 */
  border-radius: var(--ink-radius-md); /* 8px */
  overflow: hidden;
}

.table.striped tbody tr:nth-child(even) {
  background: var(--ink-neutral-10); /* #f7f6f7 */
}

/* Table Sizes - Exact Figma Specs */
/* Header cells - DocuSign uses 49px height, 4px vertical padding */
.th {
  min-height: 49px;
  height: 49px;
  padding: var(--ink-spacing-50) var(--ink-spacing-200); /* 4px vertical, 16px horizontal - matches DocuSign */
}

/* Compact: 48px row height */
.table.small .td {
  height: 48px;
  padding: var(--ink-spacing-50) var(--ink-spacing-200); /* 4px vertical, 16px horizontal */
}

/* Default/Medium: 65px row height - matches DocuSign production */
.table.medium .td {
  height: 65px;
  padding: var(--ink-spacing-50) var(--ink-spacing-200); /* 4px vertical, 16px horizontal */
}

/* Tall/Large: 96px row height */
.table.large .td {
  height: 96px;
  padding: var(--ink-spacing-100) var(--ink-spacing-200); /* 8px vertical, 16px horizontal */
}

/* Table Header - Production Match (light gray) */
.thead {
  background: var(--ink-neutral-10); /* Light gray background - matches production */
}

.th {
  text-align: left;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-semibold); /* 600 */
  line-height: 1.5;
  letter-spacing: 0.12px; /* Matches DocuSign */
  color: var(--ink-font-color-default); /* rgba(19,0,50,0.9) */
  white-space: nowrap;
  position: relative;
  vertical-align: middle;
}

/* Sticky Header */
.table.stickyHeader .thead {
  position: sticky;
  top: 0;
  z-index: 10;
}

/* Header Content */
.headerContent {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50); /* 4px */
}

/* Header Content Alignment - Figma Spec */
.align-left .headerContent {
  justify-content: flex-start;
}

.align-center .headerContent {
  justify-content: center;
}

.align-right .headerContent {
  justify-content: flex-end;
}

/* Sortable Columns */
.th.sortable {
  cursor: pointer;
  user-select: none;
  transition: background-color var(--ink-transition-fast);
}

.th.sortable:hover {
  background: var(--ink-neutral-20); /* #f0eff0 */
}

.sortIcon {
  display: flex;
  align-items: center;
  flex-shrink: 0;
  color: var(--ink-font-color-default);
  transition: color var(--ink-transition-fast);
}

/* Table Body - From Figma */
.tbody {
  background: var(--ink-white-100);
}

.tr {
  border-bottom: 1px solid var(--ink-neutral-fade-5); /* More subtle border - matches production */
  transition: background-color var(--ink-transition-fast);
}

.tbody .tr:last-child {
  border-bottom: none;
}

/* Hoverable Rows - Subtle hover from Figma */
.table.hoverable .tbody .tr:hover {
  background: var(--ink-neutral-10); /* #f7f6f7 */
}

/* Clickable Rows */
.tr.clickable {
  cursor: pointer;
}

/* Selected Rows - Production Match (light purple) */
.tr.selected {
  background: var(--ink-cobalt-10) !important; /* Light purple - matches production */
}

/* Table Cells - Matches DocuSign production */
.td {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px - matches DocuSign */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4; /* ~19.6px at 14px base - matches DocuSign */
  color: var(--ink-font-color-default); /* rgba(19,0,50,0.9) */
  vertical-align: middle;
}

/* Text Alignment */
.align-left {
  text-align: left;
}

.align-center {
  text-align: center;
}

.align-right {
  text-align: right;
}

/* Checkbox Cell - Using design system Checkbox component */
.checkboxCell {
  width: 56px; /* 56px from Figma for checkbox column */
  text-align: center;
  padding-left: var(--ink-spacing-200); /* 16px */
  padding-right: var(--ink-spacing-200); /* 16px */
}

/* Loading State */
.loadingContainer {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--ink-spacing-500); /* 40px */
  min-height: 200px;
  background: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-md); /* 8px */
}

.spinner {
  color: var(--ink-neutral-fade-60); /* rgba(19,0,50,0.6) */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-m-size); /* 14px */
  font-weight: var(--ink-font-weight-regular);
}

/* Empty State */
.emptyContainer {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: var(--ink-spacing-500); /* 40px */
  min-height: 200px;
  background: var(--ink-white-100);
  border: 1px solid var(--ink-neutral-30);
  border-radius: var(--ink-radius-md); /* 8px */
}

.emptyMessage {
  color: var(--ink-neutral-fade-60); /* rgba(19,0,50,0.6) */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-m-size); /* 14px */
  font-weight: var(--ink-font-weight-regular);
  text-align: center;
}

/* Action Cell Styling - For icon buttons */
.actionCell {
  width: 80px;
  text-align: center;
}

.actionButtons {
  display: flex;
  gap: var(--ink-spacing-50); /* 4px */
  justify-content: center;
  align-items: center;
}

.actionButton {
  background: transparent;
  border: none;
  padding: var(--ink-spacing-50); /* 4px */
  cursor: pointer;
  border-radius: var(--ink-radius-sm); /* 4px */
  display: flex;
  align-items: center;
  justify-content: center;
  color: var(--ink-neutral-fade-70);
  transition: all var(--ink-transition-fast);
}

.actionButton:hover {
  background: var(--ink-neutral-20);
  color: var(--ink-neutral-fade-90);
}

.actionButton:active {
  background: var(--ink-neutral-30);
}

/* Responsive Table */
@media (max-width: 768px) {
  .container.responsive {
    border: 0;
  }

  .container.responsive .table,
  .container.responsive .thead,
  .container.responsive .tbody,
  .container.responsive .th,
  .container.responsive .td,
  .container.responsive .tr {
    display: block;
  }

  .container.responsive .thead .tr {
    position: absolute;
    top: -9999px;
    left: -9999px;
  }

  .container.responsive .tr {
    border: 1px solid var(--ink-neutral-30);
    border-radius: var(--ink-radius-md); /* 8px */
    margin-bottom: var(--ink-spacing-200); /* 16px */
    padding: var(--ink-spacing-200); /* 16px */
  }

  .container.responsive .td {
    border: none;
    position: relative;
    padding-left: 50%;
    display: flex;
    align-items: center;
    min-height: 36px;
  }

  .container.responsive .td:before {
    content: attr(data-label);
    position: absolute;
    left: var(--ink-spacing-200); /* 16px */
    width: 45%;
    padding-right: var(--ink-spacing-100); /* 8px */
    white-space: nowrap;
    font-weight: var(--ink-font-weight-medium); /* 500 */
    color: var(--ink-neutral-fade-70);
    text-align: left;
  }

  .container.responsive .checkboxCell {
    padding-left: var(--ink-spacing-200);
  }

  .container.responsive .checkboxCell:before {
    content: 'Select';
  }

  .container.responsive .td.align-center,
  .container.responsive .td.align-right {
    text-align: left;
  }
}

/* Scrollbar Styling - Matching Ink Design */
.container::-webkit-scrollbar {
  height: 8px;
}

.container::-webkit-scrollbar-track {
  background: var(--ink-neutral-10);
  border-radius: var(--ink-radius-sm); /* 4px */
}

.container::-webkit-scrollbar-thumb {
  background: var(--ink-neutral-40);
  border-radius: var(--ink-radius-sm); /* 4px */
}

.container::-webkit-scrollbar-thumb:hover {
  background: var(--ink-neutral-50);
}

