.columnControlCell {
  width: 48px;
  text-align: center;
  padding: 0 var(--ink-spacing-100) !important;
}

.columnControlButton {
  /* No opacity change - matches DocuSign production */
}

/* ==========================================================================
   NEW: Favorite Column
