import React, { useState, useMemo, useCallback } from 'react';
import styles from './TableColumnControl.module.css';
import { Modal } from '../Modal';
import { SearchInput } from '../SearchInput';
import { Switch } from '../../3-primitives/Switch';
import { Button } from '../../3-primitives/Button';
import { Icon } from '../../3-primitives/Icon';
import type { TableColumn, TableColumnControlProps } from './types';

/**
 * TableColumnControl - Modal for managing column visibility and order
 *
 * Features:
 * - Search input to filter columns
 * - Toggle switches for column visibility
 * - Drag handles for reordering columns
 * - Cancel/Save buttons
 * - Reset to initial state
 */
export function TableColumnControl<T = any>({
  open,
  columns,
  initialColumns,
  onSave,
  onCancel,
}: TableColumnControlProps<T>) {
  // Local state for working with columns
  const [workingColumns, setWorkingColumns] = useState<TableColumn<T>[]>(() =>
    columns.map((col, index) => ({
      ...col,
      order: col.order ?? index,
      isVisible: col.isVisible ?? true,
    }))
  );
  const [searchQuery, setSearchQuery] = useState('');
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);
  const [dragOverIndex, setDragOverIndex] = useState<number | null>(null);

  // Reset working columns when modal opens with new columns
  React.useEffect(() => {
    if (open) {
      setWorkingColumns(
        columns.map((col, index) => ({
          ...col,
          order: col.order ?? index,
          isVisible: col.isVisible ?? true,
        }))
      );
      setSearchQuery('');
    }
  }, [open, columns]);

  // Filter columns that can be controlled (not fixed position)
  const controllableColumns = useMemo(() => {
    return workingColumns
      .filter((col) => !col.fixedPosition)
      .sort((a, b) => (a.order ?? 0) - (b.order ?? 0));
  }, [workingColumns]);

  // Filter by search query
  const filteredColumns = useMemo(() => {
    if (!searchQuery.trim()) return controllableColumns;
    const query = searchQuery.toLowerCase();
    return controllableColumns.filter((col) => {
      const headerText = typeof col.header === 'string' ? col.header : col.key;
      return headerText.toLowerCase().includes(query);
    });
  }, [controllableColumns, searchQuery]);

  // Handle visibility toggle
  const handleVisibilityChange = useCallback((columnKey: string, isVisible: boolean) => {
    setWorkingColumns((prev) =>
      prev.map((col) =>
        col.key === columnKey ? { ...col, isVisible } : col
      )
    );
  }, []);

  // Drag and drop handlers
  const handleDragStart = useCallback((index: number) => {
    setDraggedIndex(index);
  }, []);

  const handleDragOver = useCallback((e: React.DragEvent, index: number) => {
    e.preventDefault();
    setDragOverIndex(index);
  }, []);

  const handleDragLeave = useCallback(() => {
    setDragOverIndex(null);
  }, []);

  const handleDrop = useCallback((targetIndex: number) => {
    if (draggedIndex === null || draggedIndex === targetIndex) {
      setDraggedIndex(null);
      setDragOverIndex(null);
      return;
    }

    const sourceColumn = filteredColumns[draggedIndex];
    const targetColumn = filteredColumns[targetIndex];

    if (!sourceColumn || !targetColumn) return;

    // Reorder the columns
    setWorkingColumns((prev) => {
      const newColumns = [...prev];
      const sourceOrder = sourceColumn.order ?? draggedIndex;
      const targetOrder = targetColumn.order ?? targetIndex;

      return newColumns.map((col) => {
        if (col.key === sourceColumn.key) {
          return { ...col, order: targetOrder };
        }
        if (col.key === targetColumn.key) {
          return { ...col, order: sourceOrder };
        }
        return col;
      });
    });

    setDraggedIndex(null);
    setDragOverIndex(null);
  }, [draggedIndex, filteredColumns]);

  const handleDragEnd = useCallback(() => {
    setDraggedIndex(null);
    setDragOverIndex(null);
  }, []);

  // Handle save
  const handleSave = useCallback(() => {
    onSave(workingColumns);
  }, [workingColumns, onSave]);

  // Handle reset
  const handleReset = useCallback(() => {
    if (initialColumns) {
      setWorkingColumns(
        initialColumns.map((col, index) => ({
          ...col,
          order: col.order ?? index,
          isVisible: col.isVisible ?? true,
        }))
      );
    }
  }, [initialColumns]);

  // Check if there are changes from initial
  const hasChanges = useMemo(() => {
    if (!initialColumns) return false;
    return JSON.stringify(workingColumns) !== JSON.stringify(
      initialColumns.map((col, index) => ({
        ...col,
        order: col.order ?? index,
        isVisible: col.isVisible ?? true,
      }))
    );
  }, [workingColumns, initialColumns]);

  // Render footer
  const footer = (
    <div className={styles.footer}>
      {initialColumns && hasChanges && (
        <Button variant="tertiary" onClick={handleReset}>
          Reset
        </Button>
      )}
      <Button variant="secondary" onClick={onCancel}>
        Cancel
      </Button>
      <Button variant="primary" onClick={handleSave}>
        Save
      </Button>
    </div>
  );

  return (
    <Modal
      open={open}
      onClose={onCancel}
      title="Customize columns"
      size="small"
      footer={footer}
    >
      {/* Search */}
      <div className={styles.searchContainer}>
        <SearchInput
          value={searchQuery}
          onChange={setSearchQuery}
          placeholder="Find columns"
          clearable
        />
      </div>

      {/* Column list */}
      <div className={styles.columnList}>
        {filteredColumns.length === 0 ? (
          <div className={styles.emptyState}>No columns found</div>
        ) : (
          filteredColumns.map((column, index) => {
            const headerText = typeof column.header === 'string' ? column.header : column.key;
            const isLocked = column.isVisible === 'locked';
            const isVisible = column.isVisible !== false;
            const isDragging = draggedIndex === index;
            const isDragOver = dragOverIndex === index;
            const canDrag = !searchQuery && !isLocked;

            return (
              <div
                key={column.key}
                className={`
                  ${styles.columnItem}
                  ${isDragging ? styles.dragging : ''}
                  ${isDragOver ? styles.dragOver : ''}
                `.trim()}
                draggable={canDrag}
                onDragStart={() => canDrag && handleDragStart(index)}
                onDragOver={(e) => canDrag && handleDragOver(e, index)}
                onDragLeave={handleDragLeave}
                onDrop={() => canDrag && handleDrop(index)}
                onDragEnd={handleDragEnd}
              >
                <div className={styles.columnItemLeft}>
                  {/* Drag handle */}
                  <div
                    className={`
                      ${styles.dragHandle}
                      ${!canDrag ? styles.disabled : ''}
                    `.trim()}
                  >
                    <Icon name="gripper-bar" size="small" />
                  </div>

                  {/* Column label */}
                  <span className={styles.columnLabel}>{headerText}</span>
                </div>

                <div className={styles.columnItemRight}>
                  {isLocked ? (
                    <span className={styles.lockedBadge}>Locked</span>
                  ) : (
                    <Switch
                      checked={isVisible}
                      onChange={(checked) => handleVisibilityChange(column.key, checked)}
                      aria-label={`Toggle ${headerText} visibility`}
                    />
                  )}
                </div>
              </div>
            );
          })
        )}
      </div>
    </Modal>
  );
}

TableColumnControl.displayName = 'TableColumnControl';

import React, { useCallback, useMemo } from 'react';
import styles from './Table.module.css';
import { Select } from '../../3-primitives/Select';
import { IconButton } from '../../3-primitives/IconButton';
import type { TablePaginationProps } from './types';

/**
 * TablePagination - Enhanced pagination footer for the Table component
 *
 * Features:
 * - Page size dropdown (5, 10, 25, 50)
 * - "X / Page" display
 * - "1-25 of 100" info text
 * - Previous/Next navigation
 */
export function TablePagination({
  config,
  disabled = false,
  'data-qa': dataQa,
}: TablePaginationProps) {
  const {
    currentPage,
    pageSize,
    totalItems,
    pageSizeOptions = [5, 10, 25, 50],
    onPageChange,
    onPageSizeChange,
    showInfo = true,
  } = config;

  // Calculate pagination info
  const totalPages = Math.ceil(totalItems / pageSize);
  const startItem = Math.min((currentPage - 1) * pageSize + 1, totalItems);
  const endItem = Math.min(currentPage * pageSize, totalItems);

  const canGoPrevious = currentPage > 1;
  const canGoNext = currentPage < totalPages;

  // Handle page size change
  const handlePageSizeChange = useCallback(
    (e: React.ChangeEvent<HTMLSelectElement>) => {
      const newPageSize = parseInt(e.target.value, 10);
      onPageSizeChange?.(newPageSize);
      // Reset to page 1 when page size changes
      onPageChange(1);
    },
    [onPageSizeChange, onPageChange]
  );

  // Handle navigation
  const handlePrevious = useCallback(() => {
    if (canGoPrevious) {
      onPageChange(currentPage - 1);
    }
  }, [canGoPrevious, currentPage, onPageChange]);

  const handleNext = useCallback(() => {
    if (canGoNext) {
      onPageChange(currentPage + 1);
    }
  }, [canGoNext, currentPage, onPageChange]);

  // Don't render if no items
  if (totalItems === 0) {
    return null;
  }

  return (
    <div className={styles.paginationFooter} data-qa={dataQa}>
      {/* Left side - Page size selector */}
      <div className={styles.paginationLeft}>
        <div className={styles.paginationPageSize}>
          <Select
            label="Items per page"
            hideLabel
            size="small"
            value={String(pageSize)}
            onChange={handlePageSizeChange}
            disabled={disabled}
            width="100px"
          >
            {pageSizeOptions.map((option) => (
              <option key={option} value={option}>
                {option} / Page
              </option>
            ))}
          </Select>
        </div>
      </div>

      {/* Right side - Info and navigation */}
      <div className={styles.paginationRight}>
        {showInfo && (
          <span className={styles.paginationInfo}>
            {startItem} - {endItem} of {totalItems}
          </span>
        )}

        <div className={styles.paginationNav}>
          <IconButton
            icon="chevron-left"
            variant="tertiary"
            size="small"
            onClick={handlePrevious}
            disabled={disabled || !canGoPrevious}
            aria-label="Previous page"
          />
          <IconButton
            icon="chevron-right"
            variant="tertiary"
            size="small"
            onClick={handleNext}
            disabled={disabled || !canGoNext}
            aria-label="Next page"
          />
        </div>
      </div>
    </div>
  );
}

TablePagination.displayName = 'TablePagination';

// Main Table component
export { Table } from './Table';

// Sub-components
export { TableActionBar } from './TableActionBar';
export { TableColumnControl } from './TableColumnControl';
export { TablePagination } from './TablePagination';

// Types
export type {
  // Core types
  TableProps,
  TableColumn,
  TableSize,
  TableVariant,
  TableAlign,
  SortDirection,
  ColumnVisibility,
  // Pagination types
  TablePaginationConfig,
  // Action bar types
  TableAction,
  TableActionBarProps,
  // Column control types
  ColumnControlSaveData,
  TableColumnControlProps,
  // Pagination component props
  TablePaginationProps,
} from './types';

import React from 'react';

// ============================================================================
// Base Types
// ============================================================================

export type TableSize = 'small' | 'medium' | 'large';
export type TableVariant = 'default' | 'bordered' | 'striped';
export type TableAlign = 'left' | 'center' | 'right';
export type SortDirection = 'asc' | 'desc' | null;

/** Column visibility state: true (visible), false (hidden), 'locked' (visible, cannot toggle) */
export type ColumnVisibility = boolean | 'locked';

// ============================================================================
// Column Definition
// ============================================================================

export interface TableColumn<T = any> {
  /** Unique key for the column */
  key: string;
  /** Display header for the column */
  header: React.ReactNode;
  /** Width of the column (CSS value) */
  width?: string;
  /** Text alignment */
  align?: TableAlign;
  /** Whether column is sortable */
  sortable?: boolean;
  /** Custom cell render function (Figma pattern) - receives the row */
  cell?: (row: T) => React.ReactNode;
  /** Custom render function for cell (legacy pattern) - receives value, row, and index */
  render?: (value: any, row: T, index: number) => React.ReactNode;
  /** Custom sort function */
  sortFn?: (a: T, b: T) => number;
  /** Column visibility (true | false | 'locked') - defaults to true */
  isVisible?: ColumnVisibility;
  /** Column order for reordering (lower = earlier) */
  order?: number;
  /** Lock column to start or end position (won't appear in column control) */
  fixedPosition?: 'start' | 'end';
  /** Tooltip text for column header */
  headerTooltip?: string;
}

// ============================================================================
// Pagination Configuration
// ============================================================================

export interface TablePaginationConfig {
  /** Current page number (1-indexed) */
  currentPage: number;
  /** Number of items per page */
  pageSize: number;
  /** Total number of items */
  totalItems: number;
  /** Available page size options (default: [5, 10, 25, 50]) */
  pageSizeOptions?: number[];
  /** Callback when page changes */
  onPageChange: (page: number) => void;
  /** Callback when page size changes */
  onPageSizeChange?: (pageSize: number) => void;
  /** Pagination variant */
  variant?: 'default' | 'simple';
  /** Show page info text (e.g., "1-25 of 100") */
  showInfo?: boolean;
}

// ============================================================================
// Action Bar Types
// ============================================================================

export interface TableAction {
  /** Action label/text */
  label: string;
  /** Icon name (from Icon component) */
  icon?: string;
  /** Click handler - receives set of selected row keys */
  onClick: (selectedRows: Set<string | number>) => void;
  /** Button variant */
  variant?: 'primary' | 'secondary' | 'tertiary' | 'destructive';
  /** Whether action is disabled */
  disabled?: boolean;
  /** Optional dropdown items for action menu */
  dropdownItems?: Array<{
    label: string;
    onClick: () => void;
    icon?: string;
    disabled?: boolean;
  }>;
}

// ============================================================================
// Column Control Types
// ============================================================================

export interface ColumnControlSaveData<T = any> {
  /** Updated columns with visibility and order changes */
  columns: TableColumn<T>[];
  /** Map of column keys to visibility changes */
  visibilityChanges: Map<string, boolean>;
  /** Map of column keys to new order positions */
  orderChanges: Map<string, number>;
}

// ============================================================================
// Table Props
// ============================================================================

export interface TableProps<T = any> {
  // -------------------------------------------------------------------------
  // Core Props (existing)
  // -------------------------------------------------------------------------

  /** Column definitions */
  columns: TableColumn<T>[];
  /** Data rows */
  data: T[];
  /** Visual variant */
  variant?: TableVariant;
  /** Size variant */
  size?: TableSize;
  /** Whether to show row hover state */
  hoverable?: boolean;
  /** Row click handler */
  onRowClick?: (row: T, index: number) => void;
  /** Loading state */
  loading?: boolean;
  /** Empty state message */
  emptyMessage?: React.ReactNode;
  /** Whether table is responsive (stacks on mobile) */
  responsive?: boolean;
  /** Additional className */
  className?: string;
  /** Sticky header */
  stickyHeader?: boolean;
  /** Max height for scrollable table */
  maxHeight?: string;

  // -------------------------------------------------------------------------
  // Selection Props (existing)
  // -------------------------------------------------------------------------

  /** Whether rows are selectable */
  selectable?: boolean;
  /** Selected row keys (controlled) */
  selectedRows?: Set<string | number>;
  /** Callback when selection changes */
  onSelectionChange?: (selected: Set<string | number>) => void;
  /** Row key extractor */
  getRowKey?: (row: T, index: number) => string | number;

  // -------------------------------------------------------------------------
  // Sorting Props (existing)
  // -------------------------------------------------------------------------

  /** Currently sorted column key */
  sortColumn?: string;
  /** Current sort direction */
  sortDirection?: SortDirection;
  /** Sort change handler */
  onSortChange?: (column: string, direction: SortDirection) => void;

  // -------------------------------------------------------------------------
  // Column Control Props (NEW)
  // -------------------------------------------------------------------------

  /** Show column control icon in header */
  showColumnControl?: boolean;
  /** Callback when columns are saved from column control modal */
  onColumnControlSave?: (data: ColumnControlSaveData<T>) => void;
  /** Initial column definitions for reset functionality */
  initialColumns?: TableColumn<T>[];

  // -------------------------------------------------------------------------
  // Action Bar Props (NEW)
  // -------------------------------------------------------------------------

  /** Actions to show in selection action bar */
  selectionActions?: TableAction[];
  /** Custom content for the action bar (replaces default actions) */
  actionBarContent?: React.ReactNode;

  // -------------------------------------------------------------------------
  // Pagination Props (NEW)
  // -------------------------------------------------------------------------

  /** Pagination configuration */
  pagination?: TablePaginationConfig;

  // -------------------------------------------------------------------------
  // Favorite Column Props (NEW - Optional)
  // -------------------------------------------------------------------------

  /** Enable favorite/star column */
  showFavoriteColumn?: boolean;
  /** Set of favorited row keys */
  favoriteRows?: Set<string | number>;
  /** Callback when favorite state changes */
  onFavoriteChange?: (rowKey: string | number, isFavorite: boolean) => void;

  // -------------------------------------------------------------------------
  // Testing Props
  // -------------------------------------------------------------------------

  /** Data QA attribute for testing */
  'data-qa'?: string;
}

// ============================================================================
// Sub-component Props
// ============================================================================

export interface TableActionBarProps {
  /** Number of selected rows */
  selectedCount: number;
  /** Actions to display */
  actions: TableAction[];
  /** Custom content (replaces actions) */
  customContent?: React.ReactNode;
  /** Whether the bar is visible */
  visible: boolean;
  /** Selected row keys (for action callbacks) */
  selectedRows: Set<string | number>;
}

export interface TableColumnControlProps<T = any> {
  /** Whether modal is open */
  open: boolean;
  /** Column definitions */
  columns: TableColumn<T>[];
  /** Initial columns for reset */
  initialColumns?: TableColumn<T>[];
  /** Save callback */
  onSave: (columns: TableColumn<T>[]) => void;
  /** Cancel callback */
  onCancel: () => void;
}

export interface TablePaginationProps {
  /** Pagination configuration */
  config: TablePaginationConfig;
  /** Whether pagination is disabled */
  disabled?: boolean;
  /** Data QA attribute */
  'data-qa'?: string;
}

