import React, { useState } from 'react';
import styles from './Tabs.module.css';

export interface TabItem {
  id: string;
  label: string;
  content: React.ReactNode;
  disabled?: boolean;
}

export interface TabsProps {
  tabs?: TabItem[];
  items?: TabItem[];  // Support both naming conventions
  defaultTab?: string;
  value?: string;  // Support controlled component
  activeTab?: string;  // Support activeTab as alias for value
  onChange?: (tabId: string) => void;
  onTabChange?: (tabId: string) => void;  // Support onTabChange as alias for onChange
  className?: string;
}

export const Tabs: React.FC<TabsProps> = ({
  tabs,
  items,
  defaultTab,
  value,
  activeTab: activeTabProp,
  onChange,
  onTabChange,
  className,
}) => {
  const tabItems = tabs || items || [];
  const controlledValue = value !== undefined ? value : activeTabProp;
  const [activeTab, setActiveTab] = useState(controlledValue || defaultTab || tabItems[0]?.id);
  const handleChange = onChange || onTabChange;

  const handleTabClick = (tabId: string, disabled?: boolean) => {
    if (disabled) return;
    setActiveTab(tabId);
    handleChange?.(tabId);
  };

  // Update activeTab when controlled value changes
  React.useEffect(() => {
    if (controlledValue !== undefined) {
      setActiveTab(controlledValue);
    }
  }, [controlledValue]);

  const activeTabContent = tabItems.find(tab => tab.id === activeTab)?.content;

  return (
    <div className={`${styles.wrapper} ${className || ''}`}>
      <div className={styles.tabList} role="tablist">
        {tabItems.map((tab) => (
          <button
            key={tab.id}
            role="tab"
            aria-selected={activeTab === tab.id}
            aria-controls={`panel-${tab.id}`}
            id={`tab-${tab.id}`}
            className={`${styles.tab} ${activeTab === tab.id ? styles.active : ''} ${
              tab.disabled ? styles.disabled : ''
            }`}
            onClick={() => handleTabClick(tab.id, tab.disabled)}
            disabled={tab.disabled}
          >
            {tab.label}
          </button>
        ))}
      </div>
      <div
        role="tabpanel"
        id={`panel-${activeTab}`}
        aria-labelledby={`tab-${activeTab}`}
        className={styles.panel}
      >
        {activeTabContent}
      </div>
    </div>
  );
};

Tabs.displayName = 'Tabs';

TaskCard:4.content:
/* Ink Design System - TaskCard Component Styles */
/* Based on Figma: [INK] Task Card */
@import '../../1-tokens/tokens.css';

.taskCard {
  background: var(--ink-bg-color-default);
  border: var(--ink-border-width-xs) solid var(--ink-neutral-fade-10);
  border-radius: var(--ink-radius-size-s); /* 8px - radiusSizeS */
  overflow: hidden;
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-300); /* 24px - gap300 */
  padding: var(--ink-spacing-250); /* 20px - gap250 */
  min-width: 240px;
  width: 352px;
  font-family: var(--ink-font-family);
}

/* Header */
.header {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  gap: var(--ink-spacing-100); /* 8px - gap100 */
  width: 100%;
}

.heading {
  flex: 1;
  font-size: var(--ink-font-size-lg); /* 20px - fontHeadingXS */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  line-height: 1.25;
  color: var(--ink-font-color-default);
  margin: 0;
}

/* Body */
.body {
  width: 100%;
  font-size: var(--ink-font-size-md); /* 14px - fontBodyS */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: 1.4;
  color: var(--ink-font-color-default);
}

/* Footer */
.footer {
  display: flex;
  align-items: center;
  justify-content: flex-end;
  gap: var(--ink-spacing-300); /* 24px - gap300 */
  width: 100%;
}

.metadata {
  flex: 1;
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  line-height: 1.5;
  letter-spacing: var(--ink-letter-spacing-wide); /* 0.16px */
  color: var(--ink-font-color-subtle);
}

import React from 'react';
import { Heading, Text } from '../../3-primitives/Typography';
import { IconButton } from '../../3-primitives/IconButton';
import { Button } from '../../3-primitives/Button';
import styles from './TaskCard.module.css';

export interface TaskCardProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
}

export interface TaskCardHeaderProps extends React.HTMLAttributes<HTMLDivElement> {
  /** The heading text of the TaskCard */
  heading: string;
  /** Shows an icon in the upper end corner for contextual actions */
  showIcon?: boolean;
  /** Icon name to display (defaults to 'overflow-horizontal') */
  icon?: string;
  /** Callback when the icon button is clicked */
  onIconClick?: () => void;
  /** Accessible label for the icon button */
  iconAriaLabel?: string;
}

export interface TaskCardBodyProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
}

export interface TaskCardFooterProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Extra content like metadata to display */
  metadata?: string;
  /** Show the extra content section */
  showMetadata?: boolean;
  /** Primary action button label */
  actionLabel?: string;
  /** Callback when the action button is clicked */
  onActionClick?: () => void;
  /** Custom content for the footer (overrides default structure) */
  children?: React.ReactNode;
}

export const TaskCardHeader: React.FC<TaskCardHeaderProps> = ({
  heading,
  showIcon = false,
  icon = 'overflow-horizontal',
  onIconClick,
  iconAriaLabel = 'More options',
  className,
  ...props
}) => {
  return (
    <div className={`${styles.header} ${className || ''}`} {...props}>
      <Heading level={3} className={styles.heading}>
        {heading}
      </Heading>
      {showIcon && (
        <IconButton
          icon={icon}
          variant="tertiary"
          size="small"
          aria-label={iconAriaLabel}
          onClick={onIconClick}
        />
      )}
    </div>
  );
};

export const TaskCardBody: React.FC<TaskCardBodyProps> = ({ children, className, ...props }) => {
  return (
    <div className={`${styles.body} ${className || ''}`} {...props}>
      {children}
    </div>
  );
};

export const TaskCardFooter: React.FC<TaskCardFooterProps> = ({
  metadata,
  showMetadata = true,
  actionLabel = 'Button',
  onActionClick,
  children,
  className,
  ...props
}) => {
  // If children are provided, render them instead of default structure
  if (children) {
    return (
      <div className={`${styles.footer} ${className || ''}`} {...props}>
        {children}
      </div>
    );
  }

  return (
    <div className={`${styles.footer} ${className || ''}`} {...props}>
      {showMetadata && metadata && (
        <Text size="small" className={styles.metadata}>
          {metadata}
        </Text>
      )}
      <Button kind="secondary" size="small" onClick={onActionClick}>
        {actionLabel}
      </Button>
    </div>
  );
};

export const TaskCard: React.FC<TaskCardProps> & {
  Header: typeof TaskCardHeader;
  Body: typeof TaskCardBody;
  Footer: typeof TaskCardFooter;
} = ({ children, className, ...props }) => {
  return (
    <div className={`${styles.taskCard} ${className || ''}`} {...props}>
      {children}
    </div>
  );
};

TaskCard.Header = TaskCardHeader;
TaskCard.Body = TaskCardBody;
TaskCard.Footer = TaskCardFooter;
TaskCard.displayName = 'TaskCard';

export { TaskCard, TaskCardHeader, TaskCardBody, TaskCardFooter } from './TaskCard';
export type {
  TaskCardProps,
  TaskCardHeaderProps,
  TaskCardBodyProps,
  TaskCardFooterProps,
} from './TaskCard';

AlertBadge:3.content:
@import '../../1-tokens/tokens.css';

.badge {
  display: flex;
  align-items: center;
  justify-content: center;
  border: var(--ink-form-border-width-s) solid var(--ink-border-emphasis-inverse);
  border-radius: var(--ink-radius-full);
  box-sizing: border-box;
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-badge-size);
  font-weight: var(--ink-font-weight-medium);
  line-height: 1.5;
  letter-spacing: 0.16px;
  color: var(--ink-font-color-inverse);
  text-align: center;
  white-space: nowrap;
}

/* Kind variants */
.emphasis {
  background: var(--ink-message-bg-alert);
}

.subtle {
  background: var(--ink-status-bg-subtle-inverse);
}

/* Size variants */
.number {
  min-height: 20px;
  max-height: 20px;
  min-width: 16px;
  padding: 0 4px;
}

.multipleDigits {
  min-height: 20px;
  max-height: 20px;
  min-width: 20px;
  padding: 0 var(--ink-spacing-100);
}

.dot {
  min-height: 12px;
  max-height: 12px;
  min-width: 12px;
  max-width: 12px;
  width: 12px;
  height: 12px;
}

import React from 'react';
import styles from './AlertBadge.module.css';

export type AlertBadgeKind = 'emphasis' | 'subtle';

export interface AlertBadgeProps {
  /** Number to display in the badge (1-99+) */
  value?: number;
  /** Visual style of the badge */
  kind?: AlertBadgeKind;
  /** Show as a dot indicator instead of number */
  dot?: boolean;
  /** Additional className */
  className?: string;
}

export const AlertBadge: React.FC<AlertBadgeProps> = ({
  value = 1,
  kind = 'emphasis',
  dot = false,
  className,
}) => {
  const displayValue = value > 99 ? '99+' : value.toString();
  const isMultipleDigits = value >= 10;

  const badgeClasses = [
    styles.badge,
    styles[kind],
    dot ? styles.dot : isMultipleDigits ? styles.multipleDigits : styles.number,
    className,
  ]
    .filter(Boolean)
    .join(' ');

  if (dot) {
    return <div className={badgeClasses} aria-label="Alert indicator" />;
  }

  return (
    <div className={badgeClasses} aria-label={`${value} alerts`}>
      {displayValue}
    </div>
  );
};

AlertBadge.displayName = 'AlertBadge';

Avatar:3.content:
.avatar {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  background: var(--ink-neutral-20);
