  overflow: hidden;
  flex-shrink: 0;
}

.circle {
  border-radius: var(--ink-radius-size-full);
}

.square {
  border-radius: var(--ink-radius-size-m); /* 12px for square avatars */
}

/* Size variants - exact Figma specs */
.xsmall {
  width: var(--ink-spacing-300);
  height: var(--ink-spacing-300);
  font-size: var(--ink-font-detail-xs-size); /* 10px */
