import React from 'react';
import { iconPaths } from './iconPaths';
import styles from './Icon.module.css';

export type IconName = keyof typeof iconPaths;

export type IconSize = 'small' | 'medium' | number;

export interface IconProps {
  /** Name of the icon to display */
  name: IconName;
  /** Size of the icon: small (16px in 20x20 container), medium (24px in 24x24 container), or custom number */
  size?: IconSize;
  /** Color of the icon (defaults to currentColor) */
  color?: string;
  /** Additional CSS class name */
  className?: string;
  /** Accessible label for the icon */
  'aria-label'?: string;
  /** Whether the icon is decorative (hidden from screen readers) */
  'aria-hidden'?: boolean;
  /** Additional props to pass to the SVG element */
  [key: string]: any;
}

const sizeMap: Record<string, number> = {
  small: 16,
  medium: 24,
};

/**
 * Icon component from the DocuSign Ink Design System
 *
 * @example
 * ```tsx
 * <Icon name="check" size="small" />
 * <Icon name="arrow-left" size="medium" />
 * <Icon name="status-warn" size={32} color="#0066FF" aria-label="Warning" />
 * ```
 */
export const Icon: React.FC<IconProps> = ({
  name,
  size = 'medium',
  color = 'currentColor',
  className,
  'aria-label': ariaLabel,
  'aria-hidden': ariaHidden,
  ...props
}) => {
  const iconData = iconPaths[name];

  if (!iconData) {
    console.warn(`Icon "${name}" not found in iconPaths`);
    return null;
  }

  const sizeValue = typeof size === 'number' ? size : sizeMap[size] || sizeMap.medium;
  const viewBox = iconData.viewBox || '0 0 24 24';

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox={viewBox}
      width={sizeValue}
      height={sizeValue}
      fill={color}
      className={`${styles.icon} ${className || ''}`}
      aria-label={ariaLabel}
      aria-hidden={ariaHidden ?? !ariaLabel}
      focusable="false"
      role={ariaLabel ? 'img' : undefined}
      {...props}
    >
      <path d={iconData.path} />
    </svg>
  );
};

Icon.displayName = 'Icon';

/**
 * Simple demo component to showcase the Icon system
 * This can be used in your app to preview all icons
 */

import { Icon, IconName } from './Icon';
import { iconPaths } from './iconPaths';

export function IconDemo() {
  const allIconNames = Object.keys(iconPaths) as IconName[];

  return (
    <div style={{ padding: '24px', maxWidth: '1200px', margin: '0 auto' }}>
      <h1 style={{ marginBottom: '32px' }}>Ink Design System - Icon Library</h1>

      <div style={{ marginBottom: '48px' }}>
        <h2 style={{ marginBottom: '16px' }}>Size Examples</h2>
        <div style={{ display: 'flex', gap: '24px', alignItems: 'center' }}>
          <div style={{ textAlign: 'center' }}>
            <Icon name="home" size="small" />
            <p style={{ fontSize: '12px', marginTop: '8px' }}>Small (16px)</p>
          </div>
          <div style={{ textAlign: 'center' }}>
            <Icon name="home" size="medium" />
            <p style={{ fontSize: '12px', marginTop: '8px' }}>Medium (20px)</p>
          </div>
          <div style={{ textAlign: 'center' }}>
            <Icon name="home" size="large" />
            <p style={{ fontSize: '12px', marginTop: '8px' }}>Large (24px)</p>
          </div>
        </div>
      </div>

      <div style={{ marginBottom: '48px' }}>
        <h2 style={{ marginBottom: '16px' }}>All Icons ({allIconNames.length} total)</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fill, minmax(100px, 1fr))',
          gap: '16px'
        }}>
          {allIconNames.map((name) => (
            <div
              key={name}
              style={{
                textAlign: 'center',
                padding: '16px',
                border: '1px solid #e0e0e0',
                borderRadius: '8px',
                cursor: 'pointer',
                transition: 'all 0.2s',
              }}
              onMouseEnter={(e) => {
                e.currentTarget.style.borderColor = '#0066FF';
                e.currentTarget.style.backgroundColor = '#f5f9ff';
              }}
              onMouseLeave={(e) => {
                e.currentTarget.style.borderColor = '#e0e0e0';
                e.currentTarget.style.backgroundColor = 'transparent';
              }}
              title={name}
            >
              <Icon name={name} size="large" />
              <p style={{
                fontSize: '10px',
                marginTop: '8px',
                color: '#666',
                wordBreak: 'break-word'
              }}>
                {name}
              </p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export { Icon } from './Icon';
export type { IconProps, IconName, IconSize } from './Icon';
export { AIIcon } from './AIIcon';
export type { AIIconProps, AIIconName, AIIconSize } from './AIIcon';
export { iconPaths } from './iconPaths';
export type { IconPath } from './iconPaths';

IconButton:3.content:
/* Ink Design System - Icon Button */

.iconButton {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  border: 1px solid transparent; /* Production: transparent border */
  border-radius: var(--ink-radius-size-xs); /* 4px */
  cursor: pointer;
  transition-property: background-color, border-color, color;
  transition-duration: 100ms; /* Production: 0.1s */
  transition-timing-function: cubic-bezier(0.33, 0, 0.67, 1); /* Production easing */
  font-family: var(--ink-font-family);
  position: relative;
  user-select: none;
  vertical-align: middle;
  padding: 0; /* Production: padding-block/inline: 0 */
}

.iconButton:focus-visible {
  outline: 2px solid var(--ink-form-control-border-color-active);
  outline-offset: 2px;
}

/* Sizes */
.medium {
  width: 40px;
  height: 40px;
}

.small {
  width: 32px;
  height: 32px;
}

/* Primary variant */
.primary {
  background-color: var(--ink-cta-bg-color-primary-default);
  color: var(--ink-cta-font-color-primary);
}

.primary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-primary-hover);
}

.primary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-primary-active);
}

/* Brand variant */
.brand {
  background-color: var(--ink-cta-bg-color-brand-default);
  color: var(--ink-cta-font-color-brand);
}

.brand:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-brand-hover);
}

.brand:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-brand-active);
}

/* Secondary variant */
.secondary {
  background-color: var(--ink-cta-bg-color-secondary-default);
  border: 1px solid var(--ink-cta-border-color-secondary-default);
  color: var(--ink-cta-font-color-secondary);
}

.secondary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-secondary-hover);
  border-color: var(--ink-cta-border-color-secondary-hover);
}

.secondary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-secondary-active);
  border-color: var(--ink-cta-border-color-secondary-active);
}

/* Tertiary variant - Icon-only tertiary buttons stay transparent */
/* Text buttons use --ink-cta-bg-color-tertiary-default, but icon-only are transparent */
.tertiary {
  background-color: transparent; /* Icon-only tertiary stays transparent */
  color: var(--ink-cta-font-color-tertiary); /* rgba(19, 0, 50, 0.9) */
}

.tertiary:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-tertiary-hover);
  color: var(--ink-cta-font-color-tertiary); /* No color change on hover */
}

.tertiary:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-tertiary-active);
  color: var(--ink-cta-font-color-tertiary); /* No color change on active */
}

/* Danger variant - Figma spec: transparent bg with red icon by default */
.danger {
  background-color: transparent;
  color: var(--ink-cta-bg-color-danger-default); /* Red icon color */
}

.danger:hover:not(:disabled) {
  background-color: var(--ink-cta-bg-color-danger-hover);
  color: var(--ink-cta-font-color-danger); /* White icon on hover */
}

.danger:active:not(:disabled) {
  background-color: var(--ink-cta-bg-color-danger-active);
  color: var(--ink-cta-font-color-danger); /* White icon on active */
}

/* Disabled state */
.iconButton:disabled {
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
}

/* Icon container */
.icon {
  display: flex;
  align-items: center;
  justify-content: center;
  width: var(--ink-icon-size-s);
  height: var(--ink-icon-size-s);
}

.small .icon {
  width: 16px; /* Smaller than icon-size-s for small buttons */
  height: 16px;
}

import React, { forwardRef } from 'react';
import { Icon } from '../Icon';
import styles from './IconButton.module.css';

export interface IconButtonProps extends React.ButtonHTMLAttributes<HTMLButtonElement> {
  /** Icon name from the icon set */
  icon: string;
  /** Button variant/kind */
  variant?: 'primary' | 'brand' | 'secondary' | 'tertiary' | 'danger';
  /** Button size */
  size?: 'small' | 'medium';
  /** Disabled state */
  disabled?: boolean;
  /** Accessible label for the button */
  'aria-label': string;
}

export const IconButton = forwardRef<HTMLButtonElement, IconButtonProps>(
  (
    {
      icon,
      variant = 'tertiary',
      size = 'medium',
      disabled = false,
      className,
      ...props
    },
    ref
  ) => {
    const buttonClasses = [
      styles.iconButton,
      styles[variant],
      styles[size],
      className,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <button
        ref={ref}
        type="button"
        className={buttonClasses}
        disabled={disabled}
        {...props}
      >
        <Icon name={icon} size={size === 'small' ? 'small' : 'medium'} />
      </button>
    );
  }
);

IconButton.displayName = 'IconButton';

Input:3.content:
/* Ink Design System - Input Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50);
  width: 100%;
}

/* Label */
.label {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-default);
  line-height: 1.4; /* Exact Figma spec */
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
}

.label.disabled {
  opacity: var(--ink-opacity-disabled);
}

.required {
  color: var(--ink-form-text-required);
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Description */
.description {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs);
  font-weight: var(--ink-font-weight-medium);
  letter-spacing: 0.16px;
  color: var(--ink-font-color-neutral);
  line-height: var(--ink-line-height-normal);
}

/* Input */
.input {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 16px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  color: var(--ink-font-color-default);
  background: var(--ink-form-control-bg-color-default);
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-default);
  border-radius: var(--ink-radius-sm); /* 4px */
  padding: var(--ink-spacing-100) var(--ink-spacing-200);
  padding-left: var(--ink-spacing-200);
  transition: all var(--ink-transition-fast);
  width: 100%;
  line-height: 1.5; /* Exact Figma spec */
  outline: none;
}

/* Size variants */
.input.medium {
  height: 40px;
}

.input.small {
  height: 32px;
}

.input.multiline {
  height: auto;
  resize: vertical;
  min-height: 80px;
  padding: var(--ink-spacing-100) var(--ink-spacing-200);
}

/* Hover state */
.input:hover:not(:disabled):not(:read-only):not(.error) {
  border-color: var(--ink-form-control-border-color-hover);
}

/* Focus/Active state - 2px border per Figma spec */
.input:focus:not(.error) {
  border: 2px solid var(--ink-cobalt-120);
  /* Adjust padding to compensate for thicker border */
  padding: calc(var(--ink-spacing-100) - 1px) calc(var(--ink-spacing-200) - 1px);
  padding-left: calc(var(--ink-spacing-200) - 1px);
}

/* Error state */
.input.error {
  border-color: var(--ink-form-control-border-color-error);
}

.input.error:focus {
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-error);
  /* Keep same padding for error state */
  padding: var(--ink-spacing-100) var(--ink-spacing-200);
  padding-left: var(--ink-spacing-200);
}

/* Read-only state */
.input:read-only {
  background: var(--ink-form-bg-read);
  border: none;
  padding: var(--ink-spacing-100) var(--ink-spacing-200);
}

/* Disabled state */
.input:disabled {
  background: var(--ink-form-bg-disabled);
  opacity: var(--ink-opacity-disabled);
  cursor: not-allowed;
  border-color: var(--ink-form-control-border-color-default);
}

/* Placeholder */
.input::placeholder {
  color: var(--ink-font-color-placeholder);
  opacity: 1;
}

/* Error message */
.errorMessage {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-error);
  line-height: 1.5; /* Exact Figma spec */
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-50);
}

.errorIcon {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

import React, { forwardRef } from 'react';
import styles from './Input.module.css';
import { Icon } from '../Icon';

export interface InputProps extends Omit<React.InputHTMLAttributes<HTMLInputElement | HTMLTextAreaElement>, 'size'> {
  label: string;
  error?: string;
  description?: string;
  hideLabel?: boolean;
  required?: boolean;
  multiline?: boolean;
  rows?: number;
  size?: 'small' | 'medium';
  width?: string;
}

export const Input = forwardRef<HTMLInputElement | HTMLTextAreaElement, InputProps>(
  (
    {
      label,
      error,
      description,
      hideLabel = false,
      required = false,
      multiline = false,
      rows = 4,
      size = 'medium',
      width,
      className,
      id,
      disabled,
      ...props
    },
    ref
  ) => {
    const generatedId = React.useId();
    const inputId = id || generatedId;
    const errorId = `${inputId}-error`;
    const descriptionId = `${inputId}-description`;

    const ariaDescribedBy = [
      error ? errorId : null,
      description ? descriptionId : null,
    ]
      .filter(Boolean)
      .join(' ');

    const inputClasses = [
      styles.input,
      styles[size],
      error && styles.error,
      disabled && styles.disabled,
      multiline && styles.multiline,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const InputElement = multiline ? 'textarea' : 'input';

    return (
      <div className={styles.wrapper} style={{ width }}>
        <label
          htmlFor={inputId}
          className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
            disabled ? styles.disabled : ''
          }`}
        >
          {label}
          {required && <span className={styles.required}> *</span>}
        </label>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <InputElement
          ref={ref as any}
          id={inputId}
          className={inputClasses}
          aria-describedby={ariaDescribedBy || undefined}
          aria-invalid={error ? true : undefined}
          aria-required={required || undefined}
          disabled={disabled}
          rows={multiline ? rows : undefined}
          {...(props as any)}
        />

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            <span className={styles.errorIcon}>
              <Icon name="status-error" size="small" />
            </span>
            <span>{error}</span>
          </div>
        )}
      </div>
    );
  }
);

Input.displayName = 'Input';

Link:3.content:
/* Ink Design System - Link - Exact Figma Specs */

/* Base Link */
.link {
  font-family: var(--ink-font-family);
  font-weight: var(--ink-font-weight-regular); /* 400 */
  text-decoration: underline;
  text-decoration-skip-ink: none;
  text-underline-position: from-font;
  text-decoration-thickness: auto;
  cursor: pointer;
  transition:
    color var(--ink-transition-fast),
    text-decoration var(--ink-transition-fast);
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-50); /* 4px */
}

/* Size Variants */
.size-medium {
  font-size: var(--ink-font-size-md); /* 16px */
  line-height: 1.5;
}

.size-small {
  font-size: var(--ink-font-size-sm); /* 14px */
  line-height: 1.4;
}

/* Extra small - for "View Job" style links (matches DocuSign) */
.size-xs {
  font-size: var(--ink-font-size-xs); /* 12px */
  line-height: 1.4;
  font-weight: var(--ink-font-weight-medium); /* 500 */
}

/* Discrete Mode - no underline by default, only on hover/active */
.discrete {
  text-decoration: none;
}

.discrete:hover,
.discrete:active {
  text-decoration: underline;
}

/* Discrete mode height for consistent hit area */
.discrete.size-medium {
  height: 40px;
  align-items: center;
}

.discrete.size-small {
  height: 32px;
  align-items: center;
}

/* Kind: Default - Cobalt Colors */
.kind-default {
  color: var(--ink-cobalt-100); /* #4c00fb */
}

.kind-default:hover {
  color: var(--ink-cobalt-110); /* #4200ca */
}

.kind-default:active {
  color: var(--ink-cobalt-120); /* #37039e */
}

.kind-default:visited {
  color: var(--ink-cobalt-140); /* #260559 */
}

/* Kind: Subtle - Production: fontColorSubtle */
.kind-subtle {
  color: var(--ink-neutral-fade-70); /* Production: fontColorSubtle = rgba(19,0,50,0.7) */
}

.kind-subtle:hover {
  color: var(--ink-neutral-fade-60); /* rgba(19,0,50,0.6) */
}

.kind-subtle:active {
  color: var(--ink-neutral-fade-90); /* rgba(19,0,50,0.9) */
}

.kind-subtle:visited {
  color: var(--ink-neutral-fade-60); /* rgba(19,0,50,0.6) */
}

/* Disabled State */
.disabled {
  color: var(--ink-neutral-fade-20); /* rgba(19,0,50,0.2) */
  cursor: not-allowed;
  pointer-events: none;
  text-decoration: none;
}

.disabled:hover,
.disabled:active,
.disabled:visited {
  color: var(--ink-neutral-fade-20);
  text-decoration: none;
}

/* Focus State */
.link:focus-visible {
  outline: 2px solid var(--ink-cobalt-100);
  outline-offset: 2px;
  border-radius: var(--ink-radius-size-xs); /* 4px */
}

import React, { forwardRef } from 'react';
import styles from './Link.module.css';
import { Icon } from '../Icon';

export type LinkKind = 'default' | 'subtle';
export type LinkSize = 'xs' | 'small' | 'medium';

export interface LinkProps
  extends Omit<React.AnchorHTMLAttributes<HTMLAnchorElement>, 'className'> {
  /** Link text or content */
  children: React.ReactNode;
  /** URL to navigate to */
  href?: string;
  /** Link kind/variant */
  kind?: LinkKind;
  /** Link size */
  size?: LinkSize;
  /** Discrete mode - only show underline on hover/active */
  discrete?: boolean;
  /** Whether to show external link icon */
  external?: boolean;
  /** Disabled state */
  disabled?: boolean;
  /** Additional className */
  className?: string;
}

export const Link = forwardRef<HTMLAnchorElement, LinkProps>(
  (
    {
      children,
      href = '#',
      kind = 'default',
      size = 'medium',
      discrete = false,
      external = false,
      disabled = false,
      className,
      target,
      rel,
      onClick,
      ...props
    },
    ref
  ) => {
    const linkClasses = [
      styles.link,
      styles[`kind-${kind}`],
      styles[`size-${size}`],
      discrete && styles.discrete,
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    // If external prop is true and target is not specified, set target to _blank
    const linkTarget = external && !target ? '_blank' : target;

    // If target is _blank, add rel="noopener noreferrer" for security
    const linkRel = linkTarget === '_blank' ? 'noopener noreferrer' : rel;

    const handleClick = (e: React.MouseEvent<HTMLAnchorElement>) => {
      if (disabled) {
        e.preventDefault();
        return;
      }
      onClick?.(e);
    };

    return (
      <a
        ref={ref}
        href={href}
        className={linkClasses}
        target={linkTarget}
        rel={linkRel}
        aria-disabled={disabled}
        onClick={handleClick}
        {...props}
      >
        {children}
        {external && <Icon name="external-link" size="small" />}
      </a>
    );
  }
);

Link.displayName = 'Link';

ProgressBar:3.content:
@import '../../1-tokens/tokens.css';

.container {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50);
  width: 100%;
}

/* Label row with label on left, percentage on right */
.labelRow {
  display: flex;
  justify-content: space-between;
  align-items: flex-start;
  gap: var(--ink-spacing-200);
  width: 100%;
}

.labelText {
  font-family: var(--ink-font-family-default);
  font-size: var(--ink-font-size-14);
  font-weight: var(--ink-font-weight-medium);
  line-height: 1.4;
  color: var(--ink-font-color-default);
  flex: 1 0 0;
}

.percentageText {
  font-family: var(--ink-font-family-default);
  font-size: var(--ink-font-size-14);
  font-weight: var(--ink-font-weight-regular);
  line-height: 1.4;
  color: var(--ink-font-color-default);
  text-align: right;
  flex-shrink: 0;
}

/* Track container */
.track {
  position: relative;
  width: 100%;
  height: var(--ink-bar-width-s);
  background-color: var(--ink-bar-track-default);
  border-radius: var(--ink-radius-full);
  overflow: hidden;
  display: flex;
  align-items: center;
}

/* Fill bar */
.fill {
  height: 100%;
  min-width: 0;
  min-height: 1px;
  transition: width var(--ink-motion-duration-300) var(--ink-motion-curve-default);
}

/* Kind variants */
.info .fill {
  background-color: var(--ink-bar-fill-default);
}

.success .fill {
  background-color: var(--ink-bar-fill-success);
}

/* Stop indicator - appears at the end of the track */
.stopIndicator {
  position: absolute;
  right: 0;
  top: 0;
  width: var(--ink-bar-width-s);
  height: var(--ink-bar-width-s);
  background-color: var(--ink-bar-stop-indicator);
  border-radius: var(--ink-radius-full);
}

/* Indeterminate state */
.trackIndeterminate {
  position: relative;
  width: 100%;
  height: var(--ink-bar-width-s);
  border-radius: 2px;
  overflow: hidden;
}

.trackIndeterminateBackground {
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: var(--ink-bar-width-s);
  background-color: var(--ink-bar-track-default);
  transform: translateY(-50%);
}

.fillIndeterminate {
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: var(--ink-bar-width-s);
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-700);
  transform: translateY(-50%);
  animation: indeterminate var(--ink-motion-duration-2000) var(--ink-motion-curve-default) infinite;
}

.fillIndeterminateBar {
  width: var(--ink-spacing-700);
  height: 100%;
  flex-shrink: 0;
}

.info .fillIndeterminateBar {
  background-color: var(--ink-bar-fill-default);
}

.success .fillIndeterminateBar {
  background-color: var(--ink-bar-fill-success);
}

@keyframes indeterminate {
  0% {
    transform: translateY(-50%) translateX(-100%);
  }
  50% {
    transform: translateY(-50%) translateX(50%);
  }
  100% {
    transform: translateY(-50%) translateX(200%);
  }
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './ProgressBar.module.css';

export type ProgressBarKind = 'info' | 'success';
export type ProgressBarVariant = 'determinate' | 'indeterminate';

export interface ProgressBarProps {
  /** Current progress value (0-100) */
  value?: number;
  /** Maximum value (default: 100) */
  max?: number;
  /** Whether the Progress Bar shows info or success */
  kind?: ProgressBarKind;
  /** Whether the Progress Bar is determinate or indeterminate */
  variant?: ProgressBarVariant;
  /** Show the label and content of the Progress Bar */
  showLabel?: boolean;
  /** Label to display for the Progress Bar */
  label?: string;
  /** Text at the end of the Progress Bar (percentage display) */
  content?: string;
  /** Shows the content/percentage of the Progress Bar */
  showContent?: boolean;
  /** Additional className */
  className?: string;
}

const ProgressBar: React.FC<ProgressBarProps> = ({
  value = 0,
  max = 100,
  kind = 'info',
  variant = 'determinate',
  showLabel = true,
  label = 'Label',
  content,
  showContent = true,
  className,
}) => {
  const percentage = Math.min(Math.max((value / max) * 100, 0), 100);
  const displayContent = content || `${Math.round(percentage)}%`;
  const isIndeterminate = variant === 'indeterminate';

  return (
    <div className={cn(styles.container, className)}>
      {/* Label row - shows for both determinate and indeterminate when showLabel is true */}
      {showLabel && (
        <div className={styles.labelRow}>
          <span className={styles.labelText}>{label}</span>
          {/* Only show percentage for determinate variant */}
          {!isIndeterminate && showContent && (
            <span className={styles.percentageText}>{displayContent}</span>
          )}
        </div>
      )}

      {/* Determinate progress bar */}
      {!isIndeterminate && (
        <div
          className={cn(styles.track, styles[kind])}
          role="progressbar"
          aria-valuenow={value}
          aria-valuemin={0}
          aria-valuemax={max}
          aria-label={label}
        >
          <div className={styles.fill} style={{ width: `${percentage}%` }} />
          {/* Stop indicator at the end of track */}
          <div className={styles.stopIndicator} />
        </div>
      )}

      {/* Indeterminate progress bar */}
      {isIndeterminate && (
        <div
          className={cn(styles.trackIndeterminate, styles[kind])}
          role="progressbar"
          aria-label={label}
        >
          <div className={styles.trackIndeterminateBackground} />
          <div className={styles.fillIndeterminate}>
            <div className={styles.fillIndeterminateBar} />
          </div>
        </div>
      )}
    </div>
  );
};

ProgressBar.displayName = 'InkProgressBar';

export { ProgressBar };

Radio:3.content:
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  /* No gap - description sits directly below label per Figma */
}

.radioWrapper {
  display: flex;
  align-items: center; /* Center-align radio with label text */
  gap: var(--ink-spacing-100); /* 8px - matches Figma gap100 */
  position: relative;
}

/* Disabled state applies to entire wrapper per Figma */
.radioWrapper.disabled {
  opacity: 0.25;
  cursor: not-allowed;
}

/* Control container - 24px to match Figma, provides space for hover bg */
.control {
  position: relative;
  width: 24px;
  height: 24px;
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

/* Hover background circle - extends beyond control per Figma */
.control::before {
  content: '';
  position: absolute;
  inset: -4px; /* Extends 4px beyond 24px container = 32px total */
  border-radius: var(--ink-radius-full);
  background: transparent;
  transition: background-color var(--ink-transition-fast);
}

.radioWrapper:not(.disabled):hover .control::before {
  background: var(
    --ink-neutral-fade-10
  ); /* rgba(19,0,50,0.1) - Figma formHighlightBgColorDefault */
}

.input {
  width: 20px;
  height: 20px;
  margin: 0;
  border: 2px solid var(--ink-form-control-border-color-default); /* rgba(19,0,50,0.5) - Figma formBorderColorDefault */
  border-radius: var(--ink-radius-full);
  background: var(--ink-white-100);
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  flex-shrink: 0;
  appearance: none;
  position: relative;
}

/* Hover state - border color change per Figma */
.radioWrapper:not(.disabled):hover .input {
  border-color: var(--ink-neutral-fade-70); /* rgba(19,0,50,0.7) - Figma formBorderColorHover */
}

/* Active/pressed state per Figma */
.input:active:not(:disabled) {
  border-color: var(--ink-cobalt-100); /* Figma formBorderColorActive */
}

.input:focus {
  outline: none;
  border-color: var(--ink-cobalt-100);
  box-shadow: var(--ink-focus-ring-accent-subtle);
}

.input:checked {
  border-color: var(--ink-cobalt-100);
}

.input:checked::after {
  content: '';
  position: absolute;
  left: 4px;
  top: 4px;
  width: 8px;
  height: 8px;
  border-radius: var(--ink-radius-full);
  background: var(--ink-cobalt-100);
}

/* Disabled uses wrapper opacity, but keep cursor style */
.input:disabled {
  cursor: not-allowed;
}

.label {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px - matches Figma fontBodyS */
  color: var(--ink-font-color-default); /* rgba(19,0,50,0.9) - Figma fontColorDefault */
  line-height: 1.4; /* Figma fontBodyS line-height */
  cursor: pointer;
  user-select: none;
}

/* Disabled label inherits opacity from wrapper, just change cursor */
.label.disabled {
  cursor: not-allowed;
}

.label.error {
  color: var(--ink-red-60);
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Description text per Figma fontDetailS */
.description {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px - Figma font.fontSize.12 */
  font-weight: var(--ink-font-weight-medium); /* 500 - Figma font.fontWeight.500 */
  color: var(--ink-neutral-fade-70); /* rgba(19,0,50,0.7) - Figma fontColorSubtle */
  line-height: var(--ink-line-height-normal); /* 1.5 - Figma fontDetailS line-height */
  letter-spacing: var(--ink-letter-spacing-wide); /* 0.16px - Figma font.letterSpacing.wide */
  padding-left: var(--ink-spacing-400); /* 32px - aligns with label (24px control + 8px gap) */
}

/* Error message per Figma fontDetailS with error color */
.errorMessage {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-error); /* Figma fontColorError */
  line-height: var(--ink-line-height-normal); /* 1.5 */
  letter-spacing: var(--ink-letter-spacing-wide); /* 0.16px */
  padding-left: var(--ink-spacing-400); /* 32px */
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-50); /* 4px gap between icon and text */
}

import React, { forwardRef } from 'react';
import styles from './Radio.module.css';

export interface RadioProps extends Omit<React.InputHTMLAttributes<HTMLInputElement>, 'type'> {
  label: string;
  error?: string;
  description?: string;
  hideLabel?: boolean;
}

export const Radio = forwardRef<HTMLInputElement, RadioProps>(
  ({ label, error, description, hideLabel = false, className, id, disabled, ...props }, ref) => {
    const generatedId = React.useId();
    const radioId = id || generatedId;
    const errorId = `${radioId}-error`;
    const descriptionId = `${radioId}-description`;

    const ariaDescribedBy = [error ? errorId : null, description ? descriptionId : null]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={styles.wrapper}>
        <div className={`${styles.radioWrapper} ${disabled ? styles.disabled : ''}`}>
          {/* Control container for hover background effect */}
          <div className={styles.control}>
            <input
              ref={ref}
              id={radioId}
              type="radio"
              className={`${styles.input} ${className || ''}`}
              aria-describedby={ariaDescribedBy || undefined}
              aria-invalid={error ? true : undefined}
              disabled={disabled}
              {...props}
            />
          </div>
          <label
            htmlFor={radioId}
            className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
              disabled ? styles.disabled : ''
            } ${error ? styles.error : ''}`}
          >
            {label}
          </label>
        </div>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            {error}
          </div>
        )}
      </div>
    );
  }
);

Radio.displayName = 'Radio';

Select:3.content:
/* Ink Design System - Select Component Styles */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50);
  width: 100%;
}

/* Label */
.label {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-default);
  line-height: 1.4; /* Exact Figma spec */
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
}

.label.disabled {
  opacity: 0.25;
}

.required {
  color: var(--ink-form-text-required);
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Description */
.description {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px - Figma spec */
  font-weight: var(--ink-font-weight-medium); /* 500 - Figma spec */
  color: var(--ink-font-color-neutral);
  line-height: var(--ink-line-height-normal);
  letter-spacing: var(--ink-letter-spacing-wide); /* 0.16px - Figma spec */
}

/* Select Container */
.selectContainer {
  position: relative;
  display: inline-block;
  width: 100%;
}

/* Select */
.select {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md); /* 16px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  color: var(--ink-font-color-default);
  background: var(--ink-form-control-bg-color-default);
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-default); /* Figma: neutral-fade-50 */
  border-radius: var(--ink-radius-sm); /* 4px */
  padding: var(--ink-spacing-100) var(--ink-spacing-600) var(--ink-spacing-100)
    var(--ink-spacing-200); /* DocuSign: 48px right padding for icon space */
  transition: all var(--ink-transition-fast);
  width: 100%;
  line-height: 1.5; /* Exact Figma spec */
  appearance: none;
  cursor: pointer;
  outline: none;
}

/* Size variants */
.select.medium {
  height: 40px;
}

.select.small {
  height: 32px;
  padding: var(--ink-spacing-50) var(--ink-spacing-600) var(--ink-spacing-50) var(--ink-spacing-200); /* DocuSign: 48px right padding */
  font-size: var(--ink-font-size-sm); /* 14px for small variant */
}

/* Hover state */
.select:hover:not(:disabled):not(.error) {
  border-color: var(--ink-form-control-border-color-hover);
}

/* Focus/Active state - 2px border as per Figma */
.select:focus:not(.error) {
  border: var(--ink-form-border-width-active) solid var(--ink-form-control-border-color-active);
  /* Adjust padding to compensate for thicker border */
  padding: calc(var(--ink-spacing-100) - 1px) calc(var(--ink-spacing-600) - 1px)
    calc(var(--ink-spacing-100) - 1px) calc(var(--ink-spacing-200) - 1px);
}

.select.small:focus:not(.error) {
  /* Adjust padding for small variant on focus */
  padding: calc(var(--ink-spacing-50) - 1px) calc(var(--ink-spacing-600) - 1px)
    calc(var(--ink-spacing-50) - 1px) calc(var(--ink-spacing-200) - 1px);
}

/* Error state */
.select.error {
  border-color: var(--ink-form-control-border-color-error);
}

.select.error:focus {
  border: var(--ink-form-border-width) solid var(--ink-form-control-border-color-error);
  /* Keep same padding for error state */
  padding: var(--ink-spacing-100) var(--ink-spacing-600) var(--ink-spacing-100)
    var(--ink-spacing-200); /* DocuSign: 48px right padding for icon space */
}

.select.small.error:focus {
  /* Keep same padding for small error state */
  padding: var(--ink-spacing-50) var(--ink-spacing-600) var(--ink-spacing-50) var(--ink-spacing-200);
}

/* Disabled state */
.select:disabled {
  background: var(--ink-form-bg-disabled);
  opacity: 0.2;
  cursor: not-allowed;
  border-color: var(--ink-form-control-border-color-default);
}

/* Chevron icon */
.icon {
  position: absolute;
  right: var(--ink-spacing-100);
  top: 50%;
  transform: translateY(-50%);
  pointer-events: none;
  color: var(--ink-font-color-default);
  display: flex;
  align-items: center;
  justify-content: center;
}

.select:disabled ~ .icon {
  opacity: 0.2;
}

/* Error message */
.errorMessage {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-red-90);
  line-height: 1.5; /* Exact Figma spec */
  display: flex;
  align-items: flex-start;
  gap: var(--ink-spacing-50);
}

.errorIcon {
  flex-shrink: 0;
  display: flex;
  align-items: center;
  justify-content: center;
}

import React, { forwardRef } from 'react';
import styles from './Select.module.css';
import { Icon } from '../Icon';

export interface SelectProps extends Omit<React.SelectHTMLAttributes<HTMLSelectElement>, 'size'> {
  label: string;
  error?: string;
  description?: string;
  hideLabel?: boolean;
  required?: boolean;
  size?: 'small' | 'medium';
  width?: string;
  children: React.ReactNode;
}

export const Select = forwardRef<HTMLSelectElement, SelectProps>(
  (
    {
      label,
      error,
      description,
      hideLabel = false,
      required = false,
      size = 'medium',
      width,
      className,
      id,
      disabled,
      children,
      ...props
    },
    ref
  ) => {
    const generatedId = React.useId();
    const selectId = id || generatedId;
    const errorId = `${selectId}-error`;
    const descriptionId = `${selectId}-description`;

    const ariaDescribedBy = [
      error ? errorId : null,
      description ? descriptionId : null,
    ]
      .filter(Boolean)
      .join(' ');

    const selectClasses = [
      styles.select,
      styles[size],
      error && styles.error,
      disabled && styles.disabled,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={styles.wrapper} style={{ width }}>
        <label
          htmlFor={selectId}
          className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
            disabled ? styles.disabled : ''
          }`}
        >
          {label}
          {required && <span className={styles.required}> *</span>}
        </label>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <div className={styles.selectContainer}>
          <select
            ref={ref}
            id={selectId}
            className={selectClasses}
            aria-describedby={ariaDescribedBy || undefined}
            aria-invalid={error ? true : undefined}
            aria-required={required || undefined}
            disabled={disabled}
            {...props}
          >
            {children}
          </select>
          <div className={styles.icon}>
            <Icon name="chevron-down" size="small" />
          </div>
        </div>

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            <span className={styles.errorIcon}>
              <Icon name="status-error" size="small" />
            </span>
            <span>{error}</span>
          </div>
        )}
      </div>
    );
  }
);

Select.displayName = 'Select';

Skeleton:3.content:
/* Ink Design System - Skeleton Component Styles */
@import '../../1-tokens/tokens.css';

.skeleton {
  display: block;
  background-color: var(--ink-neutral-20);
  border-radius: var(--ink-radius-sm);
}

.animated {
  background: linear-gradient(
    90deg,
    var(--ink-neutral-20) 0%,
    var(--ink-neutral-10) 50%,
    var(--ink-neutral-20) 100%
  );
  background-size: 200% 100%;
  animation: shimmer 1.5s ease-in-out infinite;
}

@keyframes shimmer {
  0% {
    background-position: 200% 0;
  }
  100% {
    background-position: -200% 0;
  }
}

/* Variants */
.text {
  height: 1em;
  width: 100%;
  border-radius: var(--ink-radius-sm);
  transform-origin: 0 60%;
  transform: scale(1, 0.6);
}

.circular {
  border-radius: var(--ink-radius-full);
  aspect-ratio: 1;
}

.rectangular {
  border-radius: var(--ink-radius-sm);
  width: 100%;
  height: 100px;
}

.rounded {
  border-radius: var(--ink-radius-lg);
  width: 100%;
  height: 100px;
}

/* Sizes (for circular variant) */
.xs {
  width: 24px;
  height: 24px;
}

.sm {
  width: 32px;
  height: 32px;
}

.md {
  width: 40px;
  height: 40px;
}

.lg {
  width: 56px;
  height: 56px;
}

.xl {
  width: 80px;
  height: 80px;
}

/* Skeleton Group */
.skeletonGroup {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-2);
  width: 100%;
}

.skeletonGroup .skeleton {
  margin: 0;
}

.lastLine {
  width: 80%;
}

import * as React from 'react';
import { cn } from '@/lib/utils';
import styles from './Skeleton.module.css';

export type SkeletonVariant = 'text' | 'circular' | 'rectangular' | 'rounded';
export type SkeletonSize = 'xs' | 'sm' | 'md' | 'lg' | 'xl';

export interface SkeletonProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Variant of the skeleton */
  variant?: SkeletonVariant;
  /** Size preset (only applies to circular variant) */
  size?: SkeletonSize;
  /** Custom width */
  width?: string | number;
  /** Custom height */
  height?: string | number;
  /** Whether to animate */
  animated?: boolean;
  /** Number of lines (for text variant) */
  lines?: number;
  /** Custom className */
  className?: string;
}

const Skeleton = React.forwardRef<HTMLDivElement, SkeletonProps>(
  (
    {
      variant = 'text',
      size = 'md',
      width,
      height,
      animated = true,
      lines = 1,
      className,
      style,
      ...props
    },
    ref
  ) => {
    const skeletonStyle: React.CSSProperties = {
      ...style,
      ...(width && { width: typeof width === 'number' ? `${width}px` : width }),
      ...(height && { height: typeof height === 'number' ? `${height}px` : height }),
    };

    // For text variant with multiple lines
    if (variant === 'text' && lines > 1) {
      return (
        <div
          ref={ref}
          className={cn(styles.skeletonGroup, className)}
          {...props}
        >
          {Array.from({ length: lines }).map((_, index) => (
            <div
              key={index}
              className={cn(
                styles.skeleton,
                styles[variant],
                animated && styles.animated,
                // Make last line shorter
                index === lines - 1 && styles.lastLine
              )}
              style={{
                ...skeletonStyle,
                width: index === lines - 1 && !width ? '80%' : skeletonStyle.width,
              }}
            />
          ))}
        </div>
      );
    }

    return (
      <div
        ref={ref}
        className={cn(
          styles.skeleton,
          styles[variant],
          variant === 'circular' && styles[size],
          animated && styles.animated,
          className
        )}
        style={skeletonStyle}
        {...props}
      />
    );
  }
);

Skeleton.displayName = 'InkSkeleton';

export { Skeleton };

Slider:3.content:
/* Ink Design System - Slider Component Styles */
/* Updated to match Figma specifications */
@import '../../1-tokens/tokens.css';

.wrapper {
  display: flex;
  flex-direction: column;
  gap: var(--ink-spacing-50); /* 4px gap per Figma */
  width: 256px; /* Default width per Figma */
  border-radius: var(--ink-radius-xs);
}

/* Label */
.label {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px */
  font-weight: var(--ink-font-weight-medium); /* 500 */
  color: var(--ink-font-color-default);
  line-height: 1.4;
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-50);
}

.label.disabled {
  opacity: 0.25;
}

.visuallyHidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Description */
.description {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-xs); /* 12px per Figma */
  color: var(--ink-font-color-subtle);
  line-height: 1.5;
  letter-spacing: 0.16px; /* wide per Figma */
}

/* Container with optional value display */
.container {
  display: flex;
  align-items: center;
  gap: var(--ink-spacing-200); /* 16px gap per Figma */
  width: 100%;
}

.container.withValue {
  gap: var(--ink-spacing-200);
}

/* IconButton styling for start/end elements */
.sliderIconButton {
  flex-shrink: 0;
}

/* Slider Container */
.sliderContainer {
  position: relative;
  flex: 1;
  height: 40px; /* 40px per Figma */
  display: flex;
  align-items: center;
  border-radius: var(--ink-radius-full);
}

/* Track */
.track {
  position: absolute;
  top: 50%;
  left: 0;
  right: 0;
  height: var(--ink-bar-width-s); /* 4px */
  background: var(--ink-bar-track-default);
  border-radius: var(--ink-radius-full);
  transform: translateY(-50%);
  pointer-events: none;
}

/* Stop indicator at end of track */
.stopIndicator {
  position: absolute;
  right: 0;
  top: 50%;
  transform: translateY(-50%);
  width: var(--ink-bar-width-s); /* 4px */
  height: var(--ink-bar-width-s); /* 4px */
  background: var(--ink-bar-stop-indicator);
  border-radius: var(--ink-radius-full);
  pointer-events: none;
}

/* Filled Track */
.trackFilled {
  position: absolute;
  top: 50%;
  left: 0;
  height: var(--ink-bar-width-s); /* 4px */
  background: var(--ink-bar-fill-subtle); /* neutral-fade-70 per Figma default state */
  border-radius: var(--ink-radius-full);
  transform: translateY(-50%);
  transition: background-color var(--ink-transition-fast);
  pointer-events: none;
}

/* Track fill color states - emphasis on hover/active */
.trackFilled.hover {
  background: var(--ink-bar-fill-emphasis); /* neutral-140 on hover */
}

.trackFilled.active {
  background: var(--ink-bar-fill-accent-emphasis); /* cobalt-120 on active */
}

/* Range Input */
.slider {
  -webkit-appearance: none;
  appearance: none;
  width: 100%;
  height: 40px;
  background: transparent;
  outline: none;
  position: relative;
  cursor: pointer;
  margin: 0;
}

.slider:disabled {
  cursor: not-allowed;
}

.wrapper.disabled {
  opacity: 0.25;
}

.slider:disabled + .tooltip {
  display: none;
}

/* Webkit Thumb - Solid filled circle per Figma */
.slider::-webkit-slider-thumb {
  -webkit-appearance: none;
  appearance: none;
  width: 16px;
  height: 16px;
  background: var(--ink-form-control-thumb-bg-default); /* neutral-100 */
  border: none;
  border-radius: 50%;
  cursor: pointer;
  transition: all var(--ink-transition-fast);
  position: relative;
  z-index: 2;
}

.slider:hover:not(:disabled)::-webkit-slider-thumb {
  background: var(--ink-form-control-thumb-bg-hover); /* neutral-140 on hover */
  box-shadow: 0 0 0 8px var(--ink-neutral-fade-10); /* hover ring effect */
  transform: scale(1.5); /* 24px total with ring */
}

.slider:focus-visible:not(:disabled)::-webkit-slider-thumb {
  background: var(--ink-form-control-thumb-bg-hover);
  box-shadow: 0 0 0 8px var(--ink-neutral-fade-10);
  outline: none;
}

.slider:active:not(:disabled)::-webkit-slider-thumb {
  background: var(--ink-form-control-thumb-bg-active); /* cobalt-120 on active */
  box-shadow: none;
  transform: scale(1);
}

.slider:disabled::-webkit-slider-thumb {
  background: var(--ink-form-control-thumb-bg-default);
  cursor: not-allowed;
}

/* Firefox Thumb */
.slider::-moz-range-thumb {
  width: 16px;
  height: 16px;
  background: var(--ink-form-control-thumb-bg-default);
  border: none;
  border-radius: 50%;
  cursor: pointer;
  transition: all var(--ink-transition-fast);
}

.slider:hover:not(:disabled)::-moz-range-thumb {
  background: var(--ink-form-control-thumb-bg-hover);
  box-shadow: 0 0 0 8px var(--ink-neutral-fade-10);
  transform: scale(1.5);
}

.slider:focus-visible:not(:disabled)::-moz-range-thumb {
  background: var(--ink-form-control-thumb-bg-hover);
  box-shadow: 0 0 0 8px var(--ink-neutral-fade-10);
  outline: none;
}

.slider:active:not(:disabled)::-moz-range-thumb {
  background: var(--ink-form-control-thumb-bg-active);
  box-shadow: none;
  transform: scale(1);
}

.slider:disabled::-moz-range-thumb {
  background: var(--ink-form-control-thumb-bg-default);
  cursor: not-allowed;
}

/* Firefox Track (hide default track) */
.slider::-moz-range-track {
  background: transparent;
  border: none;
}

/* Tooltip - Updated per Figma with white border and caret */
.tooltip {
  position: absolute;
  top: -44px; /* Adjusted for proper positioning above thumb */
  transform: translateX(-50%);
  background: var(--ink-message-bg-default); /* #2b2843 per Figma */
  color: var(--ink-white-100);
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-sm); /* 14px per Figma */
  font-weight: var(--ink-font-weight-regular); /* 400 per Figma */
  line-height: 1.4;
  padding: var(--ink-spacing-100); /* 8px per Figma */
  border-radius: var(--ink-radius-xs); /* 4px per Figma */
  border: 1px solid var(--ink-border-emphasis-inverse); /* white border */
  white-space: nowrap;
  pointer-events: none;
  z-index: 10;
  box-shadow: 0 4px 8px var(--ink-neutral-fade-15); /* elevationLow per Figma */
  max-width: 272px;
}

/* Tooltip caret pointing down */
.tooltip::after {
  content: '';
  position: absolute;
  top: 100%;
  left: 50%;
  transform: translateX(-50%);
  border-width: 8px 7px 0 7px;
  border-style: solid;
  border-color: var(--ink-message-bg-default) transparent transparent transparent;
}

/* Tooltip caret border (white outline) */
.tooltip::before {
  content: '';
  position: absolute;
  top: 100%;
  left: 50%;
  transform: translateX(-50%);
  border-width: 9px 8px 0 8px;
  border-style: solid;
  border-color: var(--ink-white-100) transparent transparent transparent;
  z-index: -1;
}

/* Value Display */
.valueDisplay {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-size-md);
  font-weight: var(--ink-font-weight-medium);
  color: var(--ink-font-color-default);
  min-width: 40px;
  text-align: right;
  flex-shrink: 0;
}

/* Disabled track - uses subtle variant */
.wrapper.disabled .trackFilled {
  background: var(--ink-bar-fill-subtle);
}

import React, { useRef, useState, useCallback, useEffect } from 'react';
import { IconButton } from '../IconButton';
import styles from './Slider.module.css';

export interface SliderProps {
  /** The label text for the slider */
  label: string;
  /** Hide the label visually (still accessible to screen readers) */
  hideLabel?: boolean;
  /** Description text shown below the label */
  description?: string;
  /** Minimum value */
  min?: number;
  /** Maximum value */
  max?: number;
  /** Step increment */
  step?: number;
  /** Current value */
  value: number;
  /** Callback when value changes */
  onChange: (value: number) => void;
  /** Whether the slider is disabled */
  disabled?: boolean;
  /** Show the current value next to the slider */
  showValue?: boolean;
  /** Show tooltip on hover/drag */
  showTooltip?: boolean;
  /** Icon name to display as IconButton at the start of the slider (e.g., 'zoom-out') */
  startElement?: string;
  /** Icon name to display as IconButton at the end of the slider (e.g., 'zoom-in') */
  endElement?: string;
  /** Callback when start IconButton is clicked */
  onStartClick?: () => void;
  /** Callback when end IconButton is clicked */
  onEndClick?: () => void;
  /** Custom formatter for tooltip value */
  formatTooltip?: (value: number) => string;
  /** ID for the slider input */
  id?: string;
  /** Custom width for the slider wrapper */
  width?: string;
}

export const Slider = React.forwardRef<HTMLInputElement, SliderProps>(
  (
    {
      label,
      hideLabel = false,
      description,
      min = 0,
      max = 100,
      step = 1,
      value,
      onChange,
      disabled = false,
      showValue = false,
      showTooltip: showTooltipProp = true,
      startElement,
      endElement,
      onStartClick,
      onEndClick,
      formatTooltip,
      id,
      width,
    },
    ref
  ) => {
    const generatedId = React.useId();
    const sliderId = id || generatedId;
    const descriptionId = `${sliderId}-description`;
    const [isHovering, setIsHovering] = useState(false);
    const [isDragging, setIsDragging] = useState(false);
    const sliderRef = useRef<HTMLInputElement>(null);
    const trackRef = useRef<HTMLDivElement>(null);

    // Merge refs
    React.useImperativeHandle(ref, () => sliderRef.current!);

    // Calculate percentage for visual feedback
    const percentage = ((value - min) / (max - min)) * 100;

    // Handle value changes from slider
    const handleChange = useCallback(
      (e: React.ChangeEvent<HTMLInputElement>) => {
        if (disabled) return;
        const newValue = parseFloat(e.target.value);
        onChange(newValue);
      },
      [disabled, onChange]
    );

    // Handle keyboard input
    const handleKeyDown = useCallback(
      (e: React.KeyboardEvent<HTMLInputElement>) => {
        if (disabled) return;

        let newValue = value;
        switch (e.key) {
          case 'ArrowLeft':
          case 'ArrowDown':
            e.preventDefault();
            newValue = Math.max(min, value - step);
            break;
          case 'ArrowRight':
          case 'ArrowUp':
            e.preventDefault();
            newValue = Math.min(max, value + step);
            break;
          case 'Home':
            e.preventDefault();
            newValue = min;
            break;
          case 'End':
            e.preventDefault();
            newValue = max;
            break;
          default:
            return;
        }

        onChange(newValue);
      },
      [disabled, value, min, max, step, onChange]
    );

    // Mouse event handlers
    const handleMouseDown = useCallback(() => {
      if (!disabled) {
        setIsDragging(true);
      }
    }, [disabled]);

    const handleMouseUp = useCallback(() => {
      setIsDragging(false);
    }, []);

    useEffect(() => {
      if (isDragging) {
        document.addEventListener('mouseup', handleMouseUp);
        return () => {
          document.removeEventListener('mouseup', handleMouseUp);
        };
      }
    }, [isDragging, handleMouseUp]);

    const wrapperClasses = [styles.wrapper, disabled && styles.disabled].filter(Boolean).join(' ');

    const labelClasses = [styles.label, hideLabel && styles.visuallyHidden]
      .filter(Boolean)
      .join(' ');

    const containerClasses = [styles.container, showValue && styles.withValue]
      .filter(Boolean)
      .join(' ');

    const sliderClasses = [styles.slider].filter(Boolean).join(' ');

    // Track fill classes based on state
    const trackFilledClasses = [
      styles.trackFilled,
      isHovering && !disabled && !isDragging && styles.hover,
      isDragging && !disabled && styles.active,
    ]
      .filter(Boolean)
      .join(' ');

    const shouldShowTooltip = showTooltipProp && (isHovering || isDragging) && !disabled;

    // Format the tooltip value
    const tooltipValue = formatTooltip ? formatTooltip(value) : `${value}%`;

    return (
      <div className={wrapperClasses} style={width ? { width } : undefined}>
        <label htmlFor={sliderId} className={labelClasses}>
          {label}
        </label>

        {description && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <div className={containerClasses}>
          {startElement && (
            <IconButton
              icon={startElement}
              variant="tertiary"
              size="medium"
              aria-label={`Decrease ${label}`}
              onClick={onStartClick}
              disabled={disabled}
              className={styles.sliderIconButton}
            />
          )}

          <div
            className={styles.sliderContainer}
            onMouseEnter={() => setIsHovering(true)}
            onMouseLeave={() => setIsHovering(false)}
          >
            <div className={styles.track} ref={trackRef}>
              {/* Stop indicator at end of track */}
              <div className={styles.stopIndicator} />
              {/* Filled track */}
              <div className={trackFilledClasses} style={{ width: `${percentage}%` }} />
            </div>

            <input
              ref={sliderRef}
              type="range"
              id={sliderId}
              className={sliderClasses}
              min={min}
              max={max}
              step={step}
              value={value}
              onChange={handleChange}
              onKeyDown={handleKeyDown}
              onMouseDown={handleMouseDown}
              disabled={disabled}
              aria-describedby={description ? descriptionId : undefined}
              aria-valuemin={min}
              aria-valuemax={max}
              aria-valuenow={value}
            />

            {/* Tooltip on hover/drag */}
            {shouldShowTooltip && (
              <div className={styles.tooltip} style={{ left: `${percentage}%` }}>
                {tooltipValue}
              </div>
            )}
          </div>

          {endElement && (
            <IconButton
              icon={endElement}
              variant="tertiary"
              size="medium"
              aria-label={`Increase ${label}`}
              onClick={onEndClick}
              disabled={disabled}
              className={styles.sliderIconButton}
            />
          )}

          {showValue && <div className={styles.valueDisplay}>{value}</div>}
        </div>
      </div>
    );
  }
);

Slider.displayName = 'Slider';

