@import '../../1-tokens/tokens.css';

.wrapper {
  display: inline-flex;
  align-items: center;
  gap: var(--ink-spacing-100); /* 8px for small */
}

.spinner {
  animation: rotate 1s linear infinite;
  flex-shrink: 0;
}

/* Sizes - From Figma ProgressCircle specs */
.spinner.small {
  width: 16px;
  height: 16px;
}

.spinner.medium {
  width: 40px;
  height: 40px;
}

.spinner.large {
  width: 80px;
  height: 80px;
}

/* Background circle - static, lighter color */
