  stroke: var(--ink-cobalt-100); /* Default cobalt/purple color #4c00fb */
}

.spinner.subtle .circle {
  stroke: var(--ink-neutral-90); /* Subtle dark gray color #565361 */
}

/* Animated foreground circle */
.circle {
  stroke-linecap: round;
  animation: dash 1.5s ease-in-out infinite;
}

/* Visually hidden label for accessibility */
.label {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Visible label */
.labelVisible {
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-s-size); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-body-s-line-height); /* 1.4 */
  color: var(--ink-neutral-fade-90);
}

@keyframes rotate {
  100% {
    transform: rotate(360deg);
  }
}

@keyframes dash {
  0% {
    stroke-dasharray: 1, 150;
    stroke-dashoffset: 0;
  }
  50% {
    stroke-dasharray: 90, 150;
    stroke-dashoffset: -35;
  }
