import React, { forwardRef, useRef, useEffect, useState } from 'react';
import styles from './TextArea.module.css';
import { Icon } from '../Icon';

export type ResizeValue = 'both' | 'horizontal' | 'vertical' | 'none';

export interface TextAreaProps extends Omit<
  React.TextareaHTMLAttributes<HTMLTextAreaElement>,
  'size'
> {
  /** The text label for the TextArea */
  label: string;
  /** Hide the TextArea label while allowing assistive devices to identify the control */
  hideLabel?: boolean;
  /** Error message to display */
  error?: string;
  /** Description/help text to display */
  description?: string;
  /** Whether the field is required */
  required?: boolean;
  /** Number of visible text lines */
  rows?: number;
  /** Control how the textarea can be resized */
  resize?: ResizeValue;
  /** Display character count when maxLength is set */
  characterCount?: boolean;
  /** Allow typing over maxLength (shows warning instead of blocking) */
  allowOverLimit?: boolean;
  /** Vertically expand the TextArea to show all content */
  autoExpand?: boolean;
  /** Maximum height in pixels when autoExpand is enabled */
  autoExpandLimit?: number;
  /** Width of the textarea */
  width?: string;
}

export const TextArea = forwardRef<HTMLTextAreaElement, TextAreaProps>(
  (
    {
      label,
      hideLabel = false,
      error,
      description,
      required = false,
      rows = 4,
      resize = 'vertical',
      characterCount = false,
      allowOverLimit = true,
      autoExpand = false,
      autoExpandLimit,
      width,
      className,
      id,
      disabled,
      maxLength,
      value,
      onChange,
      ...props
    },
    ref
  ) => {
    const generatedId = React.useId();
    const textareaId = id || generatedId;
    const errorId = `${textareaId}-error`;
    const descriptionId = `${textareaId}-description`;
    const countId = `${textareaId}-count`;

    const internalRef = useRef<HTMLTextAreaElement>(null);
    const [charCount, setCharCount] = useState(0);

    const ariaDescribedBy = [
      error ? errorId : null,
      description ? descriptionId : null,
      characterCount && maxLength ? countId : null,
    ]
      .filter(Boolean)
      .join(' ');

    const isOverLimit = maxLength ? charCount > maxLength : false;

    // Auto-expand functionality
    useEffect(() => {
      if (autoExpand && internalRef.current) {
        const textarea = internalRef.current;
        textarea.style.height = 'auto';
        const newHeight = textarea.scrollHeight;
        const maxHeight = autoExpandLimit || Infinity;
        textarea.style.height = `${Math.min(newHeight, maxHeight)}px`;
      }
    }, [value, autoExpand, autoExpandLimit]);

    // Track character count
    useEffect(() => {
      if (value !== undefined) {
        setCharCount(String(value).length);
      }
    }, [value]);

    const handleChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
      const newValue = e.target.value;
      setCharCount(newValue.length);

      // Block input if over limit and allowOverLimit is false
      if (maxLength && !allowOverLimit && newValue.length > maxLength) {
        return;
      }

      onChange?.(e);
    };

    const textareaClasses = [
      styles.textarea,
      styles[`resize-${resize}`],
      error && styles.error,
      disabled && styles.disabled,
      autoExpand && styles.autoExpand,
      className,
    ]
      .filter(Boolean)
      .join(' ');

    const wrapperClasses = [styles.wrapper, disabled && styles.wrapperDisabled]
      .filter(Boolean)
      .join(' ');

    return (
      <div className={wrapperClasses} style={{ width }}>
        <label
          htmlFor={textareaId}
          className={`${styles.label} ${hideLabel ? styles.visuallyHidden : ''} ${
            disabled ? styles.disabled : ''
          }`}
        >
          {label}
          {required && <span className={styles.required}> *</span>}
        </label>

        {description && !error && (
          <div id={descriptionId} className={styles.description}>
            {description}
          </div>
        )}

        <textarea
          ref={(node) => {
            // Merge refs
            if (typeof ref === 'function') {
              ref(node);
            } else if (ref) {
              ref.current = node;
            }
            (internalRef as React.MutableRefObject<HTMLTextAreaElement | null>).current = node;
          }}
          id={textareaId}
          className={textareaClasses}
          aria-describedby={ariaDescribedBy || undefined}
          aria-invalid={error ? true : undefined}
          aria-required={required || undefined}
          disabled={disabled}
          rows={rows}
          maxLength={!allowOverLimit ? maxLength : undefined}
          value={value}
          onChange={handleChange}
          {...props}
        />

        {characterCount && maxLength && (
          <div id={countId} className={`${styles.count} ${isOverLimit ? styles.countError : ''}`}>
            {charCount}/{maxLength}
            {isOverLimit && allowOverLimit && (
              <span className={styles.countOverMessage}> (Over character limit)</span>
            )}
          </div>
        )}

        {error && (
          <div id={errorId} className={styles.errorMessage}>
            <span className={styles.errorIcon}>
              <Icon name="status-error" size="small" />
            </span>
            <span>{error}</span>
          </div>
        )}
      </div>
    );
  }
);

TextArea.displayName = 'TextArea';

Tooltip:3.content:
@import '../../1-tokens/tokens.css';

/* Tooltip container */
.tooltip {
  position: fixed;
  display: flex;
  flex-direction: column;
  background-color: var(--ink-message-bg-default); /* #2b2843 from Figma */
  color: var(--ink-white-100);
  padding: var(--ink-spacing-100); /* 8px */
  border: 1px solid var(--ink-border-emphasis-inverse); /* white border from Figma */
  border-radius: var(--ink-radius-size-xs); /* 4px */
  font-family: var(--ink-font-family);
  font-size: var(--ink-font-body-s-size); /* 14px */
  font-weight: var(--ink-font-weight-regular); /* 400 */
  line-height: var(--ink-font-body-s-line-height); /* 1.4 */
  max-width: 272px; /* Figma spec */
  z-index: var(--ink-z-tooltip);
  pointer-events: none;
  animation: tooltipFadeIn var(--ink-transition-fast);
  word-wrap: break-word;
  box-shadow: var(--ink-shadow-elevated); /* 0px 4px 8px 0px rgba(19, 0, 50, 0.15) */
}

.text {
  max-width: 264px; /* Content max width from Figma */
}

@keyframes tooltipFadeIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

/* Caret container */
.caret {
  position: absolute;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 15px;
  height: 8px;
}

.caretSvg {
  display: block;
}

.caretFill {
  fill: var(--ink-message-bg-default); /* Same as tooltip background */
}

.caretBorder {
  stroke: var(--ink-border-emphasis-inverse); /* White border */
  fill: none;
}

/* ================================== */
/* Location: Above                    */
/* ================================== */

.above {
  flex-direction: column;
  align-items: center;
  /* Keep 8px padding on all sides */
}

.above .caret {
  bottom: -7px; /* Position outside the 8px padding */
  transform: rotate(0deg);
}

.above.align-start {
  align-items: flex-start;
}

.above.align-start .caret {
  left: 8px;
}

.above.align-center {
  align-items: center;
}

.above.align-center .caret {
  left: 50%;
  transform: translateX(-50%);
}

.above.align-end {
  align-items: flex-end;
}

.above.align-end .caret {
  right: 8px;
}

/* ================================== */
/* Location: Below                    */
/* ================================== */

.below {
  flex-direction: column;
  align-items: center;
  /* Keep 8px padding on all sides */
}

.below .caret {
  top: -7px; /* Position outside the 8px padding */
  transform: rotate(180deg);
}

.below.align-start {
  align-items: flex-start;
}

.below.align-start .caret {
  left: 8px;
}

.below.align-center {
  align-items: center;
}

.below.align-center .caret {
  left: 50%;
  transform: translateX(-50%) rotate(180deg);
}

.below.align-end {
  align-items: flex-end;
}

.below.align-end .caret {
  right: 8px;
}

/* ================================== */
/* Location: After (Right)            */
/* ================================== */

.after {
  flex-direction: row;
  align-items: center;
  /* Keep 8px padding on all sides */
}

.after .caret {
  left: -7px; /* Position outside the 8px padding */
  width: 8px;
  height: 15px;
  transform: rotate(90deg);
}

.after.align-start {
  align-items: flex-start;
}

.after.align-start .caret {
  top: 8px;
}

.after.align-center {
  align-items: center;
}

.after.align-center .caret {
  top: 50%;
  transform: translateY(-50%) rotate(90deg);
}

.after.align-end {
  align-items: flex-end;
}

.after.align-end .caret {
  bottom: 8px;
}

/* ================================== */
/* Location: Before (Left)            */
/* ================================== */

.before {
  flex-direction: row;
  align-items: center;
  /* Keep 8px padding on all sides */
}

.before .caret {
  right: -7px; /* Position outside the 8px padding */
  width: 8px;
  height: 15px;
  transform: rotate(-90deg);
}

.before.align-start {
  align-items: flex-start;
}

.before.align-start .caret {
  top: 8px;
}

.before.align-center {
  align-items: center;
}

.before.align-center .caret {
  top: 50%;
  transform: translateY(-50%) rotate(-90deg);
}

.before.align-end {
  align-items: flex-end;
}

.before.align-end .caret {
  bottom: 8px;
}

/* ================================== */
/* Legacy position support            */
/* ================================== */

/* Keep old class names for backward compatibility */
.top {
  composes: above;
}

.bottom {
  composes: below;
}

.left {
  composes: before;
}

.right {
  composes: after;
}

/* Legacy arrow class (deprecated, use caret) */
.arrow {
  composes: caret;
}

import React, { useState, useRef, useEffect } from 'react';
import { createPortal } from 'react-dom';
import styles from './Tooltip.module.css';

/** @deprecated Use TooltipLocation instead */
export type TooltipPosition = 'top' | 'bottom' | 'left' | 'right';

export type TooltipLocation = 'above' | 'below' | 'before' | 'after';
export type TooltipAlignment = 'start' | 'center' | 'end';

export interface TooltipProps {
  /** The text to display inside the Tooltip */
  text?: string;
  /** The element that triggers the tooltip */
  children: React.ReactElement;
  /** The preferred location of the Tooltip relative to its anchor element */
  location?: TooltipLocation;
  /**
   * The alignment of the Tooltip along the edge of its anchor element.
   * For locations 'above' and 'below': 'start' = left-aligned, 'center' = centered, 'end' = right-aligned
   * For locations 'before' and 'after': 'start' = top-aligned, 'center' = centered, 'end' = bottom-aligned
   */
  alignment?: TooltipAlignment;
  /** Delay in milliseconds before showing the tooltip */
  delay?: number;
  /** Whether the tooltip is disabled */
  disabled?: boolean;
  /** Data QA attribute for testing */
  'data-qa'?: string;
  /** @deprecated Use text instead */
  content?: string;
  /** @deprecated Use location instead */
  position?: TooltipPosition;
}

// Map legacy position prop to new location prop
const positionToLocation: Record<TooltipPosition, TooltipLocation> = {
  top: 'above',
  bottom: 'below',
  left: 'before',
  right: 'after',
};

export const Tooltip: React.FC<TooltipProps> = ({
  text,
  children,
  location,
  alignment = 'center',
  delay = 200,
  disabled = false,
  'data-qa': dataQa,
  // Legacy props
  content,
  position,
}) => {
  // Support legacy props
  const tooltipText = text || content || '';
  const tooltipLocation: TooltipLocation =
    location || (position ? positionToLocation[position] : 'above');

  const [visible, setVisible] = useState(false);
  const [coords, setCoords] = useState({ top: 0, left: 0 });
  const [actualLocation, setActualLocation] = useState<TooltipLocation>(tooltipLocation);
  const triggerRef = useRef<HTMLElement>(null);
  const tooltipRef = useRef<HTMLDivElement>(null);
  const timeoutRef = useRef<NodeJS.Timeout>();

  const calculatePosition = () => {
    if (!triggerRef.current || !tooltipRef.current) return;

    const triggerRect = triggerRef.current.getBoundingClientRect();
    const tooltipRect = tooltipRef.current.getBoundingClientRect();
    const gap = 0; // Figma spec: no spacing between tooltip and anchor
    const padding = 8;

    let top = 0;
    let left = 0;
    let finalLocation = tooltipLocation;

    // Helper to check if tooltip fits in a location
    const fitsAbove = () => triggerRect.top - tooltipRect.height - gap >= padding;
    const fitsBelow = () =>
      triggerRect.bottom + gap + tooltipRect.height <= window.innerHeight - padding;
    const fitsBefore = () => triggerRect.left - tooltipRect.width - gap >= padding;
    const fitsAfter = () =>
      triggerRect.right + gap + tooltipRect.width <= window.innerWidth - padding;

    // Smart positioning: flip if preferred location doesn't fit
    switch (tooltipLocation) {
      case 'above':
        if (!fitsAbove() && fitsBelow()) {
          finalLocation = 'below';
        }
        break;
      case 'below':
        if (!fitsBelow() && fitsAbove()) {
          finalLocation = 'above';
        }
        break;
      case 'before':
        if (!fitsBefore() && fitsAfter()) {
          finalLocation = 'after';
        }
        break;
      case 'after':
        if (!fitsAfter() && fitsBefore()) {
          finalLocation = 'before';
        }
        break;
    }

    // Calculate position based on final location
    switch (finalLocation) {
      case 'above':
        top = triggerRect.top - tooltipRect.height - gap;
        break;
      case 'below':
        top = triggerRect.bottom + gap;
        break;
      case 'before':
        left = triggerRect.left - tooltipRect.width - gap;
        break;
      case 'after':
        left = triggerRect.right + gap;
        break;
    }

    // Calculate alignment
    if (finalLocation === 'above' || finalLocation === 'below') {
      switch (alignment) {
        case 'start':
          left = triggerRect.left;
          break;
        case 'center':
          left = triggerRect.left + (triggerRect.width - tooltipRect.width) / 2;
          break;
        case 'end':
          left = triggerRect.right - tooltipRect.width;
          break;
      }
    } else {
      // before/after locations
      switch (alignment) {
        case 'start':
          top = triggerRect.top;
          break;
        case 'center':
          top = triggerRect.top + (triggerRect.height - tooltipRect.height) / 2;
          break;
        case 'end':
          top = triggerRect.bottom - tooltipRect.height;
          break;
      }
    }

    // Final boundary checks (slide along edge if needed)
    if (left < padding) left = padding;
    if (left + tooltipRect.width > window.innerWidth - padding) {
      left = window.innerWidth - tooltipRect.width - padding;
    }
    if (top < padding) top = padding;
    if (top + tooltipRect.height > window.innerHeight - padding) {
      top = window.innerHeight - tooltipRect.height - padding;
    }

    setActualLocation(finalLocation);
    setCoords({ top, left });
  };

  const handleMouseEnter = () => {
    if (disabled) return;

    timeoutRef.current = setTimeout(() => {
      setVisible(true);
    }, delay);
  };

  const handleMouseLeave = () => {
    if (timeoutRef.current) {
      clearTimeout(timeoutRef.current);
    }
    setVisible(false);
  };

  useEffect(() => {
    if (visible) {
      calculatePosition();
    }
  }, [visible]);

  // Update position on scroll/resize
  useEffect(() => {
    if (!visible) return;

    const handleUpdate = () => calculatePosition();
    window.addEventListener('scroll', handleUpdate, true);
    window.addEventListener('resize', handleUpdate);

    return () => {
      window.removeEventListener('scroll', handleUpdate, true);
      window.removeEventListener('resize', handleUpdate);
    };
  }, [visible]);

  // Cleanup timeout on unmount
  useEffect(() => {
    return () => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
    };
  }, []);

  // Clone the child element and add event handlers
  // Handle case where children might be undefined or invalid
  if (!children || !React.isValidElement(children)) {
    return null;
  }

  const trigger = React.cloneElement(children, {
    ref: triggerRef,
    onMouseEnter: (e: React.MouseEvent) => {
      handleMouseEnter();
      children.props?.onMouseEnter?.(e);
    },
    onMouseLeave: (e: React.MouseEvent) => {
      handleMouseLeave();
      children.props?.onMouseLeave?.(e);
    },
    onFocus: (e: React.FocusEvent) => {
      handleMouseEnter();
      children.props?.onFocus?.(e);
    },
    onBlur: (e: React.FocusEvent) => {
      handleMouseLeave();
      children.props?.onBlur?.(e);
    },
  });

  // Build CSS class based on actual location (after smart positioning) and alignment
  const tooltipClasses = [
    styles.tooltip,
    styles[actualLocation],
    styles[`align-${alignment}`],
  ].join(' ');

  return (
    <React.Fragment>
      {trigger}
      {visible &&
        !disabled &&
        createPortal(
          <div
            ref={tooltipRef}
            className={tooltipClasses}
            style={{
              top: `${coords.top}px`,
              left: `${coords.left}px`,
            }}
            role="tooltip"
            data-qa={dataQa}
          >
            <span className={styles.text}>{tooltipText}</span>
            <div className={styles.caret}>
              <svg className={styles.caretSvg} width="15" height="8" viewBox="0 0 15 8" fill="none">
                <path d="M7.5 8L0.5 0H14.5L7.5 8Z" className={styles.caretFill} />
                <path d="M0.5 0L7.5 8" className={styles.caretBorder} strokeWidth="1" />
                <path d="M14.5 0L7.5 8" className={styles.caretBorder} strokeWidth="1" />
              </svg>
            </div>
          </div>,
          document.body
        )}
    </React.Fragment>
  );
};

Tooltip.displayName = 'Tooltip';

