import React from 'react';
import styles from './Typography.module.css';

export interface HeadingProps extends React.HTMLAttributes<HTMLHeadingElement> {
  children: React.ReactNode;
  level?: 1 | 2 | 3 | 4 | 5 | 6;
  as?: 'h1' | 'h2' | 'h3' | 'h4' | 'h5' | 'h6';
}

export interface TextProps extends React.HTMLAttributes<HTMLElement> {
  children: React.ReactNode;
  as?: 'p' | 'span' | 'div' | 'label';
  size?: 'xs' | 'sm' | 'md' | 'lg' | 'xl';
  weight?: 'light' | 'regular' | 'medium' | 'semibold' | 'bold';
  color?: 'primary' | 'secondary' | 'tertiary' | 'success' | 'error';
}

export const Heading: React.FC<HeadingProps> = ({
  children,
  level = 1,
  as,
  className,
  ...props
}) => {
  const Component = as || (`h${level}` as 'h1');
  const headingClasses = [
    styles.heading,
    styles[`h${level}`],
    className,
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <Component className={headingClasses} {...props}>
      {children}
    </Component>
  );
};

export const Text: React.FC<TextProps> = ({
  children,
  as = 'p',
  size = 'md',
  weight = 'regular',
  color = 'primary',
  className,
  ...props
}) => {
  const Component = as;
  const textClasses = [
    styles.text,
    styles[`size-${size}`],
    styles[`weight-${weight}`],
    styles[`color-${color}`],
    className,
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <Component className={textClasses} {...props}>
      {children}
    </Component>
  );
};

Heading.displayName = 'Heading';
Text.displayName = 'Text';

Container:2.content:
@import '../../1-tokens/tokens.css';

.container {
  margin-left: auto;
  margin-right: auto;
  width: 100%;
}

/* Sizes */
.size-small {
  max-width: 640px;
}

.size-medium {
  max-width: 768px;
}

.size-large {
  max-width: 1024px;
}

.size-xlarge {
  max-width: 1280px;
}

.size-full {
  max-width: 100%;
}

/* Padding */
.padded {
  padding-left: var(--ink-spacing-200);
  padding-right: var(--ink-spacing-200);
}

@media (min-width: 768px) {
  .padded {
    padding-left: var(--ink-spacing-300);
    padding-right: var(--ink-spacing-300);
  }
}

import React from 'react';
import styles from './Container.module.css';

/**
 * Container - Content Width Utility
 *
 * Layer 2: Utilities
 * Dependencies: Only Layer 1 (Tokens)
 *
 * Centers content and applies max-width constraints.
 */

export interface ContainerProps extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
  /** Maximum width of the container */
  size?: 'small' | 'medium' | 'large' | 'xlarge' | 'full';
  /** Add horizontal padding */
  padded?: boolean;
}

export const Container: React.FC<ContainerProps> = ({
  children,
  size = 'large',
  padded = true,
  className,
  style,
  ...props
}) => {
  const containerClasses = [
    styles.container,
    styles[`size-${size}`],
    padded && styles.padded,
    className,
  ]
    .filter(Boolean)
    .join(' ');

  return (
    <div className={containerClasses} style={style} {...props}>
      {children}
    </div>
  );
};

Container.displayName = 'Container';

