import { useState } from 'react';
import {
  DocuSignShell,
  Stack,
  Card,
  Heading,
  Text,
  Input,
  Select,
  Button,
  Switch,
  Divider,
} from '@/design-system';

const globalNavProps = {
  logo: <img src="/logo.svg" alt="Docusign" height={24} />,
  navItems: [
    { id: 'home', label: 'Home', href: '#', active: false },
    { id: 'agreements', label: 'Agreements', href: '#', active: false },
    { id: 'templates', label: 'Templates', href: '#', active: false },
    { id: 'settings', label: 'Settings', href: '#', active: true },
