        <Card>
          <Stack gap="var(--ink-spacing-300)">
            <Heading level={2}>Personal Information</Heading>
            <Input
              label="Full Name"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <Input
              label="Email"
              type="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
            />
            <Select
              label="Timezone"
              value={timezone}
              onChange={(val) => setTimezone(val)}
              options={[
                { value: 'America/Los_Angeles', label: 'Pacific Time (PT)' },
                { value: 'America/Denver', label: 'Mountain Time (MT)' },
                { value: 'America/Chicago', label: 'Central Time (CT)' },
                { value: 'America/New_York', label: 'Eastern Time (ET)' },
              ]}
            />
          </Stack>
        </Card>

        <Card>
          <Stack gap="var(--ink-spacing-300)">
            <Heading level={2}>Notifications</Heading>
            <Stack gap="var(--ink-spacing-200)">
              <Switch
                checked={emailNotifs}
                onChange={setEmailNotifs}
                label="Email notifications"
              />
              <Text variant="secondary" size="sm">
                Receive email updates about agreement activity
              </Text>
            </Stack>
          </Stack>
        </Card>

        <Divider />

        <Stack direction="horizontal" gap="var(--ink-spacing-200)" justify="flex-end">
          <Button kind="secondary">Cancel</Button>
          <Button kind="brand">Save Changes</Button>
        </Stack>
      </Stack>
    </DocuSignShell>
  );
}

import { useState, useMemo } from 'react';
import {
  DocuSignShell,
  AgreementTableView,
  PageHeader,
  FilterBar,
  DataTable,
  Badge,
  Button,
  Avatar,
  Stack,
  Text,
} from '@/design-system';

const globalNavProps = {
  logo: <img src="/logo.svg" alt="Docusign" height={24} />,
  navItems: [
    { id: 'home', label: 'Home', href: '#' },
    { id: 'agreements', label: 'Agreements', href: '#', active: true },
    { id: 'templates', label: 'Templates', href: '#' },
  ],
};

interface Agreement {
  id: string;
  name: string;
  status: 'completed' | 'pending' | 'draft' | 'voided';
  sender: string;
  lastUpdated: string;
}

const AGREEMENTS: Agreement[] = [
  { id: '1', name: 'NDA - Acme Corp', status: 'completed', sender: 'Jane Smith', lastUpdated: '2024-01-15' },
  { id: '2', name: 'Employment Agreement', status: 'pending', sender: 'Bob Jones', lastUpdated: '2024-01-14' },
  { id: '3', name: 'Vendor Contract Q1', status: 'draft', sender: 'Jane Smith', lastUpdated: '2024-01-13' },
  { id: '4', name: 'Partnership Agreement', status: 'pending', sender: 'Alice Wong', lastUpdated: '2024-01-12' },
  { id: '5', name: 'Service Level Agreement', status: 'voided', sender: 'Bob Jones', lastUpdated: '2024-01-11' },
];

const STATUS_MAP = {
  completed: { label: 'Completed', kind: 'success' },
  pending: { label: 'Pending', kind: 'warning' },
  draft: { label: 'Draft', kind: 'neutral' },
  voided: { label: 'Voided', kind: 'error' },
} as const;

export default function AgreementDashboard() {
  const [searchQuery, setSearchQuery] = useState('');
  const [selectedRows, setSelectedRows] = useState<Set<string>>(new Set());

  const filteredData = useMemo(
    () => AGREEMENTS.filter(a => a.name.toLowerCase().includes(searchQuery.toLowerCase())),
    [searchQuery]
  );

  const columns = [
    {
      key: 'name',
      header: 'Agreement Name',
      cell: (row: Agreement) => <Text weight="medium">{row.name}</Text>,
    },
    {
      key: 'status',
      header: 'Status',
      cell: (row: Agreement) => {
        const s = STATUS_MAP[row.status];
        return <Badge kind={s.kind}>{s.label}</Badge>;
      },
    },
    {
      key: 'sender',
      header: 'Sender',
      cell: (row: Agreement) => (
        <Stack direction="horizontal" gap="var(--ink-spacing-100)" align="center">
          <Avatar name={row.sender} size="sm" />
          <Text>{row.sender}</Text>
        </Stack>
      ),
    },
    {
      key: 'lastUpdated',
      header: 'Last Updated',
      cell: (row: Agreement) => <Text variant="secondary">{row.lastUpdated}</Text>,
    },
  ];

  return (
    <DocuSignShell globalNav={globalNavProps}>
      <AgreementTableView
        pageHeader={
          <PageHeader
            title="Agreements"
            actions={<Button kind="brand">Start Agreement</Button>}
          />
        }
        filterBar={
          <FilterBar
            search={{
              value: searchQuery,
              onChange: setSearchQuery,
              placeholder: 'Search agreements...',
            }}
          />
        }
      >
        <DataTable
          columns={columns}
          data={filteredData}
          getRowKey={(row) => row.id}
          selectedRows={selectedRows}
          onSelectedRowsChange={setSelectedRows}
        />
      </AgreementTableView>
    </DocuSignShell>
  );
}

import { useState } from 'react';
import {
  Stack,
  Card,
  Heading,
  Text,
  Input,
  Select,
  Button,
  Banner,
} from '@/design-system';

export default function ContactForm() {
  const [form, setForm] = useState({ name: '', email: '', reason: '' });
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [submitted, setSubmitted] = useState(false);

  const updateField = (field: string, value: string) => {
    setForm(prev => ({ ...prev, [field]: value }));
    setErrors(prev => ({ ...prev, [field]: '' }));
  };

  const handleSubmit = () => {
    const newErrors: Record<string, string> = {};
    if (!form.name.trim()) newErrors.name = 'Name is required';
    if (!form.email.includes('@')) newErrors.email = 'Valid email is required';
    if (!form.reason) newErrors.reason = 'Please select a reason';

    if (Object.keys(newErrors).length > 0) {
      setErrors(newErrors);
      return;
    }
    setSubmitted(true);
  };

  if (submitted) {
    return (
      <Stack gap="var(--ink-spacing-300)" style={{ maxWidth: 480, margin: '0 auto', padding: 'var(--ink-spacing-400)' }}>
        <Banner kind="success">Your message has been sent successfully!</Banner>
        <Button kind="secondary" onClick={() => { setSubmitted(false); setForm({ name: '', email: '', reason: '' }); }}>
          Send Another
        </Button>
      </Stack>
    );
  }

  return (
    <Stack gap="var(--ink-spacing-300)" style={{ maxWidth: 480, margin: '0 auto', padding: 'var(--ink-spacing-400)' }}>
      <Heading level={1}>Contact Us</Heading>
      <Text variant="secondary">We'll get back to you within 24 hours.</Text>

      <Card>
        <Stack gap="var(--ink-spacing-200)">
          <Input
            label="Name"
            value={form.name}
            onChange={(e) => updateField('name', e.target.value)}
            error={errors.name}
          />
          <Input
            label="Email"
            type="email"
            value={form.email}
            onChange={(e) => updateField('email', e.target.value)}
            error={errors.email}
          />
          <Select
            label="Reason for Contact"
            value={form.reason}
            onChange={(val) => updateField('reason', val)}
            error={errors.reason}
            options={[
              { value: '', label: 'Select a reason...' },
              { value: 'support', label: 'Technical Support' },
              { value: 'billing', label: 'Billing Question' },
              { value: 'feature', label: 'Feature Request' },
              { value: 'other', label: 'Other' },
            ]}
          />
          <Button kind="brand" onClick={handleSubmit} style={{ alignSelf: 'flex-end' }}>
            Send Message
          </Button>
        </Stack>
      </Card>
    </Stack>
  );
}

import { useState } from 'react';
import {
  DocuSignShell,
  Stack,
  Card,
  Heading,
  Text,
  Button,
  Drawer,
  AIChat,
} from '@/design-system';
import type { ChatMessage } from '@/design-system';

const globalNavProps = {
  logo: <img src="/logo.svg" alt="Docusign" height={24} />,
  navItems: [
    { id: 'home', label: 'Home', href: '#', active: true },
    { id: 'agreements', label: 'Agreements', href: '#' },
  ],
  extraActions: null as React.ReactNode, // will be set in component
};

export default function AIAssistantPage() {
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [messages, setMessages] = useState<ChatMessage[]>([]);
  const [isLoading, setIsLoading] = useState(false);

  const handleSend = (content: string) => {
    const userMsg: ChatMessage = {
      id: String(Date.now()),
      role: 'user',
      content,
      timestamp: new Date().toISOString(),
    };
    setMessages(prev => [...prev, userMsg]);
    setIsLoading(true);

    // Simulate AI response
    setTimeout(() => {
      const aiMsg: ChatMessage = {
        id: String(Date.now() + 1),
        role: 'assistant',
        content: `I can help you with that! Here's what I found about "${content}"...`,
        timestamp: new Date().toISOString(),
      };
      setMessages(prev => [...prev, aiMsg]);
      setIsLoading(false);
    }, 1000);
  };

  const navProps = {
    ...globalNavProps,
    extraActions: (
      <Button kind="ghost" size="sm" onClick={() => setDrawerOpen(true)}>
        AI Assistant
      </Button>
    ),
  };

  return (
    <DocuSignShell globalNav={navProps}>
      <Stack gap="var(--ink-spacing-300)" style={{ padding: 'var(--ink-spacing-400)' }}>
        <Heading level={1}>Welcome Home</Heading>
        <Card>
          <Stack gap="var(--ink-spacing-200)">
            <Heading level={2}>Quick Actions</Heading>
            <Text variant="secondary">
              Get started by sending an agreement or asking the AI assistant for help.
            </Text>
            <Stack direction="horizontal" gap="var(--ink-spacing-100)">
              <Button kind="brand">New Agreement</Button>
              <Button kind="secondary" onClick={() => setDrawerOpen(true)}>
                Ask AI Assistant
              </Button>
            </Stack>
          </Stack>
        </Card>
      </Stack>

      <Drawer
        open={drawerOpen}
        onClose={() => setDrawerOpen(false)}
        title="AI Assistant"
      >
        <AIChat
          messages={messages}
          onSend={handleSend}
          isLoading={isLoading}
          welcomeMessage="Hi! I can help you find agreements, answer questions, or guide you through tasks."
          suggestedQuestions={[
            'How do I send an envelope?',
            'Show my pending agreements',
            'What templates are available?',
          ]}
        />
      </Drawer>
    </DocuSignShell>
  );
}
